css:
@document url(http://www.twitch.tv/),
               domain(twitch.tv)

{
.ember-application {
  	background: rgba(0,0,0,0) !important;
    opacity : 0.6;
}

.ember-chat-container {
	background: rgba(0,0,0,0) !important;
}

.chat-header {
	display: none !important;
}

span.from {
	display: none;
}

span.badges {
	display: none;
}

span.message {
  	color : #fff;
 	padding-bottom : 3px;
    padding-right : 10px;
  	font-size : 16px;
}

ul.chat-lines {
	text-align : right;
}

.ember-text-area {
	background-color : rgba(255,255,255,0.3);
}

.chat-room {
	top : 0px !important;
}

.ember-text-area:hover {
	background-color : rgba(255,255,255,1);
}

.ember-application:hover{
  opacity : 1;
}
}
