css:
@-moz-document domain("www.xda-developers.com") {
BODY, HTML {
	background:					#111 !important;
	color:						#eee !important;
	text-shadow:				1px 0 1px rgba(0,0,0,0.2) !important;
}

/*
HEADER
*/
	#header {
		background:				#212121;
		border-color:			#313131;
		-moz-box-shadow: 		0 0 15px 2px rgba(0,0,0,0.8);
		-webkit-box-shadow:		0 0 15px 2px rgba(0,0,0,0.8);
		-o-box-shadow:			0 0 15px 2px rgba(0,0,0,0.8);
		box-shadow:				0 0 15px 2px rgba(0,0,0,0.8);
		color:					#eee;
	}
	
	#header .header-input, #header #gsc-input, #header #advancedsearch {
		border-color:			#313131;
		color:					#eee;
	}
	
	#header .new-button, INPUT[type="submit"], INPUT[type="reset"], INPUT.button, BUTTON {
		border-color:			#313131;
		background-color:		#212121;
		color:					#eee !important;
	}
	
	#header .notification-count {
		color:					#212121 !important;
		background:				#ffbd2d !important;
	}




/*
NAVVIGATION
*/
	#navigation {
		color: 					#e0e0e0;
		background-color:		#212121;
		border-color:			#313131;
		border-bottom:			1px solid #313131;
	}
	
	#xda-nav LI A, #himg LI A {
		color: 					#757575;
		border-color:			#313131;
	}

	#navigation a:hover {
		background-color:		#111 !important;
	}

	
		
	div .pagenav-curpage {
  		color: #FFF;
		background-color:		#313131 !important;
	}
	
	div .pagenav {
		color: 					#e0e0e0;
		background-color:		#212121;
		border-color:			#313131;
	}
	
	div .pagenav A, .pagenav-curpage, .pagenav-disabled {
		color: 					#757575;
		border-color:			#313131;

		background-color:		#212121;
		border-right:			1px solid #313131;
		border-top:				1px solid #313131;
		border-bottom:			1px solid #313131;
	}
	
	div .pagenav A:hover {
		background-color:		#111 !important;
	}
	
	.login-input {
		background: none repeat scroll 0% 0% #212121 !important;;
		color: #757575 !important;;
		border-color: #313131 !important;;
	}
	
	#login-submit {
		background-color: #FFBD2D !important;
		color: #212121 !important;
	}
	
	#social-links LI A {
		border-left: 1px solid #2f2f2f !important;
	}
	
	img.ti {
		/* This is one rather ugly method, but it has to be done this way to stay compatible with firefox. */
		background-image: url("http://cdn1.xda-developers.com/images/2015/themer-dark.png") !important;
		padding-left: 81px !important;
		width: 0px !important;
	}
	


/*
CONTENT & SIDEBAR
*/
	div .content h2, div .content h1, * .content .article h3, .content .article h3 a, div .content .article p, div .content .article.single p, div .content .article.single, * .widget-title h3, * .widget-2 p, * .widget-4 h4, .widget-2 p a {
		color:					#fff;
	}

	* .widget-title, * .widget-2 li, * .widget-4 li	{
		border-bottom:			1px solid #313131;
	}
	
	* .content .article, * .sidebar .article {
		background:				#212121;
		border-color:			#313131;
	}
	
	* .widget-4 a {
		border-color:			#313131;
	}
	
	div .content .article .featured-image-wrapper:after {
		border-top:				145px solid #212121;
		z-index:				100;
	}
	
	div .content .article.half .featured-image-wrapper:after {
		border-top:				240px solid #212121;
		z-index:				100;
	}
	
	div .content .article .label {
		color:					#212121;
	}
	
	* .sidebar .article.category-xda-news span {
		color:					#212121;
	}
	
	div .tags {
		border-top: 			1px solid #313131;
	}


	div #author_info_wrapper {
		background:				#212121;
		border-color:			#313131;
		color:					#fff;
	}
	
	div #avatar_wrapper {
		background:				#212121 !important;
	}
	
	* .box {
		background:				#212121;
		border-color:			#313131;
		color:					#fff;
	}


	/* Various image data */
	div .widget-4 h3 {
		background-image: 		url("data:image/png;base64,") !important;
	}

	div .widget-2 h3 {
		background-image: 		url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAYAAAA71pVKAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAfElEQVQoz8WSsQqEQAxEJ2Ll/xfqiQoegv9m6zUWz8IVJCAu64HThEzILPuIgI40VQJWJQggk5SHZnDJnwu/PAIMwMwMwKff+Zke6G/LrZs1oU4X/k7tCe2jGYHfieoQlRDq4q8g+mVJxWu0ezf7Rv85gfaSS+oTAmZJ9Qb3psI6kPysXQAAAABJRU5ErkJggg==") !important;
	}

	div .content .article.extlink .label {
		background-image: 		url("data:image/png;base64,") !important;
	}
	
	div .content .article.android .label, .content .article.category-xda-news .label {
		background-image:		url("data:image/png;base64,") !important;
	}
	
	div .content .article.cloud-download .label {
		background-image:		url("data:image/png;base64,") !important;
	}
	
	div .content .article.discuss .label {
		background-image:		url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAMCAYAAABSgIzaAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsSAAALEgHS3X78AAAAbElEQVQoz62QMQ6AMAwDr4gH8BQkNo883jviG8wsCMoCSwUFKixlSXRJ7CApktdgu02bFYWq04btUAQCSFqBAKQ2YhY8oLMu9YvHBZjfgufFCehsN1/ACehtj19eDTepbrlgeBreLI2lgSIp7kP8GehxA9/VAAAAAElFTkSuQmCC") !important;
	}
	
	div .content .article.icon-fnews .label {
		background-image:		url("data:image/png;base64,") !important;
	}
	
	* .transparent-overlay {
		content: url("data:image/png;base64,") !important;
	}
	


/* 
Other stuff
*/





/* Working around a badly placed ad placeholder that covers the navigation bar, rendering some links unclickable */
	#header { z-index: 101; } #navigation { z-index: 100; }
}
