css:
@-moz-document url-prefix("http://keskustelu.kauppalehti.fi/5/i/keskustelu/thread.jspa") {
div.top_adcontainer, #topbar, #login, #sidebar, #sidebar_ads, #main_navigation, ul.topic_previous_next {
  display: none;
}

div.jive-message-list-footer ul.topic_previous_next,
ul.topic_previous_next.right {
  display: block;
  visibility: hidden;
  margin: 0 -16px 5px 0 !important;
}

div.jive-message-list-footer ul.topic_previous_next a.jive-paginator-current,
ul.topic_previous_next.right a.jive-paginator-current {
  font-weight: normal;
  background-color: #fFF;
}

div.jive-message-list-footer ul.topic_previous_next li,
ul.topic_previous_next li {
  height: auto;
}

div.jive-message-list-footer ul.topic_previous_next a,
ul.topic_previous_next.right a {
  display: inline-block;
  visibility: visible;
  font-size: 14px;
  padding: 5px 10px;
  background-color: #eee;
  text-decoration: none;
  color: #000;
}

.content_wide #content {
  margin: 10px 0 0 13%;
  width: 74%;
}
  
table.messagebox td.messagebox_header {
    line-height: 30px;
    background: #999;
}
  
table.messagebox td.messagebox_header span {
  display: block;
  font-size: 13px !important;
  padding-left: 15px;
}

table.messagebox td.messagebox_header span.time,
table.messagebox td.messagebox_header span.time a {
    float: right;
    color: #ddd;
}

table.messagebox td.messagebox_content {
  width: auto;
  padding: 10px 20px 40px 20px;
  line-height: 1.5;
}
  
table.messagebox td.messagebox_sidebar {
    display: none;
}
  
table.messagebox td.messagebox_footer {
  display: none;
}

#wrapper {
  background: #FFF !important;
}
}
