css:
@-moz-document url-prefix("https://classic.torrentday.com/") {

.fb,
.fbshare,
.love,
body > div:nth-of-type(3),
body > table:last-of-type,
tr + div[align=left],
#ajax-feedback + div[align=center] {
   display: none;
}

body {
 background-color:rgb(16,16,16) !important;
 font-family: 'Helvetica Neue', Arial, sans-serif !important;
}

div.j-title-div {
 background-color:rgb(16,16,16) !important;
 border: 1px solid black !important;  
}

.bottom font {
 font-size: 12px !important;  
}

font {
 font-size: 13px;
 line-height: 19px;
 font-weight: normal;
 text-transform: none;
}

strong {
 font-size: 18px;
 color: #dedede !important;
}

h1, h2, p {
 color: #dedede !important; 
}

hr {
border-top-color: black;
border-right-color: black;
border-bottom-color: black;
border-left-color: black;
background-color: black;
}

.trows {
 background-color: rgb(25,25,25) !important;
}

div.jokes-wrapper {
 background-color: rgb(25,25,25) !important;
 border: none !important;
}

.tcolor {
 background-color: rgb(16,16,16) !important;  
}

td span, table {
 background-color:rgb(25,25,25) !important;
 color: #999 !important;
}

td {
 color: #666 !important;
 font-weight: bold;
 border: none !important;
 letter-spacing: 0.25px;
}

td.navigation {
 text-transform: uppercase !important;  
}

font, a:link, a:visited, span.smallfont, b {
 color: #afafaf !important;
 text-decoration: none !important;
}

a:link{
 font-size: 12px;
 font-weight: bold;
}

#ttable .tags {
 color: #0091EA !important;
 display: none;
}

td span {
 border-radius: 0 !important; 
}

#ttable .thead td, .cat, td.cat, td.catTitle, td.catHead, td.catBottom {
 background:rgb(16,16,16) !important;
 padding: 12px 5px;
 background-color: black !important;
 border: 1px solid black !important;
 color: #dedede !important;
 text-transform: uppercase !important;
 letter-spacing: 0.75px !important;
 text-align: left !important;
}

#movehere {
 padding-bottom: 40px;
}

#movehere input[type="text"] {
 width: 520px;  
}

input {
 padding: 9px 9px 9px 9px;
 font-size: 14px !important;
 border: none;
 font-weight: bold;
 margin-right: 3px;
}

input[type="submit"], button {
 background-color: #0091EA !important;
 border-top: 1px solid #0091EA;
 color: white;
 letter-spacing: 0.25px;
 font-family: 'Helvetica Neue', Arial, sans-serif;
 text-transform: uppercase;
 font-size: 13px !important;
 height: 35px !important;

 padding-left: 20px;
 padding-right: 20px;
 border-bottom: none;
 border-left: none;
 border-right: none;
 margin-top: 8px;
}

input[type="reset"] {
 background-color: #999 !important;
 border-top: 1px solid #999;
 color: rgb(25,25,25);
 letter-spacing: 0.25px;
 font-family: 'Helvetica Neue', Arial, sans-serif;
 text-transform: uppercase;
 font-size: 13px !important;
 height: 35px !important;

 padding-left: 20px;
 padding-right: 20px;
 border-bottom: none;
 border-left: none;
 border-right: none;
 margin-top: 8px;
}

div.shoutbox {
 background-color: rgb(25,25,25) !important;
}

div.messages {
 border-bottom: 1px solid rgb(16,16,16) !important;
 font-size: 14px;
 padding: 10px 5px !important; 
}


.ui-tabs .ui-tabs-nav li.ui-tabs-selected {
background: #0091EA !important;
}
    
.ui-widget-header {
background: rgb(16,16,16) !important;
}
  
.ui-tabs .ui-tabs-nav li.ui-tabs-selected span {
 color: white !important;
 background: #0091EA !important;
 text-transform: uppercase !important;
 letter-spacing: 0.5px !important;
}

a.light-on {
 display: none; 
}

#rooms {
 border: 1px solid black !important; 
}

td.embedded img {
 display: none !important;
}

td.embedded a:link {
 color: #dedede !important;
} 
td.embedded {
 color: #999 !important; 
}

td.colhead {
 background-color: #0091EA !important;
 color: white !important;
}

.blink777, #topers, #hideit, #showit {
 display: none;
}

#T_table h1, #cats, .newtagsbut, .paging, .tagbutton, .main {
 display: none !important; 
}

#blockblockA {
 display: none;
}

hr, br {
 display: none;
}

b[style="color:#F00"] {
  display: none;
}

.TD-Browser {
  display: none;
}

}
