css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("murobbs.muropaketti.com") {
  
 
  .username .banned {text-decoration: line-through !important;}

  .xenPreviewTooltip {display: none !important;}  
 


  .uix_mainSidebar {display:none!important;}
  .mainContent {margin: 10px!important;}
  .footer-om {display:none;}
   
  div[class="section funbox"] {display: none !important;}
  
  a[class="unreadLink"] {display: none !important;}

  a[href]:hover {color: rgb(50, 123, 174); text-decoration: none !important;}
  
  div[class="publicControls"] {
    padding-top: 15px;}
				
	.uix_icon-lukematon:before {content: "\f12a"; color: #2BDB1F;}
       
  .messageUserBlock .extraUserInfo {
    font-size: 10px;
    background-color: #262626;
    padding: 1px 5px;}
  
  .messageUserBlock .extraUserInfo dl {
    margin: 0px 0px 0px;}
  
  .message .messageMeta.otsikko {
    margin: 0px;
    height: 20px;
    border-radius: 3px;}
  
  .PanelScroller .panel, .PanelScrollerOff .panel {
    background-color: #333;}
  
  .discussionList .main {
    width: 60%;}
  .discussionList .posterAvatar {
    width: 46px;}
  

  
  .message .messageContent {
    background-color: #222;
    padding: 2px;
    min-height: 60px;
    overflow: hidden;}
  
  .messageUserInfo {
    background-color: #262626;
    float: left;
    width: 175px;
}
  
  .messageUserBlock h3.userText {
    display:inline-block ; 
    background-color: #262626;
    padding: 0px 0px 0px 5px;}
  
   .message .messageMeta {
    font-size: 10px;
    padding-top: 1px;
    padding-bottom: 0px;
    margin-bottom: 0px;
    overflow: hidden;
    box-sizing: padding-box;}

  .messageList .message {
    background-color: #262626;
    margin-top: 2px;
    margin-bottom: 2px;
    padding: 1px;
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
    border-radius: 4px;
    border-left: 1px solid #000;
    border-right: 1px solid #000;}
    
  .message .messageInfo {
    margin-left: 200px;
    margin-bottom: 0px;}

  .messageUserBlock a.username {
    font-size: 13px;}
    
  .OverlayTrigger.item.control.report {
    padding-left: 18px;
    padding-top: 15px;} 
  
  .messageMeta.ToggleTriggerAnchor {min-height: 35px !important;}
   
  
  .bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    font-size: 9pt;
    color: #C7C7C7;
    background-color: #292929;
    background-repeat: repeat-x;
    background-position: center top;
    padding: 2px 5px;
    border-right: 1px solid grey;
    border-bottom: 1px solid grey;
    border-left: 1px solid #000;}
  
  .uix_icon-Attr_link:before {
    content: "\f112";
    color: #327BAE;
    font-size: 9pt;}

 
  
  .discussionList .lastPost {
    width: 200px;
    text-align: left;
    overflow: hidden;}
  
  .linkGroup a {
    padding: 3px 5px;}
  
  .pairsJustified dd {
    float: left;
    text-align: left;
    max-width: 100%;}
  
  .discussionList .sectionHeaders .stats .minor {
    text-align: left;}
  
  #QuickReply .submitUnit {
    margin-right: 200px;}
    
  
  .button {
    font-style: normal;
    font-weight: bold;
    font-size: 10px;
    color: #FFF;
    background-color: #394859;
    padding: 1px 1px;
    border-width: 1px;
    border-color: #000;
    border-radius: 2px;
    text-align: center;
    outline: aliceblue;
    line-height: 15px;
    display: inline-block;
    cursor: pointer;
    box-sizing: border-box;
    box-shadow: 1px 1px 0px #000;
    height: 18px;}

  .hasJs .bbCodeSpoilerText {
    padding: 1px 5px 1px 4px;
    background-color: inherit;
    border-color: darkgray;
    border-radius: 2px;}
  
  .breadBoxTop a.callToAction {
    height: 39px;
    line-height: 39px;}
  
  .thread_view .threadAlerts dt {
    color: #B4B4B4;}
 
}
