@namespace url(http://www.w3.org/1999/xhtml);

/*-----| UP CRS Light Blue |-------------------------------------------------------------------*/
/*---------------| by jemhuntr |---------------------------------------------------------------*/

/*-----| Color Palettes |---
	#44749D	rgba(68, 116, 157, 1)
	#C6D4E1	rgba(198, 212, 225, 1)
	#FFFFFF
	#EBE7E0
	#BDB8AD
-------| Color Palettes |---*/

@-moz-document domain("crs.upd.edu.ph") {

/*-----| GLOBAL OVERRIDES |--------------------------------------------------------------------*/
	* {
		font-family: "Roboto", "Open Sans", "Calibri", sans-serif;
		padding: 0px;
		margin: 0px;
		box-sizing: border-box;
		border: none;
		font-style: normal;
		font-weight: 300 !important;
	}

	a {
		text-decoration: none !important;
	}

/*-----| IMAGES |------------------------------------------------------------------------------*/
	#leftcolumn div.module-search {
		background: url(data:image/png;base64,) no-repeat right center transparent;
	}

/*-----| TEXTS AND LINKS |---------------------------------------------------------------------*/
	a, a:hover, a:visited, #leftcolumn div.module-div h1 {
		position: relative;
		color: #44749D;
	}

	div#subheader span, .clock a, #leftcolumn div.module-div a,
	#leftcolumn div.module-div a.current, #rightcolumn h2, .login-page #rightcolumn form,
	.login-page #leftcolumn h1, .login-page #rightcolumn h1 {
		color: rgba(0,0,0,0.54);
	}

	#leftcolumn div.module-div a:hover, #leftcolumn div.module-div a:focus,
	#leftcolumn div.module-div a.focused, #leftcolumn div.module-div a.current:hover,
	table, .tables, a.tab, a.tab:hover {
		color: rgba(0,0,0,0.87);
	}

	div#header p em, div#header a.logout {
		color: rgba(255,255,255,0.54);
	}

	div#header a.logout:hover, th, input[type="submit"], input[type="button"], button,
	.preen_download_link, .preen_download_link:hover, a.tab.current-tab, a.tab.current-tab:hover {
		color: rgba(255,255,255,0.87);
	}

/*-----| FONT ATTRIBUTES |---------------------------------------------------------------------*/
	/* font sizes */
	div#header p {
		font-size: 0px;
	}

	#leftcolumn div.module-div h1 {
		font-size: 13px;
	}

	div#subheader span, #leftcolumn div.module-search input#module-search-input,
	#leftcolumn div.module-div a, #leftcolumn div.module-div a strong em {
		font-size: 16px;
	}
	
	.login-page #leftcolumn h1, .login-page #rightcolumn h1 {
		font-size: 18px;
	}

	.clock a {
		font-size: 20px;
	}

	div#header p em, div#header p a {
		font-size: 46px;
	}

	/* font weights */
	div#subheader span, .clock a, #leftcolumn div.module-search input#module-search-input,
	#leftcolumn div.module-div a, #leftcolumn div.module-div a strong em, div#header p em,
	.login-page #leftcolumn h1, .login-page #rightcolumn h1 {
		font-weight: 200;
	}

	#leftcolumn div.module-div h1 {
		font-weight: 300;
	}

	/* letter spacing */
	div#subheader span, .clock a, #leftcolumn div.module-search input#module-search-input,
	#leftcolumn div.module-div a {
		letter-spacing: 1px;
	}

	/* etc */
	div#header p {
		text-transform: uppercase;
	}

	.login-page #rightcolumn form,
	.login-page #rightcolumn form input,
	.login-page #leftcolumn h1, .login-page #rightcolumn h1 {
		text-align: center;
	}

	div#header p {
		text-align: right;
	}

/*-----| BACKGROUNDS |-------------------------------------------------------------------------*/
	input[type="submit"], input[type="button"], button, .preen_download_link {
		background: rgba(68, 116, 157, 0.85);
	}

	div#header, th, input[type="submit"]:hover, input[type="button"]:hover, button:hover,
	.preen_download_link:hover, a.tab.current-tab, a.tab.current-tab:hover {
		background: #44749D !important;
	}

	#leftcolumn div.module-div a.current, #leftcolumn div.module-div a.current:hover {
		background: #C6D4E1 !important;
	}

	div#subheader {
		background: rgba(198,212,225,0.85);
	}

	body {
		background: #FFFFFF;
	}

	div#leftcolumn:before, .tr_even.tr_highlight td, .tr_odd.tr_highlight td, .highlight td,
	a.tab:hover {
		background: #EBE7E0 !important;
	}

	table, .tables, table.form, td, .tr_even, .tr_odd, .login-page #rightcolumn form,
	.login-page #leftcolumn h1, .login-page #rightcolumn h1, a.tab {
		background: #FBF7F0 !important;
	}

	#leftcolumn div.module-search input#module-search-input, #leftcolumn div.module-div a,
	div#header a.logout, input[type="text"], input[type="password"], textarea {
		background: transparent;
	}

	#leftcolumn div.module-search:hover:after, #leftcolumn div.module-div a:hover,
	#leftcolumn div.module-div a:focus, #leftcolumn div.module-div a.focused {
		background: rgba(0,0,0,0.1);
	}

/*-----| OPACITY |-----------------------------------------------------------------------------*/
	#leftcolumn div.module-search {
		opacity: 0;
	}

	#leftcolumn:hover div.module-search {
		opacity: 1;
	}

/*-----| BORDERS AND SHADOWS |-----------------------------------------------------------------*/
	/* borders */
	#leftcolumn div.module-search, #leftcolumn div.module-div a, #leftcolumn div.module-div h1,
	div#header a.logout {
		border: none;
	}

	table, .tables, table.form, .login-page #rightcolumn form,
	.login-page #leftcolumn h1, .login-page #rightcolumn h1 {
		border: 1px solid rgba(0,0,0,0.25) !important;
	}

	table tr td, table tr th, table.form tr td, .preen_download_link, a.tab,
	input[type="submit"], input[type="button"], button {
		border: 1px solid rgba(0,0,0,0.05) !important;
	}

	#leftcolumn {
		border-top: 1px solid rgba(0,0,0,0.25);
	}

	#leftcolumn div.module-search input#module-search-input,
	input[type="text"]:hover, input[type="password"]:hover, textarea:hover,
	input[type="text"], input[type="password"], textarea {
		border-bottom: 1px solid transparent;
	}

	#leftcolumn div.module-search input#module-search-input:hover,
	input[type="text"]:hover, input[type="password"]:hover, textarea:hover {
		border-bottom: 1px dotted rgba(0,0,0,0.25);
	}

	div#header, div#subheader, #leftcolumn div.module-search input#module-search-input:focus,
	#rightcolumn h2, input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
		border-bottom: 1px solid rgba(0,0,0,0.25);
	}

	div#leftcolumn:before {
		border-right: 1px solid rgba(0,0,0,0.25);
	}

	/* border radii */
	#leftcolumn div.module-search, .preen_download_link {
		border-radius: 0px;
	}

	div#subheader span {
		border-radius: 5px;
	}

	/* box shadows */
	div#subheader {
		box-shadow: 0px 3px 5px rgba(0,0,0,0.25), 0px 3px 5px -3px rgba(0,0,0,0.5) inset;
	}

	div#leftcolumn:before {
		box-shadow: 0px 0px 5px rgba(0,0,0,0.25);
	}

	/* text shadows */

/*-----| MARGIN AND PADDING |------------------------------------------------------------------*/
	/* margins */
	#leftcolumn div.module-div, div#header a.logout {
		margin: 0px;
	}

	/* paddings */
	div#header a.logout {
		padding: 0px;
	}

	div#subheader span, a.tab {
		padding: 3px 15px;
	}
	
	input[type="submit"], input[type="button"], button, .preen_download_link {
		padding: 3px 5px;
	}

	#leftcolumn div.module-div h1 {
		padding: 20px 20px 5px;
	}
	
	.login-page #rightcolumn form {
		padding: 5px 10px 10px;
	}

	#leftcolumn div.module-div a, #leftcolumn div.module-div a.current {
		padding: 10px 40px;
	}

	div#header a.logout {
		padding-left: 10px;
	}

	#leftcolumn div.module-div a:hover, #leftcolumn div.module-div a.current:hover {
		padding-left: 50px;
	}

/*-----| SIZE AND POSITION |-------------------------------------------------------------------*/
	/* height and width */
	div#header {
		height: 70px;
		width: 100%;
	}

	div#subheader {
		height: 30px;
		width: 100%;
	}

	div#leftcolumn {
		height: calc(100% - 190px);
		width: 290px;
	}

	div#leftcolumn:hover {
		height: calc(100% - 220px);
		width: 320px;
	}

	/* height */
	div#leftcolumn:before {
		height: 100%;
	}

	div#content, #leftcolumn div.module-search input#module-search-input {
		height: 60%;
	}

	#leftcolumn div.module-search {
		height: 46px;
	}

	div#header p, div#header a.logout {
		height: 40px;
	}
	
	#login-form input {
		height: 26px;
	}
	
	.preen_download_link {
		height: auto;
	}

	/* line height */
	div#subheader span {
		line-height: 30px;
	}

	div#header p {
		line-height: 40px;
	}

	/* width */
	div#content, #leftcolumn div.module-div a, #login-form input,
	input[type="text"], input[type="password"], textarea {
		width: 100%;
	}

	.clock a, #leftcolumn div.module-search, div#leftcolumn:before {
		width: 290px;
	}

	div#header a.logout {
		width: 40px;
	}

	div#leftcolumn:hover:before, #leftcolumn:hover .clock a,
	#leftcolumn:hover div.module-search {
		width: 320px;
	}

	div#rightcolumn {
		width: calc(100% - 360px);
	}

	#leftcolumn div.module-search input#module-search-input {
		width: calc(100% - 72px);
	}
	
	.preen_download_link {
		width: auto;
	}

	/* positions */
	div#header {
		position: fixed;
		top: 0px;
		left: 0px;
	}

	div#subheader {
		position: fixed;
		top: 70px;
		left: 0px;
	}

	div#content {
		position: absolute;
		top: 100px;
		left: 0px;
	}

	div#leftcolumn {
		position: fixed;
		bottom: 20px;
		left: 0px;
	}

	div#rightcolumn {
		position: absolute;
		top: 0px;
		right: 30px;
	}

	.clock a {
		position: fixed;
		top: 110px;
		left: 0px;
	}

	div.module-search {
		position: fixed;
		top: 155px;
		left: 0px;
	}

	#leftcolumn div.module-search input#module-search-input {
		position: absolute;
		top: 20%;
		left: 36px;
	}

	div#header p {
		position: absolute;
		bottom: 0px;
		right: 50px;
	}

	#leftcolumn div.module-div a {
		position: relative;
	}

	/* z-index */
	div#header {
		z-index: 500;
	}
	
	div#subheader {
		z-index: 250;
	}
	
	/* display */
	
	/* overflow */
	div#leftcolumn {
		overflow: scroll;
	}

/*-----| TRANSITIONS |-------------------------------------------------------------------------*/
	div#leftcolumn, div#leftcolumn:before, .clock a, #leftcolumn div.module-div a,
	div.module-div a.current:before, div.module-div a.current:after,
	div#header a.logout:before, div#header a.logout:after {
		transition: all 0.5s cubic-bezier(.24,.9,.24,.99);
		-webkit-transition: all 0.5s cubic-bezier(.24,.9,.24,.99);
	}

	#leftcolumn:hover div.module-search, #leftcolumn div.module-search:after {
		transition: all 0.5s cubic-bezier(.24,.9,.24,.99), opacity 0.5s ease;
		-webkit-transition: all 0.5s cubic-bezier(.24,.9,.24,.99), opacity 0.5s ease;
	}

/*-----| PSEUDO ELEMENTS |---------------------------------------------------------------------*/
	div#leftcolumn:before {
		content: " ";
		position: fixed;
		top: 0;
		left: 0;
		z-index: -1;
	}

	#leftcolumn div.module-search:after {
		content: " ";
		position: absolute;
		top: 10%;
		left: 0%;
		height: 80%;
		width: 100%;
		z-index: -1;
	}

	#leftcolumn div.module-div a.current:before {
		content: " ";
		position: absolute;
		bottom: 50%;
		left: 0%;
		border-bottom: 8px solid #44749D;
		border-right: 8px solid transparent;
	}

	#leftcolumn div.module-div a.current:after {
		content: " ";
		position: absolute;
		top: 50%;
		left: 0%;
		border-top: 8px solid #44749D;
		border-right: 8px solid transparent;
	}

	#leftcolumn div.module-div a.current:hover:before {
		border-bottom: 16px solid #44749D;
		border-right: 16px solid transparent;
	}

	#leftcolumn div.module-div a.current:hover:after {
		border-top: 16px solid #44749D;
		border-right: 16px solid transparent;
	}

	div#header a.logout:before {
		content: " ";
		position: fixed;
		top: 0px;
		right: 0px;
		height: 70px;
		width: 50px;
		background: rgba(255,255,255,0.1);
	}

	div#header a.logout:after {
		content: " ";
		position: fixed;
		top: 0px;
		right: 50px;
		border-top: 70px solid transparent;
		border-right: 35px solid rgba(255,255,255,0.1);
	}

	div#header a.logout:hover:before {
		width: 225px;
		background: rgba(255,255,255,0.25);
	}

	div#header a.logout:hover:after {
		right: 225px;
		border-right: 35px solid rgba(255,255,255,0.25);
	}

/*-----| SPECIFIC OVERRIDES |------------------------------------------------------------------*/
}
