@-moz-document url("http://play.pokemonshowdown.com/") {
/* this style is for play.pokemonshowdown.com */

.userbar:before{
        background: url(http://imgur.com/wl0rOlm.png); /*this is the logo next to the neame is the top right*/ 
 }



a{
 color: #0000FF; /* this is the color of all the links */
}

body{
	background: url(http://imgur.com/2vXZfGS.png)!important; /* This is the main background of the page*/
  	background-size: cover;
}

.highlighted, .message-announce {
background: #000000; /*This is the color of the highlight when someone says your name*/
}

.chat.timer {
color: #00BFFF; /*color of the text when the timer is on*/
}

#lobby-userlist-users small{
 color: #FFFFFF; /*color of the "___ users" text at the top of the users list in lobby*/
}

.ladder strong, .ladder em{
  color: #00FF00 !important; /*color of the text of the ladder ranking numbers and other things in the /ladder*/
}

.mainmenufooter small:after {  /*this whole thing is just text under "watch a battle" on the main page.*/
	  content: ""
      text-shadow 0 1px black;
      position: absolute;
      left: 0px;
      top: -40px;
      width: 200px;
}




.tabbar{
  bottom: 0;
  }
.userbar{
  top: 18px;
  }

  .userbar .username {
        text-shadow: 1px 1px black; /*color of the shadow behind your username in the top right*/
}
  
.userbar:before{
  display: block;
  content:"";
        width:32px;
        height: 32px;
        float: left;
        position: relative;
        top: -9px;
      }
  
.icon-user{
     display:none; 
    }

.siderooms{
background: #FFFFFF; /*color of the right half of the userbar*/
