.statbar /*this is the hp bar in the battle screen*/
{
	position: absolute;
	display: none;
	width: 186px;
	background: url(http://i.imgur.com/IKIMzZJ.png);
	background-repeat: no-repeat;
	background-position: 0px 1px;
	min-height: 40px;
	padding: 2px 4px 2px 26px;
}
.statbar strong
{
	position: absolute;
	left: 30px;
	top: 6px;
	font-size: 9px;
	font-weight: normal;
	display: block;
	text-align: center;
	color: #FFF; /*changes the color of the text above the active pokemon in the battle screen*/
	text-shadow: #000 1px 1px 0, #000 1px -1px 0, #000 -1px 1px 0, #000 -1px -1px 0;
}
.statbar strong small
{
  	font-size: 9px;
	font-weight: normal;
}
.statbar .hpbar
{
	position: relative;
	border: 1px solid #131313; 
	background: #FCFEFF;/*changes the color of the background of the hp bar (what is left when hp is lost)*/
	height: 6px;
	margin: 0 0 0 0;
	width: 151px;
	border-radius: 0px;
  	padding: 0;
  	top: -2px;
}
.hptextborder{
	display:none !important;
}



.statbar .hpbar .hp
{
	height: 3px;
	border-top: 1px solid #007734; /* changes the color of the top of the hp in the hp bar*/
