css:
@-moz-document url-prefix('https://www.sylestia.com/') {
body{
\\background-image:none !important;
\\background-color:#000000 !important;
color: white !important;
}

div[id^=div_stable] {
}


.stable_tab2, .stable_table, .main_bg3 {
 background-color: #000000 !important;
}

div[title='Health Bar'] {
 display:none;
}

.high_bg2 {
background-color: #1F1F1F;
}

.menu_circle_hover {
 background:-moz-linear-gradient(center top , rgb(70, 100, 160), rgb(50, 80, 140)) repeat scroll 0% 0% transparent !important;
 box-shadow: inset 2px 2px 40px #111111 !important;
 border:1px solid #000000 !important;
}

.sidepanelbox {
}

.textlink {
 color:#FFFFFF !important;
}

a:hover {
 text-decoration:underline !important;
}

.noselect, .main_bg2, .main_table2, .forum_section, .forum_header, .forum_navbar, .forum_key{
 background-image:none !important;
 background-color:#1F1F1F !important;
 \\border:1px #1F1F1F solid !important;
}

.side_border, .forum_row, .main_table3, .gray_bg1, .forum_text, .forum_textarea, .gray_bg1.list_border_bottom1 {
 background-color:#1F1F1F !important;
}

.content_border {
 background-color:black !important;
 border:none !important;
}
.navbutton_high, .footerbox{
 color:#F5FAFF !important;
}

table.forum_quote{
 background-color:#E0E5EA !important;
 max-height:600px !important;
 overflow-y:scroll !important;
}

#pageheaderdiv{
 background-image:none !important;
 background:url(https://sylestia.com/images/website/backgrounds/sylestiabanner.jpg) !important;
 background-color:#000000 !important;
}

.main_table3_trans2{
 display:none !important;
}

.main_bg2[background="/images/website/backgrounds/welcome_bg.jpg"]{
 background:url(https://sylestia.com/images/website/backgrounds/welcome_bg.jpg) !important;
 display:none !important;
}
}

@-moz-document url-prefix("https://www.sylestia.com/account/wardrobe/"), url-prefix("https://www.sylestia.com/explore/inventory/") {
.noselect {
 background-color:#FFFFFF !important;
}
}
