css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.na.square-enix.com"), domain("forums.eu.square-enix.com") {

  body{ background: url(/*[[wallpaper]]*/) fixed top center !important;
    background-size: cover !important; color: #aaa !important;}

  .splash {/*[[splash]]*/}

  .searchbutton, #vbtab_forum .popupmenu:hover .popupctrl, .main-menu a:hover, .announceicon, .threadstatus, .posthead, .bbcode_postedby, #quickreply_title, .forumoptiontitle, .forumhead, .searchlisthead {background: rgba(100,100,100,.5) !important;}  
  
    
  .threadlist, .forumbit_nopost ol, .forumbits > ol {background: rgba(20,20,20,.5) !important;}
  .body_wrapper, .above_body, #membership-bar-header-bg, .navpopupbody, .popupbody li a {background: rgba(20,20,20,.85) !important;}
  
  .postbody, .message, .searchresults .blockbody {background: rgba(10,10,10,.5)!important;}
  
  .sticky {background: rgba(60,60,40,.5) !important;}
  
  .buttoncontainer, #navbar, .breadcrumb, .above_threadlist_controls, .threadlisthead, dl.threadlastpost, .threadbit .threadviews, .announceinfo, .nonsticky, .thread_controls, .postbit, .footer-links, .footer-text, .blockbody, .optionlabel, .forumfoot,  .forumrow, .forumlastpost, .forumpostcount, .forumbit_nopost, .wgo_subblock div, .threadstats, .threadpostedin {background: rgba(50,50,50,.5) !important; }
  .footer-links, .footer-text{background: rgba(50,50,50,.85) !important;}
  .floatcontainer, #thread_inlinemod_form, .postuser, .postuser > div, .blockfoot, .forumbits, .searchresults .blockbody .threadbit, .pagination span a {background: transparent !important;}
  
  #se-logo {background: url("https://membership.square-enix.com/images/membership_bar/site_logo.png") no-repeat !important}
    
  
  a {color: #EED391 !important;} 
  a:hover {text-shadow: 0px 0px .5em #EED391 !important; text-decoration: none !important;}
  
  .pagination a {text-decoration: none !important;}
  .pagination .selected a {font-size: 18px !important;}
  
  
  .threadinfo, .threadviews, h2, .postuser, .userstats, .postcontent, .forumrow, .wgo_subblock div {color: #999 !important;}
  .time, .date, .bbcode_postedby div, .optionlabel, .forum_info_subblock, .threadbit {color: #999 !important;}
  .signaturecontainer, label, .label {color: #666 !important;}
  
  
  .above_body, .body_wrapper, #vbtab_forum .popupmenu:hover .popupctrl, .main-menu a:hover, #membership-bar-header-bg, .footer-links, .footer-text {border: none !important;}  
  .postbit {border: 1px #444 solid !important;}
  .bbcode_postedby, .message, .navpopupbody, .popupbody li, #forum_display_options {border-color: #444 !important;}
  .searchbutton, .textboxcontainer, .buttoncontainer, .pagination, .postuser, #cke_vB_Editor_QR_editor, #qr_defaultcontainer .blockbody, #forum_info, .forumhead, .pagination span a  {border: none !important;}

  #vbtab_forum .popupmenu:hover .popupctrl {box-shadow: 0px 0px 5px #9E9361 !important;}
   
  .postbody, .postbit, .forumbits, .forumbit_nopost {border-radius: 12px !important;}
  .posthead, .forumhead {border-radius: 12px 12px 0 0 !important;}
  
  .postbody {margin: 0 10px !important;}
  .pagination .selected a {margin-top: -4px !important; }
  .postfoot {width: 975px !important; margin: 0px 10px !important;}
  .signature {margin: 20px 0 10px 0 !important;}
  .forumrow, .searchresults .blockbody .threadbit {margin: 5px 0 !important; }
  
  
  
  .icon-bell-alt, .icon-group, .icon-bar-chart, .icon-user {filter: brightness(150%) !important;}
  
  .postfoot {filter: invert(100%) !important; }
  .postfoot .textcontrols {filter: hue-rotate(230deg) !important; opacity: .5 !important;}
  .newcontent_textcontrol, #showthread_navpopup > a, #forumdisplay_navpopup > a, input.button, .navlinks a {filter: invert(100%) hue-rotate(230deg) !important; opacity: .5 !important; text-shadow: 0px 0px 1em #f00 !important; }
  .multiquote:after{content: "Multi-Quote" !important;}
  
    
  .navpopupbody {width: 220px !important;}
  
  img {max-width: 940px !important; }
}
