css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("szotar.sztaki.hu"),
domain("dict.sztaki.hu") {

  div#search-box{
    width: auto !important;
  }
  #main_center_holy{
    position: absolute !important;
    width: 100% !important;
    top: 1.45em;
  }
  div#content {
    width: auto !important;
    left: 0em !important;
    margin-right: 10.5em !important;
    position: absolute;
    z-index: 5;
  }
  div#menu{
    top: 0em;
    right: 0em;
    width: 100%;
    overflow: hidden;
  }
  div#search-box-menu.search-box-menu-with-form, div#search-box {
    height: 13em !important;
    background-color: #fff9e1 !important;
    margin: 0em !important;
    padding: 0em !important;
    width: 100% !important;
  }
  div#main_left_holy.column{
    width: 10.5em !important;
    border-left: 1px solid gray !important;
    position: absolute !important;
    right: 0px !important;
    top: 1.45em;
    overflow: auto;
    z-index: 15;
  }
  #search-box-form td label{
    color: #000000 !important;
  }
  a#dir_link{
    color: rgb(21, 81, 96) !important;
  }
  a#dir_link:hover{
    color: rgb(255, 102, 0) !important;
  }
  div#search-box-menu{
    padding-top: 0em !important;
    background: #fff9e1 !important;
    width: 97% !important;
    height: 12em !important;
  }
  table.results{
    width: auto !important;
  }
  div#result{
    position: relative;
    width: 97% !important;
    margin-right: 10em !important;
  }

  div#main_container_holy{
    position: absolute !important;
    padding: 0em !important;
    margin: 0px !important;
    width: 100% !important;
  }
  html, body, #dict-header, #magnum, #footer, #content-left, #docs-viewer, #result, #marketing{
    width: 100% !important;
    background: none !important;
  }
  div#footer {
    display: none !important;
    position: relative !important;
  }

  #kozos-fej, div#magnum, div#dict-header a img, div#content-right, div#hp_logo_div,  div#pwrdby, div#main_right_holy,
  div#footer p.first-line img, p.copyright{
    display: none !important;
    height: 0px !important;
  }

/**********************
 * HEADER             *
 **********************/

  div#dict-header{
    position: absolute;
    font-size: 10% !important;
    z-index: 20 !important;;
 }
  div#dict-header a{
    padding-top: 0em !important;
    font-size: 12px !important;
  }
  div#services ul li a{
    background-image: none;
  }
  div#services ul li.sel a em span{
    background-color: #fff9e1 !important;
    color: rgb(21, 81, 96) !important;
  }
  div#services ul li a span{
    padding-left: 5px !important;
    display: block !important;
    background-color: rgb(21, 81, 96) !important;
    color: white !important;
    border-left: 2px solid white !important;
    font-weight: bold !important;
    height: 1.4em;
  }
  div#services ul li a{
    width: 5em !important;
    background-image: none !important;
    background-color: #fff9e1 !important;
  }

  div#services ul li{
    margin-left: 4px;
    border-bottom: none !important;
  }
  div#services ul {
    width: 100% !important;
    background-image: none !important;
  }
  div#services{
    position: absolute;
    height: 1.4em !important;
    width: 100% !important;
    border-bottom: 1px solid gray !important;
    z-index: 10;
    background-color: #ffffff !important;;
  }

/* UNUSED CODE */
/*  #result table.results td.even{
     background-color: #eee !important;
  }
  #result table.results td.odd{
     background-color: #d5d5d5 !important;
  }
  #result table.results td:hover, #result table.results tr:hover{
     background-color: #dfd9c0;
  }*/

}
