@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* ==UserStyle==
// @name         Tab: Icons to close tab on the contextual menu.
// @author       Roblesolido
// @description	 This adds icons in the context menu of each tab Tabmix Plus.
// ==/UserStyle==*/

#tm-closeLeftTabs,
#tm-closeRightTabs,
#tm-closeAllTabs,
#tm-closeSimilar,
#context_closeOtherTabs
{
-moz-binding:url(chrome://global/content/bindings/menu.xml#menuitem-iconic) !important;
}

#tm-closeRightTabs,
#tm-closeLeftTabs,
#context_closeOtherTabs
{
list-style-image: url(data:image/png;base64,) !important;
}

#tm-closeAllTabs
{
list-style-image: url(data:image/png;base64,) !important;
}
#tm-closeAllTabs
{
list-style-image: url(data:image/png;base64,) !important;
}
#tm-closeSimilar
{
list-style-image: url(data:image/png;base64,) !important;
}

#tm-closeLeftTabs[disabled="true"],
#tm-closeRightTabs[disabled="true"],
#context_closeOtherTabs[disabled="true"]
{
display:none !important;
}

.tabbrowser-tab[protected="true"],
.tabbrowser-tab[locked="true"]
{
color:rgba(255,255,204,1) !important;
background-color:rgba(153,153,0,0.9) !important;
}

/*end*/
