css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codecanyon.net") {
    .content-main {
       background-color: #D0D0D0;
}
  
  .box {
    background-color: #FBFBFB;
    color: #666;
    margin-bottom: 20px;
    padding: 15px;
    border-radius: 4px;
    border: 1px solid #FFF;
}
  
  .search-facet-actives {
    display: table;
    width: 100%;
    box-sizing: border-box;
    padding: 15px;
    margin-bottom: 0px;
    background: none repeat scroll 0% 0% #424242;
    border-bottom: 1px solid #E0E0E0;
    border-radius: 4px 4px 0px 0px;
    font-size: 14px;
}

.search-facet-actives__facet-category-value a:hover {
    background: none repeat scroll 0% 0% rgba(26, 26, 26, 0.22);
}
  
  .search-facet-actives__facet-category-value a {
    display: table-cell;
    padding: 0px 12px 0px 8px;
    border-style: solid;
    border-color: rgba(255, 255, 255, 0.4);
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-width: 1px 0px;
    border-radius: 0px;
    color: rgba(255, 255, 255, 0.85);
    text-decoration: none;
}
  
  
  .search-facet-actives__remove-all > a {
    color: rgba(255, 255, 255, 0.69);
    font-size: 12px;
}
  
  
  .photo-grid, .item-grid, .item-list, .user-list {
    background: none repeat scroll 0% 0% #F5F5F5;
    border: 1px solid #EEE;
    border-radius: 4px;
        border-top-left-radius: 0px;
        border-top-right-radius: 0px;
        border-bottom-right-radius: 4px;
        border-bottom-left-radius: 4px;
    display: block;
    list-style: outside none none;
    margin-bottom: 20px;
}
  
  .search-facet-multi-select {
    list-style-type: none;
    overflow: hidden;
    background: none repeat scroll 0% 0% #F4F4F4;
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    font-size: 13px;
    line-height: 1.9;
}
  
  .search-facet-categories {
    overflow: hidden;
    background: none repeat scroll 0% 0% #F4F4F4;
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    font-size: 13px;
    line-height: 1.2;
}
  
  
}
