css:
@-moz-document domain('zhihu.com'){

/*限制答案中图片高度*/
.zh-lightbox-thumb{
  max-height:200px;width:auto!important
}

/*页面加宽*/
.zu-main{
  width:1024px!important
}

/*页面字体加大*/
body,.zm-item-answer-author-wrap{
  font-size:14px
}

/*正文字体*/
.zm-item-rich-text,.post-content,.zm-item-rich-text block quote,.entry-content{
  font-size:16px!important;
  line-height:26px!important;
}

/*问题标题*/
.zm-profile-activity-page-item-main>.question_link,h2{
  font-size:18px!important
}
}
