css:
@-moz-document url-prefix("http://www.reddit.com/r/CyanideandHappiness/"), url-prefix("http://www.reddit.com/r/cyanideandhappiness") {
#header-bottom-left, #header {
    background: #2f264d !important;
}

#header-img {
    display: none !important;
}

#header-img-a:after {
    content: url('http://i.imgur.com/ufpMbSr.png') !important;
}

.pagename a {
    color: white !important;
    transition: color 0.2s;
    font-weight: 100 !important;
    vertical-align: initial !important;
    font-size: 18px !important;
}

.pagename a:hover {
    color: #ffa70d !important;
    transition: color 0s;
    text-decoration: none !important;
}

#sr-header-area, #srLeftContainer a {
    opacity: 0.6 !important;
    transition: opacity 1s;
    color: white !important;
    background: transparent !important;
    border-bottom: none !important;
}

#RESShortcuts {
    display: none !important;
}

#sr-header-area:hover {
    opacity: 1 !important;
    transition: opacity 0.4s;
}

.tabmenu li a {
    background: transparent !important;
    font-size: 18px !important;
    font-variant: small-caps !important;
    color: white !important;
    font-family: calibri !important;
    transition: color 0.2s;
    font-weight: 200 !important;
}

.tabmenu li.selected a {
    border: none !important;
    border-bottom: none !important;
}

.tabmenu li a:hover {
    color: #ffa70d !important;
    transition: color 0s;
}

#header-bottom-right {
    background: transparent !important;
    opacity: 0.3 !important;
    transition: opacity 1s, color 1s, text-shadow 1s;
    color: white !important;
    border-radius: 0px !important;
    float: bottom !important;
    margin-top: 55px !important;
    margin-bottom: 0px !important;
}

#header-bottom-right a {
    color: white !important;
    font-variant: small-caps !important;
    font-family: calibri !important;
    font-size: 16px !important;
}

.userkarma a {
    font-variant: normal !important;
    font-weight: 100 !important;
}

#header-bottom-right:hover {
    opacity: 0.7 !important;
    transition: opacity 0.5s, color 0.5s, text-shadow 0.5s;
    color: #ffa70d !important;
}

#userbarToggle {
    border-radius: 0px !important;
    background: transparent !important;
    border-right: none !important;
}

#viewImagesButton, .tabmenu li a[href="http://www.reddit.com/r/CyanideandHappiness/controversial/"], .tabmenu li a[href="http://www.reddit.com/r/CyanideandHappiness/ads/"] {
    display: none !important;
}

.thing .title:visited, .thing.visited .title {
    color: #2f264d !important;
}

.thing .title {
    color: orange !important;
}

.thing .title, .thing.stickied a.title, .thing.stickied a.title:visited, .thing.stickied a.title.visited {
    font-size: 20px !important;
    font-family: calibri !important;
}

.link.last-clicked {
    border: none !important;
}

.thing .tagline .author.moderator, .thing .tagline .author.submitter {
    border-radius: 0px !important;
    font-weight: 200 !important;
    padding: 3px !important;
    transition: background 0.4s;
}

.thing .tagline .author.moderator:hover, .thing .tagline .author.submitter:hover {
    transition: background 0s;
}

.arrow.down, .arrow.up, .arrow.downmod, .arrow.upmod, .thumbnail.default, .thumbnail.self, .thumbnail.nsfw {
    background-image: url(http://i.imgur.com/6rcXlSF.png) !important;
}

.morelink {
    background-image: none !important;
    background: #2f264d !important;
    color: white !important;
    border: none !important;
}

.morelink .nub {
    display: none !important;
}

.morelink a {
    color: white !important;
    font-weight: 100 !important;
    font-family: arial !important;
    font-variant: small-caps !important;
    font-size: 20px !important;
    transition: color 0.4s;
}

.morelink a:hover {
    color: #ffa70d !important;
    transition: color 0s;
}

.nextprev a, .next-suggestions a {
    border-radius: 0px !important;
    background: #2f264d !important;
    color: white !important;
    font-weight: 100 !important;
    border: none !important;
    transition: color 0.3s;
}

.nextprev a:hover, .next-suggestions a:hover {
    color: #ffa70d !important;
    transition: color 0s;
}

.subButtons a, .subButtons span {
    border-radius: 0px !important;
    border: none !important;
    font-weight: 100 !important;
    font-variant: small-caps;
    font-size: 14px !important;
    transition: color 0.6s, text-shadow 0.6s !important;
    padding: 2px 6px !important;
    padding-top: 1px !important;
}

.subButtons a:hover, .subButtons span:hover {
    text-shadow: 0px 0px 10px black !important;
    transition: color 0.3s, text-shadow 0.3s, box-shadow 0.5s !important;
}

.fancy-toggle-button .add {
    box-shadow: 1px 1px 3px grey !important;
}

.tabmenu.formtab .selected a {
    background: #2f264d !important;
}

.tabmenu.formtab a {
    background: #2f264d !important;
}

.pagename {
    color: white !important;
    font-weight: 100 !important;
}

#RESShortcutsEditContainer {
    background: transparent !important;
    color: white !important;
}
}
