css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum") {
#brdmenu {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416080141-main-menu.png");
    background-repeat: no-repeat;
    height: 30px;
}

#brdwelcome {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416081454-brd-visit.png");
    background-repeat: no-repeat;
    color: #000000;
    font-weight: bold;
    height: 60px;
    padding: 10px;
    width: 950px;
}

#contenu {
    background: url("http://image.noelshack.com/fichiers/2014/46/1416000182-fondhiver.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
    height: 100%;
    margin: 0;
    padding: 3px;
    width: 100%;
}

#footer_box {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416083631-brd-about.png");
    background-repeat: no-repeat;
    height: 100px;
    width: 950px;
}

#head_forum {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1415999633-winter2015.png");
    background-repeat: no-repeat;
    height: 450px;
    position: relative;
    width: 950px;
}

#head_links {
    bottom: 13px;
    left: 508px;
    position: absolute;
}

#head_links a {
    color: #0499c7;
}

#navextra1 a {
    color: #04268a;
}

#navindex a {
    color: #04268a;
}

#navlogout a {
    border-color: #ffffff;
    color: #04268a;
}

#navprofile a {
    color: #04268a;
}

#po p input {
    color: #037dff;
}

#qjump div label {
    color: #0073ff;
}

.box {
    border: 1px solid #7d23da;
}

.pun H2, #brdmenu {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416080026-main-head.png");
    background-repeat: no-repeat;
    color: #FFFFFF;
    height: 22px;
    width: 100%;
}

.pun TABLE, .pun DIV, .pun FORM, .pun P, .pun H1, .pun H2, .pun H3, .pun H4, .pun PRE, .pun BLOCKQUOTE, .pun UL, .pun OL, .pun LI, .pun DL, .pun DT, .pun DD, .pun TH, .pun TD, .pun FIELDSET, .pun IMG {
    list-style: none outside none;
    margin: 0;
    padding: 0;
}

.pun TH {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416080723-fondtitres.png");
    background-repeat: no-repeat;
    color: #000000;
    font-size: 13px;
    height: 100%;
    text-align: center;
}

DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
    background-color: #ffffff;
}

DIV.icon {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416087273-div.png");
    background-repeat: no-repeat;
    float: left;
    height: 49px;
    width: 40px;
}

DIV.inew {
    background-image: url("http://image.noelshack.com/fichiers/2014/46/1416087433-inew.png");
    background-repeat: no-repeat;
    height: 49px;
    width: 40px;
}

DIV.postright, DIV.postfootright {
    border-color: #B059E0;
    border-left: 18em solid #def0ff;
}

dd.postavatar {
    background-color: #def0ff;
}

dd.usercontacts a {
    background-color: #def0ff;
    color: #8263ff;
}

dd.usertitle {
    background-color: #def0ff;
    color: #0aa9ff;
}

div label input {
    color: #0073ff;
}

div label select {
    color: #0073ff;
}

div.box {
    border-color: #5f8af5;
}

div.inbox {
    color: #ffffff;
}

div.inbox div {
    background-color: #ffffff;
    color: #04268a;
}

div.inbox div a {
    background-color: #ffffff ;
    border-color: #ffffff ;
    color: #0c62e3 ;
}

div.inbox ul li {
    color: #9670ff;
}

div.incqbox h4 {
    background-color: #b2baff;
    border-color: #0066cc;
    color: #3366cc;
}

div.incqbox p {
    background-color: #b0b8ff;
    color: #ffffff;
}

div.infldset.txtarea div input {
    color: #78dbff;
}

div.infldset.txtarea label textarea {
    margin-left: 5px;
}

div.inform table {
    margin-left: 5px;
    margin-top: 5px;
}

div.postfootright ul {
    background-color: #ffffff;
}

div.postfootright ul hr {
    background-color: #ffbdfd;
}

div.postleft dl dd {
    background-color: #def0ff;
    color: #00bbff;
}

div.postleft dl dt {
    background-color: #ffffff;
}

div.postsignature {
    background-color: #def0ff ;
}

div.postsignature hr {
    background-color: #7b85fa;
}

div.tclcon {
    color: #470447;
    background-color: #ffffff;
}

element.style {
}

h2 span a {
    color: #ffffff;
}

p.conr {
    color: #ffffff;
}

p.conr a {
    color: #ffffff;
}

p.pagelink.conl {
    color: #4d41fa;
}

p.pagelink.conl a {
    color: #112dba;
}

p.pagelink.conl strong {
    color: #100ac7;
}

p.postlink.conr a {
    color: #9670ff;
}

span.byuser {
    color: #03a7ff;
}

span.conr {
    color: #ffffff;
}

td fieldset legend {
    color: #6141e0;
}

td.tc2 {
    background-color: #ffffff;
    color: #3ca8f0;
}

td.tc3 {
    background-color: #ffffff;
    color: #3ca8f0;
}

td.tcl {
    background-color: #ffffff;
    color: #7094ff;
}

td.tcr {
    background-color: #ffffff;
    border-color: #9191f7;
}

td.tcr a {
    color: #4e0dff;
}

th.tc2 {
    background-color: #ffffff;
    color: #01127d;
}

th.tc3 {
    color: #01127d;
}

th.tcl {
    background-color: #ffffff;
    border-color: #7d007d;
    color: #01127d;
}

th.tcr {
    color: #01127d;
}

tr td fieldset {
    background-color: #ffffff;
    border-color: #ffffff;
    margin-right: 5px;
}

ul li a {
    color: #8e59ff;
}

ul.bblinks li {
    color: #54bbff;
}

ul.bblinks li a {
    color: #3c63fc;
}

ul.conl li {
    color: #2147b8;
}

ul.conr li a {
    color: #04268a;
    text-decoration: none;
}

user agent stylesheetdiv {
    display: block;
}

}
