css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mlpchan.net") {

body {
background: #000;
color: #00bb00;
}

div.post.reply .body {
background-color: #131313;
}

p.intro {
background-color: #131313;
}

div.post.reply {
background: #131313;
border-color: #131313;
}

p.intro span.name {
color: #00bb00;
}

a:hover, p.intro a.post_no:hover {
color: #00bb00;
}

a, a:visited {
color: #00bb00;
}

a.bodylink {
color: #00bb00;
}

header div.subtitle, header h1 {
color: #00bb00;
}

div.banner, div.banner a {
color: #00bb00;
}

#qrDisplayButton {
color: #00bb00;
}

.boardlist.top .boardlistpart a, .boardlist.top .boardlistpart a:visited {
display: inline-block;
color: #00bb00;
padding: 6px 3px;
margin: 0 1px;
font-size: 16px;
visibility: visible;
}

#settingsScreen {
color: #00bb00;
background-color: #000;
}

h2 {
color: #00bb00;
}

p.intro span.subject {
color: #00bb00;
font-size: 14px;
}

}
