css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("genmay.com"){

/*step one: ads?  I think not*/
.ad300{display:none!important;}

/*Why the fuck did they use white for the wrap bg???*/
div#wrap {background:#050505!important;color:#ffffff!important;}

/*inner boxes*/
.postarea,.mainstory,.hptabber,#homepageleft,#homepageright,#sidebarright,.thumb_side,.thumb{background:#000000!important;color:#ffffff!important;}
.mainstory a,.hptabber a,#homepageleft a,#homepageright a,#sidebarright a,.thumb_side a,.thumb a{color:#FFCC66!important;}

/*thumbs*/
.thumb_small,.thumb_big{border-color:#666666!important;}

/*dotted dividers*/
div {border-color:#333333!important;}

/*text time*/
.headline a{color:#DDDDDD!important;}

h2{color:#CCDDFF!important;}

.story{color:#FFFFFF!important;}

/*image time*/
div#navbar,ul#nav li a{background-image:
url("data:image/png;base64,") !important;color:#CCCCCC!important;
}

/*search box SUCKS*/
.searchBoxWrap{background-image:
url("data:image/png;base64,") !important;color:#CCCCCC!important;
}
.searchBoxWrap span input{color:#bbbbbb!important}
.searchBox{background-color:#111111!important;}

/*Arrow and Page Icons*/
.cat li a:hover{background-color:#333333!important;}

ul.cat li a{background-image:
url("data:image/png;base64,") !important;
}

.widget{background-image:
url("data:image/png;base64,") !important;
}

[src="http://www.genmay.com/frontpage/wp-content/themes/genmay_wordpress/images/rss.gif"]
{background-image:
url("data:image/png;base64,") !important;width:0px !important;height:14px !important;padding-right:14px !important;
}
}
