css:
@-moz-document domain("hitbox.tv") {
@font-face {
    font-family: "Futura";
    src: url("https://dl.dropboxusercontent.com/u/12308005/Fonts/Futura.otf"),
         url("https://dl.dropboxusercontent.com/u/12308005/Fonts/Futura.eot"),
         url("https://dl.dropboxusercontent.com/u/12308005/Fonts/Futura.woff") format("woff"),
         url("https://dl.dropboxusercontent.com/u/12308005/Fonts/Futura.ttf") format("truetype"),
         url("https://dl.dropboxusercontent.com/u/12308005/Fonts/Futura.svg") format("svg");
}

* {
    font-family: "Futura";
}

.chatBody {
    padding: 0px;
}

.theme-dark .chat-messages {
    background: rgba(0, 0, 0, .5);
}

.theme-dark .chat-message-animated {
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, .5),
                0px 0px 10px rgba(255, 255, 255, .125) inset;
    margin: 5px;
    padding: 5px !important;
}

.theme-light .chat-message-animated {
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, .25);
    margin: 5px;
    padding: 5px !important;
}

.title {
    padding: 0px !important;
    text-shadow: 0px 0px 10px;
}

[src="http://edge.sf.hitbox.tv/static/img/chat/default/maki2.png"] {
    background: url("http://static-cdn.jtvnw.net/jtv_user_pictures/chansub-global-emoticon-ddc6e3a8732cb50f-25x28.png");
    width: 25px !important;
    height: 28px !important;
    padding-left: 25px;
}

@-moz-document domain("hitbox.tv") {
    .tse-scroll-content.ng-scope.ng-pristine.ng-valid,
    .tse-scroll-content.ng-scope.ng-valid.ng-dirty {
        padding-right: 3px;
    }
}
}
