css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tv.com") 
{

/*

 * Author      : CB
 * Date        : September 2008
 * Description : People were complaining about the bright white, so I made it dark brown.
 * Usage       : modify/change/use whatever you want. Credit would be nice, but is not neccesary.
 * Note        : This is NOT a complete style, but I don't have enough time to change everything. 
                 So, it might be slightly ugly/incomplete in places, but at least it's not bright white anymore. 
 * Update      : Again - not complete. Quite a big CSS file and not enough time to wade through it all in one day ; correct as needed please. CB
 
 */


/*body*/

body, element.style, .ad
{background: #343cbf !important; color:#a1a1a1 !important;}

/* white text */
 .module_title, label, .useract_toolbar ul li a, .score, .votes, .rank, .item, .item_detail, .browse_box span, .module .header .module_title, .module .header .module_title a, .board, .messages, .top_rank .body .sub_title, #todays_galleries .body .title span , #user_console .header .module_title a, .paginated_nav .pag_wrap span, .paginated_nav .pag_wrap .jump_links, #favorite_shows .subheader 
{color: #f0f0f0 !important;}
 
/*black text*/
#content #top .content_title h2, .top_rank .body .score  
{color: #000 !important;}
 
#content #top, #content .col_wrap {background:#eee !important; }

/* EYEBROW is the really small band on the top of the page */
#eyebrow { background:#343cbf !important ; color:#ccc;}
#eyebrow a {color:#ccc;}
#bg_top { background:#343cbf !important ;}

/* HEADER this is the main TV.com menu - adjust border color*/
#header {border-bottom:3px solid #343cbf !important;}

/* USER PROFILE/CONSOLE, this is the thing in the upper right corner with your icon and name */
#user_console {background:#ccc !important ;}
#user_console .header .wrap {background:#343cbf !important ;}
#user_console .body {background:#343cbf !important ; color:#a1a1a1;}
#user_console .body .basic_info .status span{background:#343cbf !important ;color:#fff !important;}
#user_console .body .basic_info .msgs {background:#343cbf !important ;}

/* LAYOUT */
#content {background:#343cbf !important ;}

/* GENERIC MODULES */
.module {background:#5d5d5d !important ;}
.module .header .module_title {color:#a1a1a1 !important;}
.module .header .module_title a {color:#a1a1a1 !important;}

/* COMMUNITY MODULES */
.module.community .header .wrap {background:#343cbf !important ;}
.module.community .header .module_title {color:#a1a1a1!important;}
.module.community .header .module_title a {color:#fcfcfc !important;}
.module.community .header span, .module.community .header span a {color:#343cbf;}
.module.community .header .icon {background:#343cbf !important;}

/* ACTION DIALOG BUBBLE */
.module .header .wrap .action_box {background:#343cbf !important;}
.module .header .wrap .action_box .wrap {background:#343cbf !important;}
.module .header .wrap .action_box .wrap .wrap {background:#343cbf !important ;}

/* MINI COL SPONSORED MODULES */
.module.green .header .wrap {background:#343cbf !important;}
.module.pink .header .wrap {background:#343cbf !important;}
.module.green .header .module_title,.module.green .header .module_title a {color:#000 !important;}
.module.pink .header .module_title,.module.green .header .module_title a {color:#000;}
#content #top .content_title h2, #content #top .content_title h2 a.forum_head {color:#000 !important;}
#content #top .content_title h2 span, #content #top .content_title h2 span a.forum_head {color:#000;}

/* LISTINGS MODULE */
#listings .header .wrap {background:#343cbf !important;}

/* TOP NEWS MODULE */
#top_news .body {background:#343cbf !important ;}
#top_news .thumb span, #top_news .thumb span a {background:#343cbf; color:#343cbf !important;}

/* COMMUNITY MODULE - TOP MEMBERS */
#top_members .thumb {background:#343cbf !important ;}
#top_members .body ul li .wrap .emblem {background:#343cbf;}

/* COMMUNITY - TOP FORUM TOPICS */
.related_forum_topics .body ul li {background:#343cbf !important  ;}

/* INFO CONTENT CONTAINER MODULE */
#info_block .body {background:#343cbf !important ;}

/* SHOWCASED (SIDE) PHOTO GALLERIES MODULE */
.showcased_galleries {background:#343cbf !important;}

/* NEW PHOTOS MODULE */
.new_photos .body ul.gallery_titles li .title .on {color:#343cbf !important;}

/* STORY TOOLBAR */
#story_toolbar_wrap {background:#343cbf !important ;}

/* inbox PM black text on whiet BG */
#popup #content {background: #fcfcfc !important;color: #a1a1a1 !important;}
#popup {background: #a1a1a1 !important; }
.m-5 {background: #a1a1a1 !important;}

/* FD TOPSLOT MODULE */
#fd_carousel .body {background:#343cbf !important ;}

/* WHAT'S NEW MODULE */
#whats_new .header {background:#343cbf !important ;}

/* dark brown border around rank numbers */
#top_shows .body li .rank {border:solid #343cbf !important;}

/* SHOWSPACE: PHOTO STREAM */
#show_photo_stream .body .thumb_wrap {background:#343cbf !important ;}

/* SHOWSPACE: EPISODE TABLE */
#eps_table .body table th {background:#000 !important;}
#eps_table .body table td {background:#343cbf !important;}

/* SHOWSPACE: EPISODE DOWNLOADS */
#eps_downloads table td {background:#343cbf !important;}

/* SHOWSPACE: EPISODE */
#indepth_block .body .ep_tabs ul li.on a {background:#343cbf !important ; color:#a1a1a1;}

/* =PHC
------------------------------------------------------------------------------*/
#content #top .gallery_title h2 {color:#f0f0f0 !important;}


/* =VHC
------------------------------------------------------------------------------*/
#content #top .video_title h2 {color:#f0f0f0 !important;}

.browse_videos .header .wrap{background:#343cbf !important; }
/* END =VHC
------------------------------------------------------------------------------*/

/* FORUM/BOARD LISTING MODULE */
.forum_list .body ul li .wrap, .forum_messages .post {background:#a1a1a1 !important;}

/* NEWBIES SPOTLIGHT MODULE */
#newbies_spotlight .header {background:#343cbf !important ;}

/* TOTAL SUBMISSIONS MODULE */
#total_submissions .body {background:#343cbf !important;}

/* REVIEW OF THE DAY MODULE */
#featured_review .score_box {color:#fcfcfc !important;}
#featured_review .body .title span {color:#fcfcfc !important;}


/*------------- FORUM MESSAGE LIST/MESSAGE PREVIEW ---------------*/

.forum_messages .thumb {background:#a1a2a2 !important ; }
.forum_messages .body ul li .profile .wrap .msg_date {background:#f6f6f6 !important ;}
.forum_messages .action_panel .action{background:#000 !important ;}
.forum_messages .action_panel .action.quick_reply{background:#9f9f9f !important; }
.forum_messages .action_panel .action.quick_quote{background:#9f9f9f !important; }
.forum_messages .action_panel .action.reply{background:#a1a1a1 !important ; }
.forum_messages .action_panel .action.quote{background:#a1a1a1 !important;}

/* nested replies */
.forum_messages table td.quote{background:#a1a1a1 !important ;}

/* WYSIWYG */
.forum_post .action_panel .action{background:#a1a1a1 !important ;}

/* FORUM SEARCH */
.forum_search form .text_input .text_field_wrap {background:#a1a1a1 !important ;}

/* FORUM SEARCH RESULTS */
.forum_search_results th {background:#a1a1a1 !important ;}

/* FORUM PAGINATION */
.forum_pagination.top{background:#9f9f9f !important; }
.forum_pagination.bottom{background:#9f9f9f !important ; }

/* NEWS CONTAINER - HUB & STORY PAGE */
.news_container .body {background:#343cbf !important ;}
.news_container .body.last {background:#343cbf !important ;}

/* NEWS STORY MODULE - HUB & STORY PAGE */
.news_story .body .title {color:#f0f0f0 !important;}

/* FEATURE SPOTLIGHT MODULE*/
#feature_spotlight .header .wrap {background:#343cbf !important ;}


/* TOP USERNAME */
#content #top .content_title.profile h2 {color:#fcfcfc !important;}

/* USER MODULE */
#user .body .level {background:#343cbf !important ;}

/* MY FEED MODULE */
#feed .body ul li {background:#343cbf !important ;}

/* MY CONTRIBUTIONS MODULE */
#contributions .body ul li ,feed .body ul li {background:#343cbf !important ;}

/* MY FRIENDS MODULE */
#friends .body ul li , feed .body ul li {background:#343cbf !important ;}
#friends .body ul li.online ,feed .body ul li {background:#343cbf !important ;}

/* MY FAVORITE SHOWS MODULE */
#favorite_shows .body ul li , feed .body ul li {background:#343cbf !important ;}

/* ABOUT ME MODULE */
#about_me .body dt {color:#f0f0f0 !important;}

/* MY RECENT REVIEWS MODULE */
#recent_reviews .body ul li , feed .body ul li {background:#343cbf !important ;}
#recent_reviews .body ul li .wrap .thumb_box , feed .body ul li {background:#343cbf !important ;}
#recent_reviews .body ul li .wrap .thumb_box .thumb_up ,feed .body ul li {background:#343cbf !important ;}
#recent_reviews .body ul li .wrap .thumb_box .thumb_down ,feed .body ul li {background:#343cbf !important ;}

/* RECENT VIDEOS MODULE */
#recent_videos .body .icon , feed .body ul li {background:#343cbf !important ;}


/* SHOW/ACTOR UPDATES (DASHBOARD)*/
#updates .body li ,feed .body ul li {background:#343cbf !important ;}
#blog_post.forum_post .body .preview .wrap {background:#343cbf !important ; }
.listings_set_location .no_providers,.listings_set_location .body form {background:#343cbf !important ;}

/* LISTINGS GRID */
.listings_grid .timeslot_nav .nav .prev_wrap a,.listings_grid .timeslot_nav .nav .next_wrap a {background:#343cbf !important;}
.listings_grid .body .content .scroll_bar {background:#343cbf !important ;}

/* LISTINGS BY NETWORK*/
.listings_network table td.show .wrap{background:#343cbf !important ;}
.rankings .header .rankings_nav, .rankings .header.rankings_nav ul li.on{color:#f0f0f0 !important;}
.rank, .ranking_table th.rank {color:#f0f0f0 !important;}

/* TOP RANKING */
.top_rank .body .sub_title{color:#f0f0f0 !important;}

/* RANKING TABLE */
.ranking_table th {color:#f0f0f0 !important;}

/* BROWSE BOX */
.browse_box ul li.on a{color:#f0f0f0 !important;}

/*--- JUST SHOWS HUB ----*/
.shows_new_videos .body .title {color:#f0f0f0 !important;}
#show_hub_gumballs .header li.first {color:#f0f0f0 !important;}

/* SEARCH */
#content #top {background:#343cbf !important ;}
.search_container {background:#343cbf !important ;}
.search_nav_tabs ul li.on {background:#343cbf !important ;}

/* MODERATION: DRILLDOWN USER SUBMISSIONS */
.mod_user_submitted .body li .wrap .input_wrap {background:#343cbf !important ;}
}
