css:
@-moz-document url-prefix("https://www.youtube.com/watch?") {
body {background: black !important;}
#yt-masthead-container {
  opacity: 1;
  transition: opacity 0.2s linear;
}
#yt-masthead-container:not(:hover) {opacity: 0;}
#body-container #player, #body-container #player-api {
  margin: 0 auto 30px !important;
  max-width: 1280px !important;
  width: 100% !important;
  height: 720px !important;
  position: static !important;
}
#placeholder-player .player-api {display: none;}
#player .html5-main-video, #player .html5-video-content {
  left: 0 !important;
  top: 0 !important;
  width: 100% !important;
  height: 100% !important;
}
#content {
  width: 100%;
  max-width: 1280px !important;
  top: 0 !important;
}
#watch7-sidebar {margin-top: 0 !important;}
#placeholder-playlist {margin-top: 125px !important;}
#watch7-sidebar-contents {margin-top: 120px !important;}
.html5-video-container {position: static;}
}
