css:
@-moz-document url("http://platinumgod.co.uk/rebirth"), url-prefix("http://platinumgod.co.uk") {
body {
min-width: 350px;
background: #0B0A0A !important;
font-family: helvetica, arial, sans-serif;
margin: 0;
} 

.main {
background: #1D1C1C !important;
padding: 10px;
margin-left: 320px;
padding-top: 60px;
}

.unit a {
display: block;
text-align: center;
margin: 10px 0;
font-weight: bold;
text-decoration: underline;
color: #ADABAB !important;
}

* {
font-family: 'Open Sans', sans-serif;
text-decoration: none;
color: #ADABAB !important;
}
}

@-moz-document url("http://platinumgod.co.uk/") {
body {
min-width: 350px;
background: #101010 !important;
font-family: helvetica, arial, sans-serif;
margin: 0;
}

.main {
background: #282727 !important;
padding: 10px;
margin-left: 320px;
padding-top: 60px;
}

.unit a {
display: block;
text-align: center;
margin: 10px 0;
font-weight: bold;
text-decoration: underline;
color: #CDC9C9 !important;
}


* {
font-family: 'Open Sans', sans-serif;
text-decoration: none;
color: #CDC9C9 !important;
} 
}

@-moz-document url("http://platinumgod.co.uk/rebirth-info") {
.synergy, .calc, .facts, .seeds, .babies, .cookies, .faq, .boi-rebirth, .r-synergy {
background: #1F1F1F !important;
padding: 10px;
padding-top: 60px;
font-family: 'Open Sans', sans-serif;
margin: 0 12% 0 12%;
}
}

@-moz-document url("http://platinumgod.co.uk/rebirth-seeds") {
.synergy, .calc, .facts, .seeds, .babies, .cookies, .faq, .boi-rebirth, .r-synergy {
background: #181818 !important;
padding: 10px;
padding-top: 60px;
font-family: 'Open Sans', sans-serif;
margin: 0 12% 0 12%;
}

.seeds table td {
font-size: 14px;
color: #FFF;
background: #272525 !important;
border-top: 1px solid #888686;
border-bottom: 1px solid #B3A9A9;
padding: 8px;
}

.seeds table tr:hover td {
background: #322F2F !important;
}
}

@-moz-document url("http://platinumgod.co.uk/babies") {
.babies {
margin: 0 6% 0 6%;
background: #252525 !important;
-moz-box-shadow: 1px 3px 4px 0 rgba(0,0,0,0.4);
-webkit-box-shadow: 1px 3px 4px 0 rgba(0,0,0,0.4);
box-shadow: 1px 3px 4px 0 rgba(0,0,0,0.4);
}
}

@-moz-document url("http://platinumgod.co.uk/synergies") {
.synergy, .calc, .facts, .seeds, .babies, .cookies, .faq, .boi-rebirth, .r-synergy {
background: #252525 !important;
padding: 10px;
padding-top: 60px;
font-family: 'Open Sans', sans-serif;
margin: 0 12% 0 12%;
}

.synergy .synergy-text {
width: 90%;
background: #1D1D1D !important;
text-align: center;
padding: 35px 5%;
margin: 20px 0;
}

.ddcommon .ddTitle .ddTitleText {
display: block;
background: #1D1D1D !important;
}

.dd .ddChild li {
padding: 5px;
background-color: #494848 !important;
border-bottom: 1px solid #060606 !important;
}

.dd .ddChild li .ddlabel {
color: #FCFAFA !important;
}

.dd .ddChild li.selected {
background-color: #221E1E !important;
color: #3C3B3B !important;
}
}

@-moz-document url("http://platinumgod.co.uk/calculator") {
.synergy, .calc, .facts, .seeds, .babies, .cookies, .faq, .boi-rebirth, .r-synergy {
background: #2F2F2F !important;
padding: 10px;
padding-top: 60px;
font-family: 'Open Sans', sans-serif;
margin: 0 12% 0 12%;
}

.calc .calc-main {
background: #272727 !important;
padding: 35px 20px 20px 20px;
-webkit-box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.2);
-moz-box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.2);
box-shadow: 0px 1px 4px 0px rgba(0,0,0,0.2);
font-size: 0;
text-align: center;
}

.calc .calc-main .dmg, .calc .calc-main .tps, .calc .calc-main .dps {
width: 30%;
height: 75px;
border: 1px solid #d9d9d9;
border-top: 1px solid #c0c0c0;
display: inline-block;
margin: 0 0.5%;
font-size: 21px;
background: #1F1F1F !important;
line-height: 75px;
padding: 0 1%;
font-weight: 200;
position: relative;
vertical-align: top;
}

.calc .calc-main .delay {
position: absolute;
bottom: -26px;
font-size: 13px;
height: 25px;
line-height: 25px;
left: -1px;
right: -1px;
background: #151414 !important;
border: 1px solid #d9d9d9;
border-top: none;
padding: 0 8px;
text-align: center;
}

.calc .calc-main #infotext {
display: inline-block;
font-size: 20px;
text-align: center;
background: #1F1F1F !important;
width: 20%;
padding: 40px 2.5%;
vertical-align: top;
margin: 25px 2.5% 0 2.5%;
}

.calc .calc-main .button img {
background-color: rgb(50, 50, 50) !important;
}

.ddcommon .ddTitle .ddTitleText {
display: block;
background: #1F1F1F !important;
}

.dd .ddChild li {
padding: 5px;
background-color: #2D2D2D !important;
border-bottom: 1px solid #c3c3c3;
}

.dd .ddChild li.selected {
background-color: #383636 !important;
color: #4F4E4E;
}

.dd .ddChild li .ddlabel {
color: #F9F9F9 !important;
}

.dd .ddChild li {
padding: 5px;
background-color: #444343 !important;
border-bottom: 1px solid #c3c3c3;
}

.dd .ddChild li.selected {
background-color: #383838 !important;
color: #5E5E5E !important;
}

.dd .ddChild li .ddlabel {
color: #FAFAFA !important;
}
}
