css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ruseo.net") {
/**********************************/
   /*шапка без рекламы и с прокруткой*/
   /**********************************/
  
   /*шапка с рекламным баннером едет вверх (скрывая баннер)*/
   #page-header{margin-top:-132px}
   /*верх с личкой*/
   .icon-ucp{position:fixed;top:0;left:-350px;width:1090px !important;background:#474b5d;padding-right:40px;z-index:100;color:#FFF !important;padding-left:450px;height:30px;}
   /*внутри верха с личкой*/
   .icon-ucp a{color:#87CEEB !important}
   
   /*ссылка на главную*/
   #logo{position:fixed;top:-112px;left:-295px;z-index:200;}
   
   /*картинка логотипа*/
   .icon-home{display:none}
   
   /*внутри ссылки на главную*/
   #logo:after{content:'Главная';color:orange !important;font-size:12px;}
   
   /*верх с балансом*/
   .linklist.rightside{position:fixed;background:#474b5d;margin:-5px -20px 0 0;right:15px;z-index:100;height:31px;}
   
   /*внутри верха с билансом*/
   .linklist.rightside a{color:#87CEEB !important}
   
   /*форма поиска по сайту*/
   form#search{position:fixed;z-index:200;top:4px;left:500px;}
   a[title='Параметры расширенного поиска']{display:none}
   input#keywords{width:200px !important;border-radius:10px}
   input.button2[type=submit]{border-radius:10px;}
   input.button2[type=submit]:hover{color:#FFF}
  
  
  
  
   /*******************/
   /*разные полезности*/
   /*******************/
   
   /*кнопка жалобы не нужна (для избежания случайных нажатий)*/
   .report-icon{display:none}
   
   /*кнопки социальных сетей*/
   .yashare-auto-init.b-share_theme_counter{display:none} /*соцсети не нужно*/
   
   /*твиттер не нужен (выпилился не везде)*/
   #twitter-widget-1{display:none !important;}
  
  
   /********/
   /*подвал*/
   /********/
   
   /*самый низ со счетчиками не нужен*/
   #page-footer{display:none}
}
