css:
@-moz-document domain("behance.net") {
[class*=ImageElement-placeholder-][style*=background] {
  background: none !important;
}
::-webkit-scrollbar {
  width: 14px;
  height: 14px;
  background: #1a1a1a;
}
::-webkit-scrollbar-button:single-button {
  background: radial-gradient(circle at center, #333 40%, #1a1a1a 40%)
}
::-webkit-scrollbar-button:single-button:hover {
  background: radial-gradient(circle at center, #444 40%, #1a1a1a 40%);
}
::-webkit-scrollbar-button:single-button:active {
  background: radial-gradient(circle at center, #555 40%, #1a1a1a 40%);
}
::-webkit-scrollbar-track-piece {
  background: #333;
  border: 4px solid #1a1a1a;
  border-radius: 8px
}
::-webkit-scrollbar-track-piece:hover {
  background: #444;
}
::-webkit-scrollbar-track-piece:active {
  background: #555;
}
::-webkit-scrollbar-thumb {
  border: 3px solid #1a1a1a;
  border-radius: 8px;
  background: #555
}
::-webkit-scrollbar-thumb:hover {
  background: #666;
}
::-webkit-scrollbar-thumb:active {
  background: #777;
}
:-webkit-autofill {
  box-shadow: 0 0 0 1000px #111 inset;
  -webkit-text-fill-color: #fff;
}
}

@-moz-document regexp("https?://(www\\.)?behance.net(/gallery/\\d+.*|/galleries.*|/search.*|/?|/[^/]+?(|/stats|/appreciated|/followers|/following|/resume|/team_members|/wip|/members|/collections))") {
#nav-wrap,
.rf-secondary-nav,
.nav--filter {
    background: linear-gradient(to bottom, #30343a 50%, #181a1e 100%);
    box-shadow: 0 0 20px #111;
}
.rf-secondary-nav__link,
.rf-secondary-nav__link:hover {
    color: #88b6e6!important
}
.rf-secondary-nav__link:hover,
.rf-secondary-nav__link.active:hover,
.nav--filter .nav__link:hover {
    text-decoration: underline;
    text-decoration-skip: ink
}
.rf-secondary-nav__link.active,
.rf-secondary-nav__link.active:hover {
    color: white
}
a,
.fake-link {
    color: #2391FF;
}
.breadcrumb-text {
    color: #888;
    font-weight: 600;
}
#site-content {
    background: none!important
}
#site-content header {
    background-color: #2e3c4a;
    color: white;
    border-color: #111
}
#project-sidebar,
#project-container {
    background: #202224!important;
    color: #aaa!important;
}
#project-main {
    border-right: 1px solid #181818 !important;
    box-sizing: border-box !important;
}
#project-header {
    background: linear-gradient(#555, #333) !important;
    border-color: #111 !important;
}
.project-block {
    border-color: #333
}
.project-title {
    color: white;
}
.project-fields-field a,
.secondary-text,
.secondary-text a {
    color: #999 !important;
}
.project-fields-field a:hover {
    color: #ddd!important
}
#project-stats {
    bottom: -40px
}
#project-stats .project-stat {
    border-color: #444
}
.featured-badge-container img {
    filter: drop-shadow(0px 0px 2px black);
    -webkit-filter: drop-shadow(0px 0px 2px black);
}
#project-modules .title {
    color: #fff;
}
#project-modules .sub-title {
    color: #fff;
}
#project-modules .editor-text .main-text,
#primary-project-content.project-styles .main-text {
    color: #ccc !important;
    font-size: 12pt !important;
}
#project-modules .caption,
#project-modules .caption-text-wrap {
    color: #ccc !important;
}
#primary-project-content {
    background-color: #202224 !important;
}
.divider {
    border-color: #555 !important;
}
#project-modules *[style*="color"] {
    color: white!important;
}
#collect-and-share {
    background: #202224;
}
#collect-and-share .twitter-share-button {
    font-size: 110%;
    font-weight: bold;
    margin-top: 2px;
}
#footer-owners {
    padding: 23px 55px 17px;
}
#footer-owners .multiple-owners-list .owner-name,
#footer-owners-and-follow {
    background: #181818;
}
#footer-owners-and-follow {
    border-bottom: 2px solid #111;
    border-top: 2px solid #111;
}
#footer-owners .owner-location {
    color: #999;
}
#footer-owners .form-button {
    border-color: #aaa;
}
#footer-owners #follow-container-bottom a {
    border-color: #446!important;
}
#other-projects,
#other-projects-content {
    background: #202224;
    border-bottom: 2px solid #111;
}
#view-all-projects {
    background: black;
}
#comment {
    color: white;
}
#comment:focus {
    color: black;
}
#project-footer,
#project-footer .project-block,
#project-comments,
#project-block-footer-comments {
    background: #202224 !important;
    border: none;
}
#comments-container {
    width: 100% !important;
}
#comments-container > * {
    margin: 0 20pt;
}
#comments-header {
    color: #fff;
}
.form-text,
.form-textarea {
    background: #181818 !important;
    border-color: #282828 !important;
}
#project-footer {
    padding-top: 0;
}
#project-footer .project-block {
    padding: 27px 20px;
}
#project-footer .project-block-header {
    color: white;
}
#project-block-footer-comments .comment-container,
#project-block-footer-comments .comment-post-container,
#project-block-footer-comments .project-block-header,
.breakpoint-comments #project-footer-inner {
    max-width: inherit;
    padding: 0;
}
#project-block-footer-comments .comments-list:not(:empty) {
    border: none !important;
    margin: 0 -20px !important;
}
#project-block-footer-comments .comment-container {
    display: flex!important;
    margin: 0 -30px;
    padding: 20px 20px;
}
.comment-container:nth-child(odd) {
    background: #282a2d;
}
.comments-list .comment-user-image-link {
    float: none;
}
#project-block-footer-comments .comment-container .user-name-link {
    color: #729ECC !important;
}
#project-block-footer-comments .comment-container .user-name-link:visited {
    color: #7B98B7 !important;
}
#project-block-footer-comments .comment-date {
    color: #7D8493;
}
#project-block-footer-comments .comment-date:hover {
    color: #99A3B9 !important;
}
.comment-user-date-wrap {
    display: inline-block;
    margin-right: 2ex;
    width: 20%;
}
.comment-user-date-wrap a {
    display: block;
}
#project-block-footer-comments .comment-date:before {
    display: none;
}
#project-block-footer-comments .comment-text-wrap {
    display: inline-block;
    margin-top: -2px;
    vertical-align: top;
    width: calc(80% - 2ex - 4px);
}
.breakpoint-comments #project-footer-info-wrap .project-block,
.breakpoint-comments #project-footer-info-wrap .project-block-minimal {
    width: auto;
}
#project-block-footer-comments .comment-text {
    color: #eee;
    font-size: 10.5pt;
    line-height: 1.3;
}
.comment-spam {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
#project-block-footer-comments .comments-pagination {
    background: linear-gradient(#666, #444);
    border-bottom: 1px solid black;
    border-top-color: black!important;
    color: white;
    height: 3em;
    line-height: 3em;
    margin: 20px -20px;
}
#project-block-footer-comments .comments-pagination:hover {
    background: linear-gradient(#777, #444)
}
.form-button-see-more,
.form-button-loading {
    border-color: #222;
}
.form-button-see-more:hover,
.form-button-see-more:active {
    background: linear-gradient(#777, #555);
}
.sidebar-group {
    background: transparent;
    border: none
}
#project-sidebar {
    border: none!important;
}
.profile-list-name a,
#owner {
    color: white!important;
}
#owner-location-container .location-link {
    color: #bbb!important;
}
#owner-buttons-container {
    border-top-color: #444 !important;
}
.form-button-light-and-grey,
.form-button-light-and-blue,
.object-tag {
    background: linear-gradient(#666, #444);
    border-color: #444;
    color: white;
}
.form-button-light-and-blue {
    color: white;
    text-shadow: 0px 1px 0px #333;
}
.form-button-light-and-grey {
    color: #bbb;
    text-shadow: 0px 1px 0px #222;
}
.form-button-light-and-grey:hover,
.form-button-light-and-grey:focus,
.form-button-light-and-grey:active,
.form-button-light-and-blue:hover,
.form-button-light-and-blue:focus,
.form-button-light-and-blue:active,
.object-tag:hover,
.object-tag:active {
    background: linear-gradient(#777, #555);
    border-color: #666;
    color: white;
}
.form-button-light-and-blue.form-button-left-icon:before,
.project-icon-left:before {
    filter: invert(100%) grayscale(100%) contrast(150%);
    -webkit-filter: invert(100%) grayscale(100%) contrast(150%);
}
.sidebar-block {
    border-bottom: 1px solid #1b1b1b !important;
}
.sidebar-block.sidebar-actions .sidebar-action {
    background-color: #282828 !important;
    background-image: linear-gradient(to bottom, #333 70%, #282828 100%) !important;
    border-color: #222 !important;
    color: white !important;
    font-weight: bold;
}
.sidebar-block.sidebar-actions .sidebar-action:hover {
    background-color: #282828 !important;
    background-image: linear-gradient(to bottom, #444 70%, #383838 100%) !important;
    border-color: #222 !important;
}
.project-description {
    font-size: 11pt !important;
    line-height: 1.3 !important;
}
.project-published {
    font-size: 9pt;
}
.object-stats {
    border-color: #444 !important;
    color: #aaa !important;
}
.object-tag {
    border-color: #181818;
    box-shadow: 0px 1px 0px #686868 inset;
    color: #ddd;
}
.object-tag:hover,
.object-tag:active {
    color: #fff;
}
#super-footer {
    background: #0c0d0e;
}
#sorts-container {
    background-color: #111;
    border-bottom: 2px solid #333;
    box-shadow: inset 0 -20px 20px -20px black;
}
.sort:after {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
#top-panel,
#content-wrapper {
    background: #202224;
}
.list-links a {
    color: #2391FF;
}
.filter-block:after {
    background-color: #555;
}
.block-menu .menu-item {
    background: linear-gradient(#666, #444);
    border-color: #111;
    color: white;
}
.block-menu .menu-item:hover {
    background: linear-gradient(#777, #555);
}
.block-menu .menu-item:last-child {
    box-shadow: 0 1px 0 0 #181818;
}
#content {
    border-color: #444;
}
.project-cover {
    box-shadow: 1px 1px 3px #111;
}
.project-cover,
.project-cover .cover-info {
    background: #282828;
}
.cover-by {
    display: none;
}
#profile .cover-name-link,
#profile .wip-block .wip-title-link,
#profile .wip-block .wip-owner-link,
#profile .collection-block .gallery-name,
.cover-name-link,
.project-cover .cover-name-link {
    color: #2391FF;
    font-size: 11pt;
    line-height: 1.1;
}
#profile .user-row .user-name,
#profile .multiple-owners-list,
#profile .project-cover .single-owner-link,
.single-owner-link,
.multiple-owners-list {
    color: #5E96E5;
}
.project-owner-info--header.single-owner-info .profile-list-name,
.project-owner-info--header .multiple-owners-name {
    color: #aaa
}
.project-cover .cover-by-wrap {
    border-bottom: none;
}
.project-cover .cover-fields a,
.project-cover .cover-fields span {
    color: #888;
}
.project-cover .cover-fields a:hover,
.project-cover .cover-fields span:hover {
    color: #ccc;
}
.project-cover .cover-stat-wrap {
    background-color: #242424;
    border-color: #181818;
    box-shadow: none;
}
.cover-stat.cover-stat-highlighted:before,
.cover-stat:before {
    color: #666;
    text-shadow: 0px 1px 0px #111;
}
.cover-stat,
.cover-stat.cover-stat-highlighted {
    color: #777;
}
.project-cover .featured {
    border-color: #222;
    color: #B5A04E;
}
.content-container {
    background: #222;
}
.content-main:before {
    background: #333;
}
.content-section-header {
    border-color: #333;
}
#profile .content-section-nav-item.active {
    border-color: #aaa;
    color: white;
    font-weight: bold;
}
.content-minor-section,
.content-minor-section-fat {
    border-color: #222;
    border-width: 2px;
}
body,
#profile .profile-section .profile-stat-label:before {
    color: #aaa;
}
.team-list-location-link,
.profile-location-link,
#profile .content-section-nav-item,
#profile .profile-section a:not(.form-button):not(.draft-delete):not(.normal-link):not(.ts-full-search),
#profile .profile-section .fake-link,
#profile .add-custom-section,
#profile .empty-wrap a:not(.form-button) {
    color: #aaa;
}
#profile-display-name a,
.team-list-name a {
    color: white !important;
}
a.ss-social {
    color: white !important;
}
.profile-experience-position {
    color: #ccc;
}
.profile-gallery-content-wrap a.location-link,
.profile-gallery-content-wrap a.field-link,
.content-minor-bullet-list .content-minor-bullet-secondary {
    color: #aaa;
}
#profile-date,
.profile-date {
    color: white;
}
.empty-wrap {
    color: #888;
}
.wip-block {
    background: #282828;
}
#profile .wip-block .wip-owner-link {
    color: #4676B9;
}
.wip-block .wip-section {
    background: #242424;
    border-color: #111;
    color: #888;
}
.profile-gallery-content-wrap,
.gallery-stats .beicons-pre:before {
    color: #777;
}
.gallery-stats .gallery-stat-divided {
    border: none;
}
.project-cover-minimal,
.rf-project-cover {
    background: #282828;
    color: #888;
}
.profile-gallery-content-wrap .cover-img {
    box-shadow: 1px 1px 4px black;
}
.rf-project-cover .rf-project-cover__title {
    color: #ccc !important
}
.project-cover-minimal .project-appreciations,
.project-cover-minimal .project-owner-footer {
    color: #999;
}
.project-cover-minimal .project-owner:hover,
.rf-project-cover .rf-owners__owner-link:hover {
    color: white;
}
.project-cover-minimal .project-owner,
.rf-project-cover .rf-owners__owner-name {
    color: #888;
}
.rf-owners__list.rf-tooltip--container::after {
    border-top-color: #aaa
}
.rf-owners::after {
    background-color: #555
}
.rf-project-cover__stats {
    border-top-color: #444
}
.rf-project-cover__stat svg {
    fill: #777
}
#profile-display-name a.team-badge {
    color: black !important;
}
.profile-stat-count {
    color: #666;
}
.user-row .beicons-pre:before {
    color: #777;
}
.profile-gallery-content-wrap .user-stats {
    border: none;
}
.gallery-projects-wrap .project-cover-wrap,
.gallery-projects-wrap {
    background: none;
    border: none;
}
.gallery-projects-wrap .project-cover-wrap {
    border-left: 6px solid transparent;
}
#gallery .users-gallery-container .list-item:first-child,
#gallery .user-row:first-child,
#gallery .user-row {
    background: #282828;
    border: none;
    margin: 10px;
    padding: 12px 15px 12px 20px;
}
#gallery .profile-gallery-content {
    padding-bottom: 20px;
}
#gallery .collection-block {
    background: #333;
    padding: 10px 15px;
}
#gallery .collection-block .gallery-info-wrap {
    display: inline-block;
    float: none;
    margin-left: 0;
    margin-top: 3px;
    vertical-align: top;
    width: calc(100% - 520px);
}
#gallery .gallery-projects-wrap .project-cover-wrap:nth-child(n+3) {
    display: block;
}
.project-cover-minimal .user-item {
    background-color: transparent;
}
.project-cover-minimal .user-name {
    color: white;
}
.project-cover-minimal .user-name-hover {
    color: #A9D1FF;
}
.project-cover-minimal .action-label.new-tag {
    background: #555;
    color: #FFF;
    height: 18px;
    margin-top: -2px;
    padding: 2px 8px 3px;
}
#project-modules .editor-text .main-text,
#primary-project-content.project-styles .main-text {
    line-height: inherit
}
.tooltipi-white {
    background: #414850
}
.tooltipi-white:before {
    background: #222;
    box-shadow: -2px -2px 0 -1px #555;
}
.rf-project-cover__image-container {
  background-color: #333;
}
[class^=Project] {
  background: unset;
  color: unset;
}

.qa-project-info {
  background: #222;
}

.qa-project-comment {
  background: none;
  border: none;
}

[class*=ProjectInfo-infoBlocks-], [class*=ProjectTags-projectTags-] {
  background: #1C1C1C;
  color: #aaa;
  border-color: #111;
}

.qa-user-link {
  color: #81c4ff;
}

[class*=ProjectInfo-projectName-] {
  color: #eee;
}


[class*=ProjectTags-tag-] {
  border-color: #003cad;
  background: #0a2d61;
  color: #75b4ea;
}

[class*=Tools-iconWrapper-], [class*=Project-sidebarCollectionTrigger] {
  background: none;
  border: 1px solid #333;
}

[class*=Project-icon-] {
  fill: #CCC !important;
}

[class^=Tooltip-container-], .rf-mini-profile {
  background: #000;
  color: #888;
}

[class*=Tooltip-triangle-] {
  border-top-color: #000 !important;
}

[class*=Tools-toolLink-] {
  color: #3d7ece;
}

.rf-mini-profile__user-name {
  color: #4fadff;
}

.rf-mini-profile__user-location {
  color: #1b91f9;
}

[class*=Project-fullPageOwner-] .rf-button__label {
  color: #2a9bff !important;
}
}

@-moz-document regexp("https?://(www\\.)?behance.net/?(|activity/?|gallery/\\d+.*|[^/]+?(|/stats|/appreciated|/followers|/following|/resume|/team_members|/wip|/members))") {
html,
body,
#top-panel,
#site-container,
#site-content {
    background: #131416;
}
}

@-moz-document regexp("https?://(www\\.)?behance.net(/?|/activity/?)") {
.popup.menu {
    box-shadow: 2px 2px 20px black;
}
.popup.mini-profile::before {
    background: #666;
}
.popup.menu .popup-inner-wrap {
    background: #666;
}
.mini-profile-wrap .user-name {
    color: white;
}
.mini-profile-wrap .location-link {
    color: #ccc;
}
.mini-profile-wrap .gallery-projects-wrap {
    width: 350px;
}
.mini-profile-wrap .gallery-projects-wrap .project-cover-wrap,
.mini-profile-wrap .gallery-projects-wrap {
    background: transparent;
    border: none;
}
.mini-profile-wrap .gallery-projects-wrap .project-cover-wrap {
    margin-right: 7px;
    box-shadow: 1px 1px 3px #222;
    width: 105px;
}
.mini-profile-wrap .cover-stat {
    color: #aaa;
}
.mini-profile-wrap .cover-stat:before {
    color: #999;
}

}

@-moz-document regexp("https?://(www\\.)?behance.net/connections/find.*") {
html,
body,
#content-wrapper {
    background: #131416;
}
#content-container {
    width: 100%;
    padding-left: 4ex;
    padding-right: 4ex;
}
#content {
    width: calc(100% - 35ex);
    padding-right: 25px;
    border-color: #444;
}
#filters {
    width: 35ex;
}
.list-links a:active,
.list-links a:hover {
    background: none;
    color: white;
}
.list-links a {
    color: #999;
}
body,
.default-color {
    color: #666;
}
.users-gallery-container .list-item:first-child,
.user-row:first-child,
.user-row {
    background: #222;
    border: none;
    margin: 10px;
    padding: 12px 15px 12px 20px;
    min-width: 740px;
    float: left;
}
.user-row .location-link,
.user-row .field-link {
    color: #aaa;
}
.user-row .user-stats {
    border-color: #444;
    color: #777;
}
.user-row .beicons-pre:before {
    color: #666;
}
.gallery-projects-wrap {
    background: transparent;
    border: none;
}
.gallery-projects-wrap .project-cover-wrap {
    background: transparent;
    border-left: 6px solid transparent;
}
}

@-moz-document regexp("https?://(www\\.)?behance.net/galler.*") {
#site-content,
body,
html {
    background: #202224;
    color: #aaa;
}
.header-bar {
    background-color: #111;
    box-shadow: inset 0 -20px 20px -20px black;
    border-bottom: 2px solid #333;
}
.header-bar .nav-item-link {
    color: #68A2FF;
    font-weight: bold;
}
.header-bar .nav-item.active .nav-item-link {
    border-bottom: 2px solid white;
    color: white;
}
.list-item {
    background: #2e2f30;
    border-color: #181818;
}
a {
    color: white;
}
.gallery-controls {
    border-color: #111;
}
.form-button-light-and-blue {
    background: linear-gradient(#666, #444);
    border-color: #444;
    color: white;
    text-shadow: 0px 1px 0px #333;
}
.form-button-light-and-blue:hover {
    background: linear-gradient(#777, #555);
    border-color: #666;
}
.rf-curated-galleries-cover {
    background: #333;
    border-color: #444
}
.rf-curated-galleries-cover__label {
    color: #eee;
}
.rf-curated-galleries-cover__description {
    color: #999;
}
.curated-galleries__view-more-icon {
    background-color: #8a7146;
}
.curated-galleries__header-strip {
    background: linear-gradient(to bottom, #30343a 50%, #181a1e 100%);
    box-shadow: 0 0 20px #111;
}
#site-content header h1 {
    color: white
