css:
@-moz-document domain("eve-central.com") {
    body {
        font-family: "source-sans-pro",sans-serif;
        margin: 0px;
        background: none repeat scroll 0% 0% #222222;
    }
    #bodyText form button {
        background-color: #468;
        border-radius: 2px;
    }
    #bodyText form a, #bodyText form a:visited {
        border-radius: 2px;
        background-color: #654;
        color: #cba;
    }
    .search_suggestions a, .search_suggestions a:visited {
        background-color: #345 !important;
        color: #ccc !important;
    }
    .topbar form button, button {
        border: 1px solid #567;
        padding: 0px;
        border-radius: 2px;
        background-color: #456;
    }
    input, #bodyText form input[type="text"]  {
        border: 1px solid #345;
        padding: 3px;
        border-radius: 2px;
        background-color: #234;
    }
    a, p {
        color: #abc;
    }
    a:hover {
        color: #def;
    }
    table {
        border-collapse:collapse;
        border-spacing: 2px;
    }

    #sell_orders td {
        padding: 0px;
        line-height:  15px;
        font-size: 10px;
        white-space: nowrap;
        color: #b64;
    }
    .highlightSell td {
        color: #b83 !important;
    }
    #buy_orders td {
        padding: 0px;
        line-height:  15px;
        font-size: 10px;
        white-space: nowrap;
        color: #482;
    }
    .highlightBuy td {
        color: #2a7 !important;
    }
    h3, h2, h1, th {
        background-color: #333;
        color: #eee;
    }
    .quicklookSorted {
        background-color: #444;
        border: 0px solid #654;
    }
    .highlightSell {
        background-color: #382828;
        border-top: #333;
        border-bottom: #333;
        color: #aaa;
    }
    .highlightBuy {
        background-color: #283828;
        border-top: #333;
        border-bottom: #333;
        color: #aaa;
    }
    div #sell_orders {
        width: 49%;
        float: left;
    }
    div #buy_orders {
        width: 49%;
        float: right;
    }
    #sell_orders th { 
        background-color: #422;
        padding: 5px;
    }
    #buy_orders th {
        background-color: #242;
        padding: 5px;
    }
    #statsHolder {
        text-align: center;
    }
    #footer {
        clear: both;
    }
    #sell_orders {
        border-left: 3px solid #600;
        padding-left: 10px;
    }
    #buy_orders {
        border-left: 3px solid #060;
        padding-left: 10px;
    }
    #bodyText .quicklookHeader form {
        line-height: 25px;
        background-color: #333;
        margin-left: 0px;
    }
    #bodyText form {
        background-color: #233;
        padding: 8px;
        color: #ccc;
        line-height: 30px;
        border-radius: 5px;
    }
    .stat-bar {
        color: #ccc;
        padding: 15px;
        background-color:  #222;
        border-radius: 3px;
        margin-top: 20px;
        margin-bottom: 30px;
    }
    .stat-bar .sepdiv {
        padding: 10px;
        color: #999;
        background-color: #222;
    }
    .picked {
        color: #579;
    }
    .tradeTool {
        width: 100%;
        background-color: #233;
        padding: 23px;
        border-radius: 5px;
        border: 1px solid #344;
        margin-bottom: 20px;
    }
    .doctable th {
        background-color: #333;
    }
    .docsection {
        margin-right: 10em;
        margin-left: -2ex;
        font-family: "freight-sans-pro",sans-serif;
        font-size: 150%;
        font-weight: 700;
        background-color: #333;
    }
}
