css:
@-moz-document domain("taringa.net"){

div.container-post {
    background-color: #ebe8eb;
    border-style: dashed;
    border-color: #c4c4c4;
    border-left-width: 2px;
    border-right-width: 2px;
}

div.v6 {
    background-image: url("https://cdn.tutsplus.com/webdesign/uploads/legacy/108_BackgroundPatterns3/d6.jpg");
}

div.v6-container.container-home {
    background-color: #ebe8eb;
}

main.v6-content {
    background-color: #ebe8eb;
}

#page {
    background-color: #ebe8eb;
    background-image: url("https://cdn.tutsplus.com/webdesign/uploads/legacy/108_BackgroundPatterns3/d6.jpg");
}

#footer-links {
    background-color: #ebe8eb;
}

div.comment.clearfix {
    border-style: dashed;
    border-color: #919191;
    border-top-width: 2px;
    background-color: #ffffff;
    width: 847px;
}

div.comment.clearfix.comment-highlight-top {
    background-color: #c7ffd9;
    border-style: none;
}

img.lazy.avatar-48 {
    border-style: outset;
    border-color: #c2c2c2;
    border-width: 3px;
    padding: 0;
}

div.meta-data {
    background-color: #ebe8eb;
}

a.hovercard {
    color: #1ba9f5;
}

#footer {
    border-style: none;
}

div.banner-footer.container {
    border-style: none;
}

p.hosted {
    background-image: url("https://cdn.tutsplus.com/webdesign/uploads/legacy/108_BackgroundPatterns3/d6.jpg");
}

a.subir {
    display: none;
}

.v6 body.section-post #comments .comment-actions, .v6 body.section-post .comments-box .comment-actions, .v6 body.section-post #comment-linked .comment-actions´ {
    display: inline   
;
}

h1.post-title {
    border-color: #c4c4c4;
    border-bottom-width: 2px;
    border-style: dashed;
    width: 850px;
    border-top-width: 2px;
    border-left-width: 2px;
    letter-spacing: 0;
    font-family: Roboto;
}

div.row.row--puntos {
    border-style: dashed;
    border-color: #c4c4c4;
    border-top-width: 2px;
    border-right-width: 2px;
    border-bottom-width: 2px;
}

div.row.row--stats {
    border-bottom-width: 2px;
    border-right-width: 2px;
    border-style: dashed;
}

div.total span {
    color: #ffffff;
    font-weight: bold;
}

div.container-post footer {
    border-style: dashed;
    border-top-width: 2px;
    width: 850px;
}

div.user-tip {
    width: 350px;
}

div.col-1-3 {
    width: 50px;
}

div.title.clearfix h3 {
    width: 835px;
    border-style: dashed;
    border-color: #c4c4c4;
    border-top-width: 2px;
    border-bottom-width: 2px;
}

.v6 body.section-post #comments .comment-replies .comment, .v6 body.section-post .comments-box .comment-replies .comment, .v6 body.section-post #comment-linked .comment-replies .comment {
    width: 800px;
}

div.more-replies a {
    width: 847px;
}
}
