@-moz-document domain(www.youtube.com) {

div#watch7-content {
  margin-top: 0px;
}


div#yReplayBox {
  border-radius: 5px 0 0 0!important;
  margin: 10px 0px 0px -1px!important;
}

a:link {color:#008FFF !important;}      
a:visited {color:#C0B5B5 !important;}  
a:hover {color:#FF7A00 !important;}  
a:active {color:#5F5F5F !important;} 


::-webkit-scrollbar {height: 12px!important;width: 12px!important;background: -webkit-linear-gradient(40deg , #000000 , #1e1e1e , #000000 100%)!important;}
::-webkit-scrollbar-thumb {
background-color: #009223!important;
border-radius: 0px!important;
}


/*Youtube HTML 5 Video Player*/
.html5-player-chrome {background-image: linear-gradient(to right bottom, rgb(0, 0, 0) 37%, rgb(17, 26, 131) 61%, rgb(2, 0, 41) 78%)!important;}
.ytp-play-progress {background: no-repeat url(http://files.enjin.com/106810/websitestyles/youtube/blue/loadbar4.gif) !important;background-size: 100% 100% !important;box-shadow: 0 0 12px #000000 !important;}
.progress-bar-progress,.ytp-load-progress {background-image: linear-gradient(to right bottom, rgb(0, 0, 0) 0%, rgb(54, 54, 54) 16%, rgb(0, 0, 0) 35%, rgb(36, 36, 36) 48%, rgb(0, 0, 0) 64%, rgb(0, 4, 94) 83%, rgb(0, 0, 0) 100%)!important;}
.html5-scrubber-button {border: 5px solid #0700e2!important;background: #000000!important;}
.ytp-unloaded-progress{background: transparent !important;}
.site-center-aligned #player #player-api{box-shadow: 0 0 30px #000000 !important;}
#player-mole-container{background-color: 0!important;}
.html5-video-player{outline: none!important;}
#player{top: 0;}
.watch-medium,.watch-large {width:100%!important;}
.watch-medium .player-width {width:100%!important; height: 85vh!important;  max-height:85vh!important; height:calc(100vh - 92px)!important;}
.watch-large .player-height {height: calc(100vh - 50px) !important; width: calc(100% - 0px) !important;}
.video-stream {width:100%!important; height: 100%!important;}
.html5-main-video{left: 0!important;}
.html5-video-container{background: url("http://files.enjin.com/106810/websitestyles/youtube/back.gif") !important;}
.ytp-progress-list,.html5-progress-bar {width: 100%!important;}
#player{width: 100% !important;}
