@namespace url(http://www.w3.org/1999/xhtml);

/*

Only this is needed in Stylish:

@import url(https://fonts.googleapis.com/css?family=Merriweather+Sans:400,700italic,400italic,700);

instead of the individual @font-face declarations...

*/

@font-face {
  font-family: 'Merriweather Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Merriweather Sans Regular'), local('MerriweatherSans-Regular'), url(https://fonts.gstatic.com/s/merriweathersans/v5/AKu1CjQ4qnV8MUltkAX3sMZ4HySTEWshgORbPp2tk8k.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
@font-face {
  font-family: 'Merriweather Sans';
  font-style: normal;
  font-weight: 700;
  src: local('Merriweather Sans Bold'), local('MerriweatherSans-Bold'), url(https://fonts.gstatic.com/s/merriweathersans/v5/6LmGj5dOJopQKEkt88GowbvMLSVzqOGkoxdgTSsKqWM.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
@font-face {
  font-family: 'Merriweather Sans';
  font-style: italic;
  font-weight: 400;
  src: local('Merriweather Sans Italic'), local('MerriweatherSans-Italic'), url(https://fonts.gstatic.com/s/merriweathersans/v5/3Mz4hOHzs2npRMG3B1ascefU2ka509O1odprhyXBUMU.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
@font-face {
  font-family: 'Merriweather Sans';
  font-style: italic;
  font-weight: 700;
  src: local('Merriweather Sans Bold Italic'), local('MerriweatherSans-BoldItalic'), url(https://fonts.gstatic.com/s/merriweathersans/v5/nAqt4hiqwq3tzCecpgPmVafo0sDQ75c9ExQ-26lwTdA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}


@-moz-document domain("userstyles.org") {

  /* Set main text colour, size and spacing */
  body {
    -webkit-font-smoothing: antialiased;
    text-rendering: geometricPrecision;
    word-spacing: normal !important;
    letter-spacing: normal !important;

    color: #222 !important;
    font-size: 20px;
    line-height: 1.5em !important;
  }
    
  /* Set the font everywhere */
  body, 
  font, 
  input, 
  td, 
  th, 
  a {
    font-family: 'Merriweather Sans', sans-serif !important;
  }

  /* Make links a nicer colour, no !important so sites can style their own links */
  a {
    color: #05f;
    text-decoration: none;
  }
  a:visited {
    color: #c60 !important;
  }
  a:hover {
    text-decoration: underline;
  }

  /* Add some spacing to the text */
  td, 
  th, 
  input[type=text],
  input[type=url] {
    padding: 4px 8px !important;
  }

  /* Shrink additional info text size! */
  #additional-info {
    font-size: 16px;
  }

}
