css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fritz.box") {
	
	/*!
	 * FRITZ!Box â€“ alternative theme
	 * https://github.com/kelunik/fritzbox-theme
	 * 
	 * (c) 2014 Niklas Keller <me@kelunik.com>
	 */
	
	body {
		background: #fefefe;
	}
	
	#main_page_all {
		width: 100%;
		padding: 0;
		font-family: 'Lato', sans-serif;
		color: #333;
	}
	
	/* header */
	
	#intro_bar_box {
		height: 48px;
	}
	
	#intro_bar {
		margin-top: 0;
		background: #222;
		border-radius: 0;
		border: 0;
		text-align: center;
	}
	
	#intro_bar_left {
		background-size: contain;
		width: 50px;
		height: 50px;
		top: 5px;
		margin: 0;
		margin-left: 10px;
		height: 50px;
		display: inline-block;
		float: none;
		position: relative;
		border: 0;
		margin-right: 10px;
	}
	
	#intro_bar_left img {
		display: none;
	}
	#intro_boxinfo {
		margin-left: 10px;
		margin-right: 10px;
	}
	
	#intro_boxinfo,
	#intro_boxinfo p {
		line-height: 50px;
		font-size: 20px;
		padding: 0;
		margin: 0;
		width: auto;
		display: inline-block;
		float: none;
		position: relative;
	}
	
	#intro_bar_middle {
		display: none;
	}
	
	/* header sub menu */
	
	#sub_menu_head {
		background: #333;
		box-shadow: 0 -5px 5px -5px rgba(0,0,0,.5) inset;
		border-top: 1px solid #111;
		color: transparent;
		font-size: 0;
		line-height: 26px;
		margin-right: 0;
		margin-bottom: 0;
	}
	
	#sub_menu_head > div {
		line-height: 26px;
	}
	
	#sub_menu_head > div > span:hover,
	#sub_menu_head > div > a:hover {
		color: #08e;
	}
	
	#sub_menu_head > div > span,
	span#sso_dropdown.hidelist,
	#sub_menu_head > div > a {
		background-image: none;
		height: auto;
		padding: 3px 8px 0 8px;
		text-align: center;
		border: 0;
		margin: 0 8px;
		margin-top: -1px;
		line-height: 26px;
		font-size: 13px;
		color: #eee;
		font-weight: bold;
		display: inline-block;
		min-width: 0;
		height: 26px;
		vertical-align: middle;
		border-bottom: 1px solid transparent;
	}
	
	#sub_menu_head a > span {
		display: inline;
		margin: 0;
	}
	
	#sub_menu_head a.iconHelp {
		width: 20px;
		min-width: 20px;
		margin-right: 20px;
	}
	
	#sub_menu_head img {
		vertical-align: middle;
		height: 16px;
		margin-top: -14px;
	}
	
	.sm_link_selected span {
		border-bottom: 0;
		color: #fff;
	}
	
	.sm_link_selected {
		background: #222;
		border: 1px solid #111 !important;
		border-top: 0 !important;
	}
	
	/* main */
	
	#menu_content_box {
		padding: 15px;
		text-align: center;
	}
	
	/* sidebar */
	
	#menucontent,
	.main_menu_bubble {
		background-color: #fff;
		border: 1px solid rgba(0,0,0,.2);
		border-radius: 0;
		padding: 0;
	}
	
	#menucontent ul > li,
	.main_menu_bubble ul > li {
		padding: 0;
		border-bottom: 1px solid rgba(0,0,0,.1);
	}
	
	#menucontent ul > li:last-of-type {
		border-bottom: 0;
	}
	
	#menucontent ul > li.selected,
	.main_menu_bubble ul > li.selected {
		background-image: none;
		border-radius: 0;
		position: relative;
	}
	
	#menucontent ul > li.selected > a,
	#menucontent ul > li.selected > a:hover {
		color: #fff !important;
		background: rgba(0,100,200,.85);
		border: 1px solid rgba(0,0,0,.1);
		margin: -2px -2px -1px -2px;
	}
	
	#menucontent ul ul > li.selected > a {
		color: #fff !important;
		background: rgba(0,80,160,.85);
		border: 1px solid rgba(0,0,0,.1);
		margin: -2px -2px -1px -2px;
	}
	
	#menucontent ul {
		background: #fafafa !important;
	}
	
	#menucontent ul ul {
		margin-top: 1px !important;
		background: #eaeaea !important;
		box-shadow: 0 -5px 5px -5px rgba(0,0,0,.2) inset, 0 5px 5px -5px rgba(0,0,0,.2) inset;
	}
	
	#menucontent ul > li > a,
	.main_menu_bubble ul > li > a,
	.main_menu_bubble ul > li.explain {
		padding: 5px 12px;
		display: block;
	}
	
	.main_menu_bubble > ul > li.explain {
		border-bottom: 0;
	}
	
	#menucontent ul > li > a:hover,
	.main_menu_bubble > ul > li > a:hover {
		background: rgba(0,0,0,.02);
		text-decoration: none;
	}
	
	#menucontent li.submenu a {
		color: #333 !important;
		font-weight: bold;
	}
	
	/* main content */
	
	#page_content_menu_box {
		margin-left: 15px;
	}
	
	.blue_bar_back {
		background: rgba(0,100,200,.85);
		color: #fff;
		border: 1px solid rgba(0,0,0,.1);
		border-radius: 0;
	}
	
	h2 {
		font-size: 14px;
	}
	
	.page_content {
		border: 1px solid rgba(0,0,0,.1);
		border-top: 0;
		border-radius: 0;
		background: #f5f5f5;
	}
	
	ul.tabs li a {
		padding: 5px 15px 3px 15px;
		background: #fafafa;
		border: 1px solid rgba(0,0,0,.2);
	}
	
	ul.tabs li a:hover {
		background: #efefef;
		text-decoration: none;
	}
	
	ul.tabs li.active a {
		background: rgba(0,100,200,.85);
		color: #fff;
	}
	
	/* footer */
	
	#foot_menu_box {
		background: rgba(0,0,0,.1);
		border-top: 1px solid rgba(0,0,0,.1);
		border-bottom: 1px solid rgba(0,0,0,.1);
		padding: 8px;
		color: #aaa;
		margin: 0;
	}
	
	#foot_menu_box a {
		color: #666;
		font-weight: bold;
	}
	
	#foot_menu_box a:hover {
		color: #444;
		text-decoration: none;
	}
	
}
