css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("chrome://cryptocat/content/data/index.html"), url("chrome://cryptocat/content/data/index.html#") {
  
  .fileView{
    border: 1px solid rgb(64, 64, 88) !important;
  }
  
  #dialogBox .authInfo,
  div#bubble #info #introParagraph,
  .line1,
  .line2,
  #userInputSend{
    color: #ccc !important;
    text-shadow: unset !important;
  }
  
  .fileProgressBar,
  #fileSelectButton,
  #dialogBox .bjqs-markers li.active-marker a,
  #dialogBox .authInfo,
  #dialogBox .title,
  #dialogBox #dialogBoxClose,
  #fill,
  #userInputSend,
  #facebookConnect,
  #utip,
  .conversationName,
  #bubble{
    background-color: #202030 !important;
  }
  
  .fileView,
  .fileProgressBarFill,
  #fileInfoField,
  #dialogBox .bjqs-markers li a,
  #dialogBox .bjqs-caption,
  #dialogBox #displayInfo h2,
  #dialogBox #authQuestion,
  #dialogBox #authAnswer,
  #dialogBox #authSubmit,
  .missingRecipients,
  #progressBar,
  #dialogBox,
  #interestingFact,
  #loginTabs span,
  #conversationName,
  #nickname,
  #loginSubmit,
  #loginInfo,
  #facebookLogin,
  body,
  .buddy,
  .userLeave,
  .userJoin,
  .line3{
    color: #ccc !important;
    background-color: #303040 !important;
  }
  
  div#customServerDialog input#customName{
    background: scroll 0% 0% rgba(255, 255, 255, 0.08) !important;
  }
  
  .sender{
    background: none !important;
    font-weight: bold !important;
  }
  
  .buddyMenu:hover{
    background-color: #303040 !important;
  }
  
  .fileProgressBar{
    border-color: #303040 !important;
  }
  #progressBar{
    border-color: #202030 !important;
  }
  
  #dialogBox,
  #login #loginSubmit,
  #info{
    border-color: #303040 !important;
  }
  
  #dialogBox{
    border: none !important;
  }
  
  *{
    border-radius: 0px !important;
    box-shadow: none !important;
  }
  
  #conversationWrapper{
    margin: 0px !important;
    margin-bottom: -20px !important;
    padding: 0px !important;
    height: calc(83% + 2px) !important;
  }
  
  #utip:after,
  #utip:before,
  .line1:after,
  .line1:before,
  .line2:after,
  .line2:before,
  .line3:after,
  .line3:before{
    display: none !important;
  }
  
  
  .userLeave{
    padding-top: 2px !important;
    padding-bottom: 1px !important;
  }
  .missingRecipients{
    padding-top: 2px !important;
    padding-bottom: 2px !important;
  }
  .userJoin{
    padding-top: 1px !important;
    padding-bottom: 1px !important;
  }
  
  .missingRecipients,
  .userLeave,
  .userJoin,
  .line1,
  .line2,
  .line3{
    font-family: "Open Sans Bold",sans-serif !important;
    font-size: 15px !important;
    font-size: 1em !important;
    border: 0px !important;
    margin: 0px !important;
    line-height: 1.4em !important;
    top: 0px !important;
    opacity: 1 !important;
  }
  .userLeave,
  .userJoin{
    height: 20px !important;
  }
  
  .userLeave .timestamp,
  .userJoin .timestamp{
    height: 20px !important;
    padding: 3px !important;
    line-height: 20px !important;
  }
  .userLeave .timestamp{
    padding-top: 4px !important;
  }
  
  .buddyMenu{
    margin-right: -3px !important;
  }
  
  .buddy,
  .sender{
    font-size: 1em !important;
    width: auto !important;
    min-width: 0px !important;
    margin-right: 3px !important;
  }
  .sender{
    display: inline-flex !important;
  }
  .buddy{
    margin-right: 16px !important;
  }
  
  .sender:after{
    content: ":";
    display: inline !important;
  }
  
  .sender .nickname{
    width: auto !important;
  }
  
  .sender .authStatus{
    display: none !important;
  }
  
  #userInputText{
    font-family: "Open Sans Bold",sans-serif !important;
    font-size: 15px !important;
    font-size: 1em !important;
  }
  
  div#footer #languages li{
    width: 63px !important;
  }
  #userInputSend{
    height: 50px !important;
    width: 110px !important;
    margin: 0px !important;
  }
  #userInputText,
  #userInputSend{
    margin-top: 4px !important;
  }
  
  #loginSubmit{
    padding-top: 1px !important;
    height: 40px !important;
  }
  
  #authSubmit{
    height: 60px !important;
  }
  
  .fileProgressBar{
    margin-top: 2px !important;
    position: absolute;
  }
  
  .fileView{
    padding: 0px 5px !important;
    background-image: none !important;
  }
  
  .emoticon{
    margin: 0px !important;
    filter: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg'><filter id='invert'><feColorMatrix in='SourceGraphic' type='matrix' values='-1 0 0 0 0.8 0 -1 0 0 0.8 0 0 -1 0 0.8 0 0 0 0.8 0'/></filter></svg>#invert");
  }
}
