css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.realgm.com") {

/* Removes main header */
#header_wrap { display:none;}

/* remoes moderator bar and possibly other things */
p {display:none;} 

/* below lines bring back notifications removed by the above line */
.notification_list ul li p {display:block;}
.notification_list p.notification-reference, .notification_list p.notification-location, .notification_list li a p.notification-reason { display:block;}

/* brings back author post/times that were hidden by the above line */
.author {display:block}

/*  These remove wiretaps */
div.widget.archives.basketball {display:none;}
div.widget.archives.hockey {display:none;}
div.widget.archives.football {display:none;}
div.widget.archives.soccer {display:none;}
div.widget.archives.baseball {display:none;}

/* these remove announcments */
.global_unread {display:none;}
.global_unread_mine {display:none;}
.global_unread_locked {display:none;}
.global_unread_locked_mine {display:none;}
.global_read {display:none;}
.global_read_mine {display:none;}
.global_read_locked {display:none;}
.global_read_locked_mine {display:none;}
.announce_unread {display:none;}
.announce_unread_mine {display:none;}
.announce_unread_locked {display:none;}
.announce_unread_locked_mine {display:none;}
.announce_read {display:none;}
.announce_read_mine {display:none;}	
.announce_read_locked {display:none;}					
.announce_read_locked_mine {display:none;}

/* removes regional team icons */			
div.team-icons {display:none;}


/* removes bottom stuff */
div.footer-logo-container {display: none;}
div.about-us-container {display: none;}
div.link-lists-container {display: none;}
div.small-column-left {display: none;}
.display-options {display: none;}
div.stat-block {display: none;}
div.announcement {display: none;}


}
