/*======================================
  DIY Projects - How To liquid compact 2014/12
  @-moz-document url-prefix("http://sadik.net/DIYProjects/")
  ======================================*/
@-moz-document url-prefix("http://sadik.net/DIYProjects/") {
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 line-height: 1.4em !important;
 }
body {font-size: medium !important;}
#wrapperpub, #wrapper
 {width: auto !important; margin: 0 !important; padding: 0 !important;}

/*--- header ---*/
#header {width: auto !important; height: auto !important;}
#header, #header>div {margin: 0 !important; padding: 0 !important;}
#header>div {border-bottom: 2pt solid brown;}
h1.blogtitle {font-size: 3em;}

/*--- footer ---*/


/*--- main contents ---*/
#access {display: none !important;}
#container, #primary {position: relative !important;}
#primary {float: right !important; clear: none !important;}
#container {
 width: 100% !important; margin: 0 -200pt 0 0 !important; padding: 0 !important;
 }
#container>* {
 width: auto !important;
 margin: 0 200pt 0 0 !important; padding: 0 0.5em 0 0.5em !important;
 }
#primary {
 border-left: 1px solid gray;
 width: 200pt !important; margin: 0 !important; padding: 0 0 0 0.5em !important;
 }
#primary *{max-width: 200pt !important;}
#primary.sidebar {display: block !important;}

}
