css:
@-moz-document domain("tweetdeck.twitter.com") {
/* BorderDeck amoethyst is a theme for TweetDeck created by amoethyst

To close the picture preview just move your mouse to the top and click
when a pointer appears.

To tweet use ctrl+enter (don't wait for a "Tweet" button to appear)
*/

	/* Font */

/*[[extfont]]*/

html {
  color: #ddd;
  font-family: /*[[font]]*/, sans-serif !important;
  font-weight: 400;
}
.os-windows, html .system-font-stack {
  font-family: /*[[font]]*/, sans-serif !important;
  font-weight: 400;
}
html.dark {
  color: #ddd;
  font-family: /*[[font]]*/, sans-serif !important;
  font-weight: 400;
}
.tweet-detail .tweet-translation-text {
  font-family: /*[[font]]*/, sans-serif !important;
}
.txt-mute {
  color: /*[[color1]]*/ !important;
  opacity: 0.9 !important;
}

    /* scrollbar */

::-webkit-scrollbar-thumb:vertical {
  border-radius: 0 !important;
  box-shadow: inset 8px 0 /*[[color1]]*/ !important;
  transition: 0.5s !important;
}
::-webkit-scrollbar-thumb:horizontal {
  border-radius: 0 !important;
  box-shadow: inset 0 8px /*[[color1]]*/ !important;
  transition: 0.5s !important;
}
::-webkit-scrollbar-thumb:vertical:hover {
  box-shadow: inset 8px 0 /*[[color2]]*/ !important;
  transition: 0.5s
}
::-webkit-scrollbar-thumb:horizontal:hover {
  box-shadow: inset 0 8px /*[[color2]]*/ !important;
  transition: 0.5s !important;
}
::-webkit-scrollbar-track {
  border: 0 !important;
}
::-webkit-scrollbar:vertical {
  width: 8px !important;
}
::-webkit-scrollbar:horizontal {
  height: 8px !important;
}
.scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track {
  border: 0;
}

    /* Selection color */

::-moz-selection { color: #fff; background-color: /*[[color1]]*/;}
::selection { color: #fff; background-color: /*[[color1]]*/;}

    /*  Links */

a {
  color: /*[[color1]]*/ !important;
  text-decoration: none !important;
  transition: 250ms;
}
a:hover, a:active {
  color: /*[[color2]]*/ !important;
  outline: none !important;
  transition: 250ms !important;
}
a:focus {
  color: /*[[color1]]*/;
  transition: 250ms !important;
  outline: none !important;
}
.link-complex .link-complex-target {
  text-decoration: none !important;
  color: inherit;
}
.prf-header .prf-siteurl, .prf-header .prf-bio a, .prf-header
.pretty-link {
  color: #fff !important;
}
.prf-header .prf-siteurl:hover, .prf-header .prf-bio a:hover,
.prf-header .pretty-link:hover {
  text-decoration: underline;
}

    /* Buttons */

button, .Button.Button--primary, button.Button--primary {
  background-color: /*[[bgcolor]]*/ !important;
  border: none !important;
  box-shadow: none !important;
  color: /*[[color1]]*/ !important;
  transition: 250ms !important;
  font-family: /*[[font]]*/, sans-serif;
}
button:hover:not([disabled]), .Button.Button--primary:hover, button.Button--primary:hover {
  color: /*[[color2]]*/ !important;
}

    /* App header (aka "that thing on the left") */

.app-header, .column-nav-item, .app-navigator, .app-title {
  background-color: /*[[bgcolor]]*/ !important;
}
.app-header {
  border-right: 1px solid /*[[color1]]*/;
}
.app-navigator {
  margin-bottom: 0 !important;
}
.column-nav-link, .app-nav-link, .app-nav-tab {
  color: #aaa !important;
}
.app-search-input, .app-search-fake, .btn-compose {
  background-color: transparent !important;
  box-shadow: none !important;
  transition: 0.5s !important;
}
.app-search-fake {
  color: #aaa;
  border: none;
}
.search-input-control {
  background-color: /*[[bgcolor]]*/;
}
.js-popover-content {
	background-color: /*[[bgcolor]]*/;
}
.app-search-input:focus, .app-search-input.is-focused {
  border: 1px solid /*[[color4]]*/;
}
.btn-compose {
  color: /*[[color1]]*/;
}
.app-search-fake:hover, .btn-compose:hover {
  color: /*[[color2]]*/;
}
.with-nav-border-t::before {
  border-top: 0 !important;
}
.antiscroll-scrollbar {
  background-color: /*[[color1]]*/ !important;
  border-radius: 0;
}
.column-nav-updates {
  color: /*[[color2]]*/;
}
.numbered-badge {
  border-radius: 100%;
  border: 1px solid /*[[color1]]*/;
  background-color: /*[[bgcolor]]*/;
}
.nav-user-info {
  margin: 0 !important;
}
.nav-user-info .tweet-avatar {
  float: none !important;
  margin: 4px 7px 0 7px !important;
}
.txt-seamful-yellow {
	color: /*[[color1]]*/;
}
.update-available-item {
  background-color: /*[[color1]]*/;
  color: #fff !important;
}
.update-available-item:hover {
  background-color: /*[[color2]]*/;
  color: #fff !important;
}
.update-available-item::before {
	width: 0px;
}
.icon-medium {
	font-size: 24px !important;
}
.txt-twitter-yellow {
  color: /*[[color1]]*/;
  transition: 250ms !important;
}
.txt-twitter-yellow:hover {
  color: /*[[color2]]*/;
}

    /* Twitter logo */

.sprite-logo-badge, .sprite-logo, .tweetdeck-logo {
  background: transparent;
}
.js-logo.app-title > .invisible-in-contracted-header {
  display: none;
}
.app-title:before {
  left: 10px;
  content: "\f014";
  font-family: "tweetdeckregular";
  font-size: 27px;
  color: /*[[color1]]*/;
}

    /* Search Popup */

.popover {
  background-color: /*[[bgcolor]]*/;
  box-shadow: none;
  border-radius: 0;
}
.is-condensed .popover-position-rt {
  left: 47px;
}
input, textarea, select, .uneditable-input {
  border-radius: 0 !important;
  background-color: transparent;
  color: /*[[color2]]*/;
  border: none;
  border-bottom: 2px solid /*[[color1]]*/;
  transition: 250ms !important;
}
input:focus, textarea:focus, select:focus, .uneditable-input:focus {
  border-bottom: 2px solid /*[[color2]]*/;
}
.list-item {
  background-color: transparent !important;
  color: /*[[color1]]*/ !important;
}
.list-placeholder {
  text-shadow: none;
}
.icon-medium {
  font-size: 18px;
}
input:focus, select:focus, textarea:focus, .focus {
  border-color: transparent;
  box-shadow: none;
}
.caret-inner {
  border-bottom: 6px solid /*[[color1]]*/;
}
.facet-type {
  border-bottom: none !important;
  background-color: transparent !important;
}
.accordion-divider-t {
  border-top: none !important;
  border-bottom: none !important;
}
.facet-subtitle {
  color: inherit;
}
.facet-title {
  transition: 250ms !important;
}
.accordion-header {
  padding: 9px 5px 9px 0 !important;
  color: /*[[color1]]*/ !important;
}
.accordion-header:hover {
  color: /*[[color2]]*/ !important;
}
.accordion .icon-arrow-d, .accordion .icon-arrow-u {
  color: /*[[color1]]*/ !important;
}
.accordion .icon-arrow-d:hover, .accordion .icon-arrow-u:hover {
  color: /*[[color2]]*/ !important;
}
.list-item:hover, .list-item:active, .list-item.is-selected {
  color: /*[[color2]]*/ !important;
}
.list-item:hover .txt-mute, .list-item:active .txt-mute,
.list-item.is-selected .txt-mute {
  color: inherit !important;
}
.list-item .list-icon {
  color: inherit !important;
}
.account-summary:hover {
  color: /*[[color2]]*/;
}
.txt-r-text-link {
	color: /*[[color1]]*/;
}
.txt-r-text-link:hover {
	color: /*[[color2]]*/;
}

    /* Columns */

.app-columns-container, .column, .app-content, .column-panel {
  background-color: /*[[bgcolor]]*/ !important;
}
.app-columns {
  padding: 0 !important;
}
.column {
  margin: 0 !important;
  border: 0;
  border-right: 1px solid /*[[color1]]*/ !important;
}
.app-columns > .draggable-dragging {
  border-left: 1px solid /*[[color1]]*/;
}
.needs-scroll-bottom-offset.scroll-styled-h {
  bottom: 0 !important;
}

    /* Columns header */

.column-header, html.dark .column-header {
  background-color: /*[[bgcolor]]*/ !important;
  border-bottom: 1px solid /*[[color1]]*/ !important;
  color: /*[[color1]]*/;
}
.column-header-temp, html.dark .column-header-temp {
  background-color: /*[[bgcolor]]*/ !important;
  border: 0 !important;
  color: /*[[color1]]*/;
}
.column-type-icon, html.dark .column-type-icon {
  display: none;
}
.sprite-drag-vertical, html.dark .sprite-drag-vertical {
  background: transparent;
  height: 100% !important;
  width: 10px !important;
  transition: 250ms !important;
}
.sprite-drag-vertical:hover {
  background: /*[[color1]]*/;
  opacity: 0.5;
}
.column-drag-handle, html.dark .column-drag-handle {
  left: 0;
  top: 0;
  margin: 0;
}
.column-header .column-header-title {
	height: 40px;
    line-height: 1em;
}
.column-number {
  display: none;
}
.column.is-focused {
  box-shadow: 0 0 6px 3px /*[[color1]]*/;
  z-index: 199;
}
.is-new .column-header, .is-new .column-header-temp {
  box-shadow: inset 0 -2px /*[[color1]]*/;
  transition: 0.25s;
}
.more-tweets-glow {
  background: none;
}
.column-message {
  background-color: /*[[bgcolor]]*/;
}
.facet-content, .facet-user {
  color: /*[[color1]]*/;
}
.column-title-edit-box {
  background-color: /*[[bgcolor]]*/ !important;
  color: /*[[color1]]*/ !important;
  border-bottom: none !important;
  border-radius: 0px;
}

    /* Smaller columns header */

.column-header, .column-header-temp, html.dark .column-header, html.dark .column-header-temp {
  height: 40px;
  line-height: 40px;
}
.column-content {
  top: 0 !important;
}
.column-title-back .icon, html.dark .column-title-back .icon {
  top: 20px;
}
.column-title-back {
  color: /*[[color1]]*/;
  transition: 250ms;
}
.column-title-back:hover {
  color: /*[[color2]]*/;
}
.column-title-back .username {
	color: inherit !important;
}

.column-header .column-title, html.dark .column-header .column-title {
  margin-left: 0;
}

    /* Fix for BTD */

.btd-ready .column-title .column-head-title, .btd-ready .column-title
.attribution {
  display: inline;
}

    /* Columns settings */

.column-settings-link, .facet-type.is-expanded {
  background-color: transparent !important;
  border: 0 !important;
}
.column-options, .column-options .button-tray {
  background-color: /*[[bgcolor]]*/ !important;
}
.accordion-divider-t, .facet-type {
  border: 0;
}
.facet-subtitle {
  color: /*[[color1]]*/;
}
.facet-subtitle:hover {
  color: /*[[color2]]*/;
}
.with-column-divider-bottom {
  border-bottom: 1px solid /*[[color1]]*/;
}
.btn-options-tray, .btn-neutral .label {
  transition: 250ms !important;
}
.btn-options-tray:hover, .btn-options-tray:focus, .btn-neutral:hover
.label {
  color: /*[[color2]]*/;
  text-decoration: none !important;
}
select {
  -moz-appearance: none;
  -webkit-appearance: none;
  appearance: none;
  background: /*[[bgcolor]]*/;
  border: 0;
  border-bottom: 2px solid /*[[color1]]*/;
  border-radius: 0;
}
select:hover, select:focus {
	  border-bottom: 2px solid /*[[color2]]*/;
	  cursor: pointer;
}
input:focus, select:focus, textarea:focus, .focus {
  box-shadow: none;
  border-color: /*[[color2]]*/
}
select:-moz-focusring {
  color: transparent;
  text-shadow: 0 0 0 /*[[color1]]*/;
}

.icon-mention-color, .icon-retweet-color, .icon-image-color,
html.dark .icon-mention-color, html.dark .icon-retweet-color, html.dark .icon-image-color {
  color: /*[[color1]]*/;
}

    /* Add column popup */

.column-type-icon {
  color: /*[[color1]]*/;
  transition: 250ms !important;
}
.btn > span {
  transition: 250ms !important;
}
.btn:hover > .column-type-icon {
  color: /*[[color2]]*/;
}
.btn:hover > span {
  color: /*[[color2]]*/;
}
.s-minimal .mdl-header, .lst-launcher .top-row {
  border-bottom-color: /*[[color1]]*/;
}

    /* Tweets */

.stream-item, html.dark .stream-item {
  border-bottom: 0;
  background-color: /*[[bgcolor]]*/;
}
.media-item.media-size-large::after {
  background: none;
}
.media-size-large-height {
  margin-bottom: 4px;
}
.media-preview-container {
  margin-top: 8px !important;
}
.tweet-footer .pull-left > span {
  display:none;
}
.item-box, html.dark .item-box {
  padding-bottom: 4px;
  padding-top: 4px;
}
.scroll-conversation, .tweet-detail-wrapper {
  background-color: /*[[bgcolor]]*/ !important;
}
.is-selected-tweet {
	background-color: /*[[color4]]*/ !important;
}
.tweet-detail-wrapper, .tweet-stats, .tweet-detail-actions,
.detail-view-inline, .card-holder, html.dark .tweet-detail-wrapper,
html.dark .tweet-stats, html.dark .tweet-detail-actions,
html.dark .detail-view-inline, html.dark .card-holder {
  border: 0;
  margin-top: 4px;
}
.tweet-stat {
  color: /*[[color1]]*/;
}
.tweet-stat:hover {
  color: /*[[color2]]*/;
}
.tweet-stat[data-type="repliers"] {
  color: /*[[color1]]*/;
}
.tweet-stat * {
	color: inherit !important;
}
a:hover .stat-word {
	color: inherit;
}
.tweet-stats > li {
	color: /*[[color1]]*/;
}
.tweet-stats > li[data-type="favoriters"] a:hover,
.tweet-stats > li[data-type="retweeters"] a:hover {
	color: /*[[color2]]*/ !important;
}
.bing-logo:before {
  content: "Bing";
}
.bing-logo {
  background: none !important;
  height: 14px;
}
.quoted-tweet {
  border: 0;
  border-left: 2px solid /*[[color1]]*/;
	border-radius: 0;
}
.media-sensitive {
  background-color: /*[[bgcolor]]*/;
  color: #fff;
}
.media-sensitive-content {
  border: 2px solid /*[[color1]]*/;
}
.media-sensitive-title {
  color: #ddd;
}
.in-tweet-divider::before {
  display: none;
}
.in-tweet-divider {
  padding: 0;
}
.tweet-action-item *, .tweet-detail-action-item * {
  color: #aaa;
  transition: 250ms !important;
}
.tweet-action-item *:hover, .tweet-detail-action-item *:hover {
  color: /*[[color2]]*/;
}
.pull-right.icon-retweet {
  color: /*[[color1]]*/ !important;
}
.social-proof-for-tweet-title {
  background-color: /*[[bgcolor]]*/ !important;
  border-bottom: 0 !important;
  color: /*[[color1]]*/ !important;
}
.quoted-tweet {
  color: #ddd;
}
.modal-content {
	background-color: /*[[bgcolor]]*/;
}
.txt-twitter-black {
	color: #ddd;
}
.mdl-header-divider {
	border-bottom-color: /*[[color1]]*/;
}
.thread {
	margin-top: 10px;
	border-radius: 0;
	background-color: /*[[color1]]*/;
	height: 100%;
}
html.dark .thread {
  background-color: /*[[color1]]*/;
}
.show-more-thread, .dot-thread {
  display: none;
}
.conversation-more {
  color: /*[[color1]]*/;
  transition: 250ms !important;
}
.conversation-more:hover {
  color: /*[[color2]]*/;
  text-decoration: none;
}
.btn-neutral-positive {
  color: /*[[color1]]*/;
  transition: 250ms !important;
}
.btn-neutral-positive:hover {
  color: /*[[color2]]*/;
}
.hw-card-container span {
	color: /*[[color1]]*/;
}
.hw-card-container * {
  background-color: /*[[bgcolor]]*/ !important;
  border-color: /*[[color1]]*/ !important;
  border-radius: 0;
  transition: 250ms !important;
}
.hw-card-container:hover span {
  color: /*[[color2]]*/;
}
.hw-card-container:hover * {
  border-color: /*[[color2]]*/ !important;
}

	/* Links to account */

.account-link * {
  color: /*[[color1]]*/;
}
.account-link .link-complex-target, .account-link .username, .account-inline .icon-protected {
  transition: 250ms;
}
.account-link:hover * {
  color: /*[[color2]]*/ !important;
}
.nbfc .account-link {
  color: /*[[color1]]*/ !important;
}
.nbfc .account-link:hover {
  color: /*[[color2]]*/ !important;
}
.drawer .nbfc {
  color: /*[[color1]]*/;
  transition: 250ms !important;
}
.drawer .nbfc:hover {
  color: /*[[color2]]*/;
}

img.emoji {
	box-shadow: none !important;
}

	/* Trends column */

.trend-label {
	color: /*[[color1]]*/;
}
.trend-item .trend-item-stats {
	color: inherit;
}

	/* Rounded Avatars */

.tweet-avatar, .size24, .size72, .prf-img, .accs .acc-avatar {
  border-radius: /*[[rad]]*/;
}
.tweet-avatar {
  margin: 2px !important;
}
.avatar {
  border-radius: /*[[rad]]*/;
  background-color: #fff;
  background-clip: padding-box !important;
}
.compose-account .compose-account-img {
  transition: opacity 250ms !important
}
.is-selected.compose-account .compose-account-img {
  box-shadow: 0 0 0 2px /*[[color1]]*/ !important;
}
.compose-account:focus .compose-account-img {
  box-shadow: none;
}
.list-account img {
  box-shadow: 0 0 0 1px /*[[color1]]*/;
}
.item-img {
  width: 40px;
}

    /* RT fav tweet */

.tweet-dogear {
  background-image: none;
  width: auto;
  height: 100%;
}
.avatar {
  transition: box-shadow 250ms !important;
}
.tweet-header .avatar, .tweet-detail .avatar, .account-summary .avatar, .tweet-avatar {
  box-shadow: 0 0 0 2px /*[[color1]]*/;
  transition: 250ms !important;
}
.tweet-header:hover .avatar, .account-summary .avatar:hover, .tweet-avatar:hover {
  box-shadow: 0 0 0 2px /*[[color2]]*/ !important;
}
.is-favorite .avatar {
  box-shadow: 0 0 0 2px #fa3;
}
.is-retweet.is-favorite .avatar {
  box-shadow: 0 0 0 2px #fff;
}
.is-retweet .avatar {
  box-shadow: 0 0 0 2px #7b5;
}
.avatar.size24 {
  box-shadow: 0 0 0 1px /*[[color1]]*/;
  margin-top: 1px;
  margin-left: 1px;
}
.avatar.size24:hover {
  box-shadow: 0 0 0 1px /*[[color2]]*/;
}
.tweet-header:hover .size24 {
  box-shadow: 0 0 0 1px !important;
}
.is-retweet .tweet-timestamp, .is-favorite .tweet-timestamp {
  margin-right: 0;
}

    /* Compose panel */

.compose {
  background-color: /*[[bgcolor]]*/ !important;
  color: /*[[color1]]*/;
  border-right: 1px solid /*[[color1]]*/;
}
.compose-header {
  border: 0;
}
.compose-header .icon-close {
  display: none;
}
.compose-text-title {
  color: /*[[color1]]*/;
}
.txt-r-white {
  color: inherit !important;
}
.color-twitter-white, .color-twitter-black {
  color: /*[[color1]]*/ !important;
}
.compose-account {
  background-color: transparent;
}
.compose-account-selected {
  background: transparent;
}
.compose-account-selected .icon {
  display: none;
}
.js-compose-message-header {
	display: none;
}
.compose-text-container {
  background-color: /*[[color4]]*/ !important;
  border-radius: 0 !important;
}
.compose-text {
	background-color: transparent !important;
	color: #fff;
	border: 0 !important;
}
.compose-text-container {
	border-top: 2px solid /*[[color1]]*/ !important;
}
.compose .compose-text-container, .compose-text {
  background-color: /*[[color4]]*/ !important;
}
.compose-text-container:focus, .compose-text:focus {
  background-color: /*[[color4]]*/ !important;
}
.compose-text-container:focus {
	border-top-color: /*[[color2]]*/ !important;
}
.js-quote-tweet-holder, .compose-media-bar-holder {
  background-color: /*[[color4]]*/ !important;
}
.js-quote-tweet-holder .nbfc {
	color: inherit !important;
}
.compose-media-bar-holder {
  padding-bottom: 0 !important;
}
.spinner-button-container {
  display: none;
}
.character-count-compose:disabled {
  color: /*[[color1]]*/
}
.btn-on-blue {
  background-color: transparent !important;
  color: /*[[color1]]*/ !important;
  transition: 250ms;
  box-shadow: none !important;
}
.btn-on-blue:hover {
  color: /*[[color2]]*/ !important;
}
.btn-on-blue[disabled], .btn-on-blue.is-disabled {
  color: /*[[color3]]*/ !important;
}
.compose-message-recipient-input-container {
  box-shadow: none !important;
}
.compose-message-recipient {
  border-color: /*[[color1]]*/;
}
.compose-reply-tweet {
  background: /*[[color4]]*/;
  color: #aaa;
}
.compose-reply-tweet .fullname {
	color: /*[[color1]]*/ !important;
}
.replyto-caret {
  display: none;
}
.compose-reply-tweet .account-link:hover .fullname {
  color: /*[[color2]]*/ !important;
}
.compose-remember-state {
  color: /*[[color1]]*/;
}
input:focus {
  box-shadow: none !important;
}
.compose-reply-tweet .avatar {
  margin: 1px;
}
.js-quote-tweet-holder .tweet-avatar {
  display: none !important;
}
.compose-message-recipient-input-container, .compose-message-account {
  border-radius: 0;
}
.js-character-count {
  display: inline !important;
}
.stroke-twitter-light-gray {
  stroke: /*[[color5]]*/;
}
.stroke-twitter-blue {
  stroke: /*[[color1]]*/;
