css:
@-moz-document regexp("(http[s]?://)*www.forocoches.*") {
.alt1-user,
	.alt1-author {
		background: var(--bg03);
		color: var(--tx01);
		border: 1px solid transparent !important;
font-size: 16px;
		line-height: 1.375;
	}

	/*------------------------------------------------------------+
	¦ COLORES                                                     ¦
	+------------------------------------------------------------*/

	/* Lista de colores modificables */
	:root {
		--bg01: #1a1a1a; /* Fondo base */
		--tx01: #d1d1d1; /* Texto base */
		--tx02: #f18181; /* Enlaces base */
		--bg02: #303030; /* Bordes principales rgba(255,255,255,.1)*/
		--bg05: #363636; /* Bordes secundarios, varios */
		--bg04: #1f1f1f; /* Fondo principal 1, "alt2", oscuro */
		--bg03: #282828; /* Fondo principal 2, "alt1", claro */
		--tx03: #6e6e6e; /* Textos secundarios, varios */
		--bg07: #424242; /* Editor de mensajes, spoiler, varios */
		--bg08: #383838; /* Barra de herramientas, separador, varios */
		--tx06: #dfdfdf; /* Texto barra de herramientas, enlaces inicio, varios */
		--tx07: #bfbfbf; /* Enlaces titulo hilo inicio y foro */
		--tx08: #d1d1d1; /* Enlaces titulo hilo inicio y foro hover */
		--bg09: #2e2e2e; /* Alternante Inicio 1, menus, varios */
		--bg06: #333333; /* Alternante Inicio 2, varios */
		--bg10: #1c1c1c; /* Hover menus y pestñas */
		--bg11: #1a1a1a; /* Fondo casillas formularios y opciones */
		--tx04: #ababdb; /* Enlaces principales, resaltar autor del hilo */
		--tx05: #afa131; /* Hover color enlaces principales */
		--rs01: #85e585; /* Resaltar usuario, resalto barra de herramientas */
		--rs04: #242924; /* Resaltar usuario fondo */
		--rs02: #ff6b6b; /* Resaltar autor de hilo */
		--rs03: #2a2222; /* Resaltar autor de hilo fondo */
		--cv01: #85e585; /* Texto verde pagina Inicio */
		--no01: #85e585; /* Fondo notificacion */
		--no02: #282828; /* Texto notificacion */
		--cs01: #d1d1d1; /* Formularios busqueda / texto */
		--cs02: #1a1a1a; /* Formularios busqueda / texto color texto */
		--bt01: #ff6b6b; /* Botones (SIN USO) */
		--bt02: #ff9191; /* Hover botones (SIN USO) */
	}

	/* Color botones foro */
	/*img[src*="/buttons/quote.gif"],
	img[src*="/buttons/multiquote_off.gif"],
	img[src*="/buttons/quickreply.gif"],
	img[src*="/buttons/edit.gif"] {
	filter: invert(100%);
}*/

	/* Colores de imagenes */
	/* Colores iconos editor de mensajes */
	img[src*="forocoches.com/foro/images/editor"],
	img[src*="dbtech/usertag/images"] {
		filter: invert(100%) brightness(110%);
	}
	/* Flecha barra de herramientas */
	img[src*="forocoches.com/foro/images/misc/menu_open.gif"] {
		filter: brightness(100%) opacity(70%);
	}

	/* Bordes formularios busqueda / texto */
	.cfield,
	.bginput,
	input[name="q"] {
		border: 0px solid var(--bg05) !important;		
	}


	/* Colores de texto, reemplazar colores de texto para adaptar a fondo oscuro */
	/*-----------------------------------------------------------*/

	/* Colores seleccionables en el editor de mensajes */
	font[color="Black"] {
		color: var(--tx01);
	}
	font[color="#000000"] {
		color: var(--tx01);
	}
	font[color="#f1f1f1"] {
		color: var(--bg03);
	}
	font[color="White"] {
		color: var(--bg01);
	}
	font[color="Silver"] {
		color: #2c2c2c;
	}
	font[color="Red"] {
		color: #f75252;
	}
	font[color="DarkRed"] {
		color: #b33636;
	}
	font[color="Navy"] {
		color: #5c95e5;
	}
	font[color="Blue"] {
		color: #91baf2;
	}
	font[color="Green"] {
		color: #85e585;
	}
	font[color="DarkGreen"] {
		color: #5cbf5c;
	}
	font[color="Yellow"] {
		color: #f2f26d;
	}
	/* Lista de colores editor de mensajes */
	img[id^="vB_Editor"][style="background-color: Black;"] {
		background: var(--tx01)!important;
	}
	img[id^="vB_Editor"][style="background-color:black"] {
		background: var(--tx01) !important;
	}
	img[id^="vB_Editor"][style="background-color: White;"] {
		background: var(--bg01) !important;
	}
	img[id^="vB_Editor"][style="background-color: Silver;"] {
		background: #2c2c2c !important;
	}
	img[id^="vB_Editor"][style="background-color: Red;"] {
		background: #f75252 !important;
	}
	img[id^="vB_Editor"][style="background-color: DarkRed;"] {
		background: #b33636 !important;
	}
	img[id^="vB_Editor"][style="background-color: Navy;"] {
		background: #5c95e5 !important;
	}
	img[id^="vB_Editor"][style="background-color: Blue;"] {
		background: #91baf2 !important;
	}
	img[id^="vB_Editor"][style="background-color: Green;"] {
		background: #85e585 !important;
	}
	img[id^="vB_Editor"][style="background-color: DarkGreen;"] {
		background: #5cbf5c !important;
	}
	img[id^="vB_Editor"][style="background-color: Yellow;"] {
		background: #f2f26d !important;
	}


	/*------------------------------------------------------------+
	¦ ANIMACIONES                                                 ¦
	+------------------------------------------------------------*/

	/* Animacion notificaciones */
	@keyframes bounceIn {
		10%,
		20%,
		40%,
		60%,
		80%,
		100% {
			animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
		}
		7.5% {
			opacity: 0;
			transform: scale3d(.3, .3, .3);
		}
		15% {
			transform: scale3d(1.1, 1.1, 1.1);
		}
		22.5% {
			transform: scale3d(.9, .9, .9);
		}
		30% {
			opacity: 1;
			transform: scale3d(1.03, 1.03, 1.03);
		}
		37.5% {
			transform: scale3d(.97, .97, .97);
		}
		45% {
			opacity: 1;
			transform: scale3d(1, 1, 1);
		}
		to {
			opacity: 1;
			transform: scale3d(1, 1, 1);
		}
	}

	/* Animacion casilla buscador */
	@keyframes bounceIn2 {
		33%,
		66%,
		100% {
		}
		33% {
			transform: scale3d(.98, .98, .98);
		}
		66% {
			transform: scale3d(1.02, 1.02, 1.02);
		}
		to {
			transform: scale3d(1, 1, 1);
		}
	}

	/* Animacion menus */
	@keyframes fadeIn {
		from {
			opacity: 0;
		}
		to {
			opacity: 1;
		}
	}

	/* Rotacion */
	@keyframes spin {
		from {
			transform:rotate(0deg);
		}
		to {
			transform:rotate(360deg);
		}
	}


	/*------------------------------------------------------------+
	¦ FUENTES DE TEXTO                                            ¦
	+------------------------------------------------------------*/

	/* Importar nuevas fuentes externas */
	@font-face {
		font-family: 'Roboto';
		font-style: normal;
		font-weight: 400;
		src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
	}
	@font-face {
		font-family: 'Roboto';
		font-style: normal;
		font-weight: 700;
		src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v15/d-6IYplOFocCacKzxwXSOFtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
	}
	@font-face {
		font-family: 'Roboto Mono';
		font-style: normal;
		font-weight: 400;
		src: local('Roboto Mono'), local('RobotoMono-Regular'), url(https://fonts.gstatic.com/s/robotomono/v4/hMqPNLsu_dywMa4C_DEpY44P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
		unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
	}

	/* Elegir fuente de texto */
	/* Fuentes extraidas de Foro */
	/* Tahoma */
	.thead,
	legend,
	.vbmenu_control {
		font-family: 'Roboto';
                                            
	}

	/* Verdana */
	body,
	td,
	th,
	p,
	li,
	.tcat,
	.wysiwyg,
	textarea,
	.bginput,
	.bginput option,
	.bginput optgroup,
	.button,
	select,
	option,
	optgroup,
	.smallfont,
	.navbar,
	.vbmenu_option,
	.vbmenu_hilite,
	.cfield,
	.cbutton {
		font-family: 'Roboto';
	}

	/* Fuentes extraidas de Inicio */
	/* Arial */
	.texto,
	.texto1 {
		font-family: 'Roboto';
	}

	/* Verdana */
	FONT,
	.contenido_menu,
	.adcontenido,
	.miniyo,
	.top2,
	TEXTAREA,
	.bginput,
	.field,
	.cfield {
		font-family: 'Roboto';
	}

	/* Geneva */
	.contenido1,
	.contenido,
	.cat,
	.fc2010 {
		font-family: 'Roboto';
	}

	/* Fuente texto code y php */
	div#posts pre.alt2,
	code {
		font-family: 'Roboto Mono';
	}

	/* Elegir tamaño de texto */
	/* Lista de hilos Inicio */
	.texto,
	.texto1 {
		font-size: 10pt; /* pasar de px a pt para coincidir */
	}

	/* Texto mensajes en hilos */
	.alt1 div[id^="post_message"],
	.alt1 div[id^="post_message"] li {
		font-size: 16px;
	}
	/* Texto citas en hilos */
	#HOTWordsTxt td.alt2,
	td.alt2[style="border:1px inset; -moz-border-radius: 8px; border-radius: 8px; margin: 8px; padding: 8px; word-wrap: break-word; display:block; max-height:410px; overflow:auto;"] {
		font-size: 16px;
	}
	/* Texto editor de mensajes */
	.wysiwyg,
	textarea {
		font-size: 16px !important; /* pasar de pt a px, en pt no se muestra correctamente */
		font-family: 'Roboto' !important;
	}

	/* Tamaño seleccionable de fuente */
	font[size="1"] {
		font-size: 12px;
	}
	font[size="2"] {
		font-size: 15px;
	}
	/* Aquí entre medias quedaría el tamaño normal por defecto */
	font[size="3"] {
		font-size: 17px;
	}
	font[size="4"] {
		font-size: 20px;
	}
	font[size="5"] {
		font-size: 24px;
	}
	font[size="6"] {
		font-size: 29px;
	}
	font[size="7"] {
		font-size: 37px;
	}


	/*------------------------------------------------------------+
	¦ QUITAR ELEMENTOS / OCULTAR ELEMENTOS                        ¦
	+------------------------------------------------------------*/

	/* QUITAR ELEMENTOS */
	/* Quitar cabecera logos */
	body>div[align="center"]>center>table[border="0"][cellspacing="0"][style="border-collapse: collapse"][bordercolor="#111111"][width="100%"][id="AutoNumber1"][cellpadding="0"] {
		display: none;
	}

	/* Quitar tablas vacias bajo cabecera para mejora visual */
	TABLE[id="AutoNumber4"][width="100%"][cellspacing="0"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"],
	table#AutoNumber6 {
		display: none;
	}

	/* En inicio */
	/* Quitar cabecera lista de hilos en inicio */
	table[class="cajasnews"] td[bgcolor="#f5f5f5"][align="left"][width="65%"],
	table[class="cajasnews"] td[bgcolor="#f5f5f5"][align="left"][width="65%"]+td[bgcolor="#f5f5f5"][align="right"],
	div[id="1st"] {
		display: none;
	}
	/* Quitar icono bullet titulo de hilo */
	img[src*="forocoches.com/image/bullet_pic2.gif"] {
		width: 0;
		height: 16px;
		padding-right: 16px;
	}
	/* Quitar icono bullet autor de hilo */
	img[src*="forocoches.com/image/bullet_minisquare.gif"] {
		display: none;
	}
	/* Quitar iconos de buscar en ficha de usuario de inicio */
	ttd[class="cat"][bgcolor="#f9f9f9"]>a {
		display: none;
	}

	/* En foro */
	/* Quitar tabla con boton de refrescar en foro. IMPORTANTE, todo el codigo necesario para no afectar otras tablas */
	/* Pues afecta la de publicidad especifica por palabras clave */
	/*table[style="margin-bottom:3px"] + br + table#table3.tborder[width="100%"][cellspacing="1"][cellpadding="5"][border="0"][align="center"] {
	display: none;
}*/

	/* Quitar barra lateral antiguo infocoches dentro de foro */
	td[id="collapseobj_sidebar"][style="padding-right:3px; "][bgcolor="#f9f9f9"][width="170"],
	td[style="background-color:#f1f1f1;"][align="center"][valign="top"][width="15"] {
		display: none !important;
	}
	/* Quita espacio lateral izquierdo tras quitar infocoches */
	td[style="padding-left:3px;"] {
		padding: 0 !important;
	}
	/* Quitar barra lateral antiguo infocoches dentro de foro estando desconectado */
	table.tborder[bgcolor="#555576"],
	td[width="170"][bgcolor="#f9f9f9"],
	td[width="170"][bgcolor="#f9f9f9"] + td[width="3"]
	{
		display: none;
	}

	/* Quita iconos al lado de fecha/hora post */
	img[src*="forocoches.com/foro/images/statusicon/post_old.gif"] {
		display: none;
	}
	img[src*="forocoches.com/foro/images/statusicon/post_new.gif"] {
		display: none;
	}

	/* Barra de herramientas / menus */
	/* Quitar iconos opciones barra de herramientas */
	img[src*="/buttons/sendtofriend.gif"],
	img[src*="/buttons/subscribe.gif"] {
		display: none;
	}
	/* Quitar quitar resaltar autor mensaje barra de herramientas */
	td[style*="border-left: 3px solid rgb(85, 145, 204); border-right: 3px solid rgb(85, 145, 204);"],
	td[style*="border-left: 3px solid rgb(202, 52, 21); border-right: 3px solid rgb(202, 52, 21);"],
	td[style="border-left: 3px solid #ca3415; border-right: 3px solid #ca3415;"],
	td[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"] {
		border: 0 !important;
	}

	/* Quitar titulo de hilo */
	.cmega {
		display: none;
	}
	/* Quitar tabla vacia titulo de hilo  */
	table[id^="fcthread"] td {
		padding: 0px !important;
		font: 0pt none !important;
	}
	/* Fix boton para borrar hilo */
	table[id^="fcthread"] {
		margin: 0px !important;
	}
	/* Color de fondo */
	table[id^="fcthread"] td.thead {
		background: var(--bg01);
	}
	table[id^="fcthread"] input.button {
		margin-bottom: 14px;
	}
	/* Ocultar tabla vacia cabecera titulo de hilo */
	table[bgcolor="#d5e5ee"] {
		margin-bottom: 0px !important;
		background: transparent;
	}

	/* Quita tabla amarilla pie de pagina */
	table.cajasprin>tbody>tr>td[bgcolor="#ffffe0"][align="center"] {
		display: none;
	}

	/* OCULTAR ELEMENTOS */
	/* Publicidad */
	/* NOTA: No quita la publicidad, pero oculta tablas de publicidad al usar bloqueadores de publicidad para no dejar espacios vacios */

	/* Fondo tablas de publicidad */
	table.cajasprin[bgcolor="#f0f5ff"] {
		background: transparent;
		border: 0 !important;
		margin-top: 0px !important;
	}
	/* Fondo tablas publicidad hilos */
	td[bgcolor="#f0f5ff"] {
		background: transparent !important;
	}
	/* Ajustar tamaño automaticamente en tablas publicidad, se ocultan si estan vacias */
	div[id^="div-970x90"] {
		height: auto !important;
		width: auto !important;
	}
	div#div-300x250,
	div#div-300x600 {
		height: auto !important;
	}
	/* Ocultar tablas de publicidad vacias dentro de hilo */
	TABLE.cajasprin[width="100%"][cellspacing="0"][cellpadding="2"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"]>tbody>tr>td {
		padding: 0;
	}
	TABLE.cajasprin[width="100%"][cellspacing="0"][cellpadding="2"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"] {
		border: 0;
	}
	/* Ajustar tablas de publicidad inicio */
	table#AutoNumber30 {
		border: transparent;
	}

	/* Elementos estando desconectado */
	/* Fondo tabla publicidad desconectado */
	td[bgcolor="#ffffe0"] {
		background: transparent;
	}
	/* Ajustar tabla publicidad pie de foro desconectado */
	div[id*="div-336x280"] {
		height: auto !important;
	}
	/* Ajustar publicidad dentro de primer post desconectado */
	span[style="border: solid #FAFAFA; border-left: 10px solid #DADADA; PADDING-LEFT: 5px; padding:5px; margin: 1px; float: right; width: 301px; height: 251px;"] {
		border: none !important;
		height: auto !important;
	}
	/* Aviso hilos restringidos desconectado */
	table#table3.tborder td.alt1[style="background-color:#FFF"] {
		background-color: var(--bg04) !important;
	}
	/* Listado de foros aviso desconectado */
	div.panel[style="margin-top:5px; background-color:#ffffff;"] {
		background-color: var(--bg03) !important;
	}


	/*------------------------------------------------------------+
	¦ PRINCIPAL                                                   ¦
	+------------------------------------------------------------*/

	/* Fondo principal */
	body {
		background: var(--bg01);
		color: var(--tx01);
		margin: 16px;
		background-image: url("https://i.imgur.com/3jwA45L.jpg")
	}

	/* Textos principales */
	.texto,
	.texto1 {
		color: var(--tx01);
	}
	font[color="#000000"] {
		color: var(--tx01);
	}

	/* Textos secundarios (en gris) */
	.time,
	.smallfont-gray {
		color: var(--tx03);
	}

	/* Enlaces principales */
	a:link,
	body_alink,
	a:visited,
	body_avisited,
	.texto A {
		color: var(--tx02);
	}
	.contenido A:hover,
	.contenido_menu A:hover,
	.adcontenido A:hover,
	.texto A:hover,
	.texto1 A:hover,
	.miniyo A:hover,
	.top2 A:hover,
	A:hover {
		color: var(--tx05);
		text-decoration: none;
	}

	/* Aviso cookies */
	#infocookie {
		margin-bottom: 16px;
		font-family: inherit;
		background: var(--bg03);
		color: var(--tx01);
		border: 1px solid var(--bg02);
		width: calc(100% - 2px);
	}
	/* Boton cerrar aviso cookies */
	#infocookie_close {
		filter: invert(100%);
	}



	/* Barra principal de enlaces */
	/* Contenedor barra principal */
	table.cajasprin[style="border-collapse: collapse"]:nth-of-type(1) {
		position: relative;
		top: 2px;
		width: 100%;
		border: 1px solid var(--bg02);
		border-bottom: 0;
	}

	/* Quitar color fondo tabla posterior, importante */
	table.cajasprin[style="border-collapse: collapse"]:nth-of-type(1),
	td.texto[bgcolor="#f5f5f5"] {
		background: transparent;
	}

	/* Quitar bordes laterales blancos barra principal */
	table[id="AutoNumber9"]>tbody>tr>td {
		border: 0;
	}

	/* Diseño barra principal */
	table[id="AutoNumber9"] {
		width: 100%;
		height: 38px;
		border-bottom: 1px solid var(--bg03); /* color fondo tabla para tapar */
	}

	/* Fuente barra principal */
	table#AutoNumber9 font {
		font-size: 15px;
		letter-spacing: 0.05em;
	}

	/* Botones barra principal */
	/* Activo */
	table[id="AutoNumber9"]>tbody>tr>td[bgcolor="#FFFFFB"] {
		background: var(--bg03);
	}
	
	/* Inactivo */
	table[id="AutoNumber9"]>tbody>tr>td[bgcolor="#ECECEC"] {
		background: var(--bg04);
		box-shadow: inset 0 -7px 9px -7px rgba(0,0,0,0.07);
	}
	/* Inactivo hover */
	table[id="AutoNumber9"]>tbody>tr>td[bgcolor="#ffffff"] {
		background: var(--bg10) !important;
		box-shadow: inset 0 -7px 9px -7px rgba(0,0,0,0.07);
	}

	/* Hacer botones clicables en todo el ancho */
	table[id="AutoNumber9"]>tbody>tr>td>a {
		padding: 7px 0px;
		display: block;
	}


	/* Effect: Brackets */
	table[id="AutoNumber9"]>tbody>tr>td>a::before,
	table[id="AutoNumber9"]>tbody>tr>td>a::after {
		display: inline-block;
		opacity: 0;
		-webkit-transition: -webkit-transform 0.3s, opacity 0.2s;
		-moz-transition: -moz-transform 0.3s, opacity 0.2s;
		transition: transform 0.3s, opacity 0.2s;
	}

	table[id="AutoNumber9"]>tbody>tr>td>a::before {
		margin-right: 10px;
		content: '[';
		-webkit-transform: translateX(20px);
		-moz-transform: translateX(20px);
		transform: translateX(20px);
	}

	table[id="AutoNumber9"]>tbody>tr>td>a::after {
