@-moz-document domain("m.forocoches.com") {
/* Quitar / ocultar elementos */
	/* Ajustar tamaño automaticamente tablas de publicidad */
	div#div-970x90-pc_header,
	div#div-336x280-pc_footer,
	div#div-970x90-pc_respuesta {
		height: auto !important;
	}
	/* Quitar borde avatar */
	.avatar {
		border: 0;
	}


	/* Principal */
	body {
		background-color: #121212;
		color: #e1e1e1;
	}
	/* Textos encabezados */
	h1 {
		color: #bfbfbf;
	}
	/* Enlaces principales */
	a:link,
	a:visited {
		color: #9b9b9b;
	}
	/* Enlaces secundarios */
	.asmvinfo A {
		color: #bfbfbf;
	}
	/* Encabezado */
	.mobiletitle {
		color: #d1d1d1;
		background: #1f1f1f;
		border-bottom: 1px solid #383838;
	}
	/* Pie de pagina */
	.mobiletitlebottom {
		color: #d1d1d1;
		background: #1f1f1f;
		border-top: 1px solid #383838;
	}
	/* Panel buscador */
	.dvslide {
		border: 0;
		color: #d1d1d1;
		background: #1f1f1f;
	}
	input.field {
		vertical-align: middle;
		background: #d1d1d1;
		color: #1f1f1f;
		border: 0px;
		min-height: 24px;
		margin: 2px;
		padding: 0px 10px;
		border-radius: 7px;
	}
	/* Botones normales */
	.button, .cbutton {
		font-family: Arial;
		font-size: 12px;
		background-color: transparent;
		color: #d1d1d1;
		border-color: #d1d1d1;
		border: 1px solid;
		border-radius: 7px;
		min-height: 26px;
		min-width: 26px;
		padding: 0 4px;
		margin: 0 2px;
	}
	/* Formularios */
	select {
		background: #1a1a1a;
		border: 1px solid #404040;
		color: #d1d1d1;
		-webkit-appearance: none;
		-moz-appearance: none;
		background-size: cover;
		background-size: 8px;
		background-image: url(http://imgh.us/down_arrow_white.svg);
		background-position: center right 8px;
		background-repeat: no-repeat;
		padding-right: 16px;
		min-height: 26px;
	}
	.bginput {
		background: #d1d1d1;
		color: #121212;
		border: 0;
		min-height: 26px;
		padding: 0px 10px;
		border-radius: 7px;
	}
	.mobiletitle a,
	.mobiletitle a:link,
	.mobiletitle a:visited {
		color: #d1d1d1;
	}
	/* Botones superiores e inferiores y buscar */
	.mobilebutton,
	.mobilebuttonbottom,
	.mobilebuttonslide {
		-webkit-border-image: initial !important;
		border-radius: 7px;
		border-width: 1px !important;
		border: 1px solid #d1d1d1;
		height: 24px;
		line-height: 24px;
		color: #d1d1d1 !important;
		background-color: transparent;
		background-image: initial !important;
		width: 80px;
	}
	/* Boton nuevo tema */
	.mobile_button {
		-webkit-border-image: initial !important;
		border-radius: 7px;
		border-width: 1px !important;
		border: 1px solid #d1d1d1;
		padding: 0 12px 0 12px;
		height: 24px;
		line-height: 24px;
		color: #d1d1d1 !important;
		background-color: transparent;
		background-image: initial !important;
		margin: 0 auto 10px auto;
		text-align: center;
		display: block;
		width: 100px;
	}
	/* Barra destacado / publicidad */
	.title-forum-display-destacado {
		background-color: #424242;
		margin-left: 4px;
		margin-right:4px;
		border: 1px solid #404040;
	}

	/* Barra notificaciones / privados */
	.ul2 {
		color: #d1d1d1;
		background-color: #282828;
		border-width: 1px;
		border-color: #383838;
		border-radius: 0;
	}
	/* Barra notificaciones / privados (activa) */
	ul.ul2[style="background-color:#ffffcc;"] {
		background-color: #85e585 !important;
		color: #121212;
	}
	/* Texto y enlaces barra notificaciones / privados */
	ul.ul2[style="background-color:#ffffcc;"] a,
	ul.ul2[style="background-color:#ffffcc;"] a:link,
	ul.ul2[style="background-color:#ffffcc;"] a:visited {
		color: #121212;
	}
	/* Titulo de hilo */
	.title-forum-display {
		background-color: transparent;
		color: #d1d1d1;
		border: 0;
	}
	/* Lista de paginas */
	/* Menciones y citas pagina perfil */
	.ulpagenav,
	ul.rlink {
		color: #d1d1d1;
		background-color: #282828;
		border-width: 1px;
		border-color: #383838;
		border-radius: 0;
	}
	/* Ver chinchetas */
	ul[style="background-color:#eeeeee"] {
		background-color: #282828 !important;
	}
	/* Fondo principal secundario */
	.page {
		background-color: transparent;
		color: #d1d1d1;
		border: 0;
	}
	/* Borde superior interior mensajes */
	li {
		border-top-color: #383838;
	}
	/* Fondos principales claros */
	ul {
		background-color: #282828;
		color: #d1d1d1;
		border-width: 1px;
		border-style: solid;
		border-color: #383838;
		border-radius: 0;
	}
	/* Fondos principales oscuros / cabecera mensajes */
	/* Cabecera mensajes privados */
	ol#posts,
	li.link-search {
		border-radius: 0;
		border-bottom: none;
		background-color: #1f1f1f;
	}
	/* Separador pie de mensaje, Citar, Responder */
	div[style="margin-top: 8px; border-top:1px solid #dddddd;"] {
		border-top: 1px dotted #383838 !important;
	}
	/* Resalto autor del hilo */
	ul[style="border-left: 3px solid #ca3415; border-right: 3px solid #ca3415;"] {
		width: calc(100% - 5px);
		/* Restar bordes adicionales */
		border-left: 6px solid #ff6b6b !important;
		border-right: 1px solid #383838 !important;
                                            
	}
	/* Resalto usuario */
	ul[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"] {
		width: calc(100% - 5px);
		/* Restar bordes adicionales */
		border-left: 6px solid #85e585 !important;
		border-right: 1px solid #383838 !important;
                                            
	}
	/* Color nombre usuario */
	ul[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"] a.ui-link {
		color: #85e585
	}
	/* Citas */
	.pre-quote {
		border: 0;
		background: #1f1f1f;
		border-left: 6px solid #383838;
		border-radius: 0;
	}
	.post-quote {
		background: #1f1f1f;
		border: 0;
		border-left: 6px solid #383838;
		border-radius: 0;
	}
	/* Spoilers */
	.spoiler {
		font-weight: normal;
		border-radius: 0px !important;
		border: 0 !important;
		border-left: 6px solid #383838 !important;
	}
	/* Fondo y texto de spoiler */
	div[class="spoiler"] {
		background: #1f1f1f;
		color: #1f1f1f;
	}
	/* Texto de spoiler activado */
	div.spoiler[style="color: rgb(0, 0, 0);"] {
		color: #d1d1d1 !important;
	}

li[style="background-color:#efebeb"] {
		background: #1f1f1f !important;
