	/* Pagina perfil de usuario */
	fieldset.fieldset {
		border: 0;
		background: #282828;
		color: #d1d1d1;
	}
	/* Cabeceas Ultimas menciones / Ultimas citas */
	table[style="background-color:#b1bac3; margin-top:0px; margin-bottom:6px; padding:6px;"],
	table[style="background-color:#b1bac3; margin-top:6px; margin-bottom:6px; padding:6px;"] {
		background-color: #1f1f1f !important;
		border: 1px solid #404040 !important;
	}
	/* Fecha */
	td.thead[style="background-color:#f1f1f1"] {
		background-color: transparent !important;
	}
	/* Lista de citas y menciones */
	td.alt2[style="background-color:#ffffff; border:1px groove; -moz-border-radius: 2px; border-radius: 2px; word-wrap: break-word; display:block; max-height:350px; overflow:auto;"] {
		background: #282828 !important;
		border: 0px solid #383838 !important;
	}
	/* Citas y menciones */
	div.alt2[style="font-size:11px; color:#4d4d4d; margin:5px 0px 5px 0px; padding:5px; border:2px groove; word-break: break-word;"] {
		background-color: #1f1f1f !important;
		border: 1px solid #404040 !important;
		margin: 4px !important;
		color: #d1d1d1 !important;
	}
	/* Quitar bordes navegador de paginas en citas y menciones */
	td.alt2[style="background-color:#ffffff; border:1px groove; -moz-border-radius: 2px; border-radius: 2px; word-wrap: break-word; display:block; max-height:350px; overflow:auto;"] .ulpagenav,
	td.alt2[style="background-color:#ffffff; border:1px groove; -moz-border-radius: 2px; border-radius: 2px; word-wrap: break-word; display:block; max-height:350px; overflow:auto;"] li {
		border: 0 !important;
	}
	/* Quitar bordes en infomacion adicional perfil de usuario */
	ul.list_no_decoration {
		border: 0;
	}


