@-moz-document regexp("(http[s]?://)*www.forocoches.com/index.*") {
/* Ocultar tablas lateral derecho */
	table.cajascat[style="border-collapse: collapse"][width="100%"][cellspacing="1"][cellpadding="0"][bordercolor="#111111"][border="0"] {
		display: none;
	}
	/* Tabla buscador */
	table.cajastip {
		padding: 4px;
	}
	/* Quitar borde contenedor principal */
	table.cajasnews {
		border: 0 !important;
	}
	/* Ficha de usuario */
	table.contenido {
		width: 304px !important;
	}
	/* Tabla debajo de buscador */
	table.cajastip+table[width="100%"] {
		display: none;
	}
	/* Ajustar bordes y tamaños para que coincida con inicio */
	td[width="16%"] {
		padding: 2px 6px 0px 6px !important;
	}
	td[width="84%"] {
		padding: 2px 2px 0px 6px !important;
	}
	/* Espaciado tablas de texto */
	table.cajasnews>tbody>tr>td.texto>table.cajascat>tbody>tr>td {
		padding: 0 8px
	}
	table.cajasnews>tbody>tr>td.texto>table.cajascat>tbody>tr:nth-of-type(2)>td {
		padding: 8px;
	}
	/* Fix color */
	td[bgcolor="#FFFFF2"] {
		background: var(--bg02);
	}
	td.cat[bgcolor="#f9f9f9"] {
		background: var(--bg04);
	}
	/* Bordes tablas estadisticas */
	table.sdTrafficTable {
		background: var(--bg07);
	}
	/* Borde abajo */
	.contenido1 {
		BORDER-BOTTOM: 0px;
		padding-bottom: 4px!important;
	}
}
