css:
/*
  The below is a recoloring of the 'user toolbar'.  The background is a reverse of the subtle 
  grey gradient the top bar uses, the normal button color is a gradient from the hint of 
  pink in Celestia's coat to pure white, and the hovered-over button color is S1 Luna's coat 
  color, to pure white.

  ...because, seriously knighty?  Olive green?
*/

@-moz-document domain(fimfiction.net) {
  
  .user_toolbar>ul{
    background:#262626;
    background:-webkit-gradient(linear, left top, left bottom, color-stop(0%, #232323), color-stop(100%, #262626));
    background:-webkit-linear-gradient(top, #232323 0%, #262626 100%);
    background:linear-gradient(to bottom, #232323 0%, #262626 100%);
    border-bottom:1px solid rgba(0,0,0,0.2);
    border-top:1px solid rgba(0,0,0,0.2);
    text-align:center;
    border-color:rgba(0,0,0,0.2)
  }

  .user_toolbar>ul>li{
    display:inline-block;
    vertical-align:middle;
    background:#ffffff;
    background:-webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(201, 150, 182, 0.05)), color-stop(100%, #ffffff));
    background:-webkit-linear-gradient(top, rgba(201, 150, 182, 0.05) 0%, #ffffff 100%);
    background:linear-gradient(to bottom, rgba(201, 150, 182, 0.25) 0%, #ffffff 100%);
    text-shadow:1px 1px rgba(201, 150, 182, 0.25);
    font-size:13px;
    font-weight:bold;
    color:#181b10;
    line-height:38px;
    box-shadow:0px 0px 8px rgba(0,0,0,0) inset;
    border-right:1px solid rgba(0,0,0,0.2);
    margin:0px;
    position:relative;
    z-index:5
  }

  .user_toolbar>ul>li:first-of-type{
    border-left:1px solid rgba(0,0,0,0.2)
  }
  
  
  .user_toolbar>ul>li:hover{
    background:rgba(101,108,185,0.5);
    background:-webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(101,108,185,0.5)), color-stop(100%, #ffffff));
    background:-webkit-linear-gradient(top, rgba(101,108,185,0.5) 0%, #ffffff 100%);
    background:linear-gradient(to bottom, rgba(101,108,185,0.25) 0%, #ffffff 100%);
    text-shadow:1px 1px rgba(101,108,185,0.25)
  }


}
