css:
@-moz-document domain("clipperz.is") {
    body {
        background-color: white !important;
        font-family:'avenir',sans-serif;
    }
	
div#logoFrame {background-color:#fff;}
	img#logo{display:none;}
	a#donateHeaderIconLink, a#donateHeaderLink{display:none;}
	.clipperzServiceDescription, .loginPanelSwitchLanguageBox{ display:none;}
    
    div.loginFormHeaderBox > h3{
        font-size:14px; 
    }
    div.browserCompatibilityBox, .loginFormFooterBox li span, .loginFormFooterBox li a, .formFieldTR td { font-weight: 300;font-size:12px}
    .loginFormField{ background: #fff; border:0; border-bottom:1px dotted #aaa; color:#778; font-size:12px;}
    div.clipperzLoginForm div.loginFormHeaderBox, div.clipperzLoginForm div.loginForm, div.clipperzLoginForm div.loginFormFooterBox {
        background:#fff;
        background-image:none;
    }
    #Clipperz_PM_Components_Panels_passwordTypeChooserTR_13 {display:none}
    #elgen-0 { font-size: 12px; background: #eee;border:1px solid #ccc;border-radius:20px;}
    body .ybtn-left, body .ybtn-center, body .ybtn-right{background-image:none;color:#889}
    
    div#accountStatus, div#directLoginsBlock {background-image:none; background:#f2f2f3; }
    div#accountStatus div.accountStatus, div#accountStatus div.accountLevel, div#accountStatus div.accountExpiration, div#directLoginsDescription {font-size:11px;line-height:12px}
    div#directLoginsBlock h3, div#accountStatus div.header h5 {font-size:12px; border-bottom:1px dotted #ddd;}
    #accountStatus, #directLoginsBlock, div#accountStatus div.header,div#accountStatus div.payButton,div#directLoginsBlock div.directLoginsBlockHeaderBox, div#directLoginsDescription {background-image:none; }
    
    div#footer{border-top:0px #fff; bottom:0;position:absolute; font-weight:300;}
    
    
    /* Menus */
    div#mainTabs{ background: #fff}
    #menusTR, #logoutLI, #lockLI {
        background: #fff; padding: 2px 10px 1px 0px; font-size:10px;
    }
    div#menus {padding-right: 0}
    div#menus > table, ul#exitLinks li a, div#menus table tbody tr td div {
        background-image:none;
    }
    ul#exitLinks li a, div#menus table tbody tr td div a {
        color: #AAA; background-image:none; text-transform: uppercase; font-size:12px;
    }
    div#menus, div#menus table tbody tr td.selectedTab, div#menus table tbody tr td.selectedTab div {background: #FFF; background-image:none; }
    div#menus table tbody tr td.selectedTab div a {color:#b4d7c2; background-image:none; border:1px solid #a2ceb3; border-radius:20px; }
    div#miscLinks ul li a {color: #b4d7c2; font-size: 10px}
    
    div#recordListBlockHeader {}
    div#recordListBlockHeader table.recordListBlockHeaderTABLE{background: #fff; color:#000; border-bottom:1px solid #ddd;}
    div#recordListBlockHeader table.recordListBlockHeaderTABLE tbody tr td.recordBlockTitleTD{background-image:none;}
    div#recordListBlockHeader table.recordListBlockHeaderTABLE h3#recordBlockTitle{color: #000; font-size:14px}
    
    div#recordDetail, table#recordListAndDetailBlockTABLE, #recordDetailSeparatorTD, 
	td#cardBoxLowerLeftTD, td#cardBoxLowerRightTD, td#recordDetailSeparatorTD{ 
  		background:#fff;background-image:none; border:0
	}
    
    /* no entries */
    form.noRecordAtAllFORM{font-size:12px;}
    
    /* List */
    ul#records{margin-top:0}
    ul#records li.selected {background-color: #eee; border: 1px solid #eee}
    ul#records li {font-size:12px} 
    
    /* Records */
    form.recordDataForm div.recordTitleBlock{ background-color:#f8f8f9;border-bottom: 1px solid #d2e1d7}
    div.recordTitleBlock h2 {color:#444; font-size:12px;}
    div.recordTitleBlock h2:before{content:"\2724  "; font-size:14px;color: #666}
    
    tr.recordFieldsTR {background-color:#999;font-size:10px}
    div.Clipperz_recordFieldLabel, div.Clipperz_recordFieldData a, div.Clipperz_recordFieldData, div.directLoginsRecordBox p {font-size:12px}
    
    div.directLoginsRecordBox {border-top:1px solid #d0e6d9;border-bottom:1px solid #d0e6d9}
    div.directLoginsRecordBox ul li {border-top: 1px dotted #EEE;}
    
    /* icons */
    div#menus table tbody tr td:nth-child(1) div a:before {content:"\270E  "}
    div#menus table tbody tr td:nth-child(2) div a:before {content:"\2729  "}
    div#menus table tbody tr td:nth-child(3) div a:before {content:"\2042  "}
    div#menus table tbody tr td:nth-child(4) div a:before {content:"\2318  "}

}
