css:
@-moz-document domain("devdocs.io") {
body {
	background: #1a1a1a;
	color: #bbb;
}
._path,
._sidebar-footer,
._list,
._list-item,
._sidebar,
._header {
	background: #2a2a2a;
}
._sidebar-footer {
	box-shadow: inset -1px 0 #111;
}
._sidebar-footer:before {
	background-image: -webkit-linear-gradient(top, rgba(45,45,45,0), rgba(45,45,45,0.95));
	background-image: linear-gradient(to bottom, rgba(45,45,45,0), rgba(45,45,45,0.95));
}
._nav,
._path-item {
	color: #ddd;
}
._angular h2, ._bower h2, ._c>h2, ._c>h3, ._chai>h2, ._coffeescript>h2, ._cordova>h2, ._d3>h2, ._ember .title, ._express h2, ._go h2, ._haskell>h2, ._jquery .section-title, ._jquery .entry-wrapper>h3, ._jquery .underline, ._knockout>h2, ._git>h2, ._grunt h2, ._laravel h2, ._less>h2, ._lodash h2, ._marionette>h2, ._markdown h2, ._maxcdn h2, ._mdn>h2, ._modernizr h2, ._moment>h2, ._mongoose>h2, ._nginx h4, ._node>h2, ._php h3.title, ._phpunit>h2, ._postgres h2, ._rdoc>.description>h2, ._rdoc header>h3, ._react>h2, ._redis>h2, ._requirejs h2, ._rethinkdb>h2, ._rfc>h2, ._sinon>h2, ._socketio>h2, ._sphinx h2, ._sphinx h3, ._underscore>h2, ._yard>h2, ._yii h2, th {
	background: #333;
    border: 1px solid #444;
}

._search-input,
._attribution-p,
pre, code, samp, ._maxcdn .url, ._redis>.example, ._haskell .src {
	background: #333;
	color: #ddd;
    border: 1px solid #444;
}

._angular .defs>li>h3:first-child, ._bower h3, ._bower h4, ._c>h4, ._chai h3, ._d3>h6, ._express h3, ._go h3, ._haskell>h3, ._haskell p.src, ._jquery .signature>.name, ._jquery .api-item>h3, ._knockout>h3, ._grunt h3, ._laravel h3, ._less h3, ._less h4, ._lodash h3, ._marionette>h3, ._markdown h3, ._maxcdn h3, ._modernizr h3, ._moment>h3, ._mongoose>h3, ._node>h3, ._node>h4, ._phpunit>h3, ._postgres .VARIABLELIST dt, ._rdoc .method-heading, ._react>h3, ._requirejs h3, ._sinon>h3, ._socketio>h3, ._sphinx dl:not(.docutils)>dt, ._yard .signature, ._yii h3, ._shortcut-code, ._label, ._angular p>code, ._angular li>code, ._angular td>code, ._bower code, ._c code, ._c .t-mark, ._c .t-mark-rev, ._chai code, ._coffeescript code, ._cordova code, ._d3 code, ._ember .return>h3, ._ember .params>h3, ._ember p>code, ._express p>code, ._haskell dt>code, ._haskell .complexity, ._haskell .version, ._jquery p>code, ._jquery li>code, ._knockout p>code, ._git code, ._grunt p>code, ._laravel p>code, ._less code, ._maxcdn p>code, ._maxcdn .http_method, ._mdn .inlineIndicator, ._modernizr code, ._moment code, ._node>p>code, ._node .type, ._phpunit>p>code, ._postgres p>code, ._react code, ._redis p>code, ._rethinkdb code, ._sinon dt>code, ._socketio code, ._sphinx p>code, ._sphinx li>code, ._underscore .header+code {
	background: #333;
	color: #ddd;
}
}
