css:
@-moz-document domain("stream-forum.de") {

/* body */
@media screen and (min-width: 801px) {
  body {
    background: #383838 url('http://hdwallpapers4u.eu/wallpaper_3840x2160/stars_light_purple_abstract_ultra_3840x2160_hd-wallpaper-80198.jpg');
    background-size:100%;
    background-attachment:fixed;
  }
}
/* /body */

/* main>div */
#main > div {
  background-color: #fff;
}
/* /main>div */

/* topMenu>icon */
@media screen and (min-width: 801px) {
  #topMenu .icon {
    font-size: 16px;
  }
}
/* /topmenu>icon */

/* breadcrumbs */
.breadcrumbs {
  border:1px solid #d3d8dc;
  border-radius:3px;
}
/* /breadcrumbs */

/* wbbLastPost */
.wbbBoardList .wbbLastPost {
  left:65%;
}
.tabularBox > ul > li > div > div > .containerHeadline ~ aside > .box24 {
  border-radius:3px;
  border-width:1px;
}
/* /wbbLastPost */

/* dropdownMenu */
.dropdownMenu {
  color: #94B6CD;
}
/* /dropdownMenu */

/* tabularBoxTitle */
.tabularBoxTitle {
  border-top-left-radius:3px;
  border-top-right-radius:3px;
}
/* /tabularBoxTitle */

/* tabularBox */
.tabularBox > ul > li {
  border-left:1px solid #d3d8dc;
  border-right:1px solid #d3d8dc;
}
/* /tabularBox */

/* container */
.container {
  border-width:1px;
  border-radius:3px;
}
/* /container */

/* cke_chrome */
.cke_chrome{
  border-width:1px !important;
  border-radius:3px !important;
}
/* /cke_chrome */

/* cke_contents */
.cke_chrome .cke_contents {
  border:0px;
}
/* /cke_contents */

/* sidebar */
#main > div > div > .sidebar {
  border:1px solid #d3d8dc;
  border-radius:3px;
}
/* /sidebar */
}
