css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain("taringa.net") 

{

@font-face {
  font-family: 'Roboto Condensed';
  src: local('Roboto Condensed'), url('http://fonts.googleapis.com/css?family=Roboto+Condensed') format('truetype');
}

/* Animaciones */
.v6 .list-m ul li a:hover,
.content-left ul li:hover,
.content-left ul li:hover a,
.content-left ul li:hover .meta a span,
.content-left ul li:hover .meta a:before,
.content-left ul li:hover .meta span,
.content-right .ver:hover,
#lastCommentsBox li a:hover,
nav.header *,
.content-right ul li a:hover h3,
.v6 nav.tabs ul li a,
.content-left .list-l ul li h3 a {
    -moz-transition: ease-in all .1s !important;
}

/* General */

body {
    background-color:rgb(240,240,240) !important;
}


/* Header */

.nav-principal {
    position: relative !important;
    top: -1px !important;
}

.nav-principal a {
    color: rgba(255,255,255,.7) !important;
    font-family: 'Roboto Condensed', sans-serif !important;
    font-weight: 400 !important;
}

.nav-principal li {
    margin-right:2px !important;
}

.nav-principal a:hover, .nav-principal li.active a {
    color: white !important;
}

.nav-principal li.tops a:after {
    display: none !important;
}

.user-action {
    background: none !important;
}

.user-action > a {
    display: block !important;
    height: 100% !important;
    width: 100% !important;
}

.user-action:hover, .nav-principal li:hover, .nav-principal li.active {
    background: rgba(0,0,0,.2) !important;
}

.user-action:last-child {
    height: 40px !important;
}

.user-actions {
    background: none !important;
    padding:0 !important;
}

nav.header {
    background: url(http://brianvalente.tk/taringathemes/modernv6/headerbg.jpg) 0 0 no-repeat, url(http://brianvalente.tk/taringathemes/modernv6/headerbg-2.jpg) 0 0 no-repeat, black !important;
    background-position: center !important;
    background-size: cover !important;
    border: none !important;
    position: fixed !important;
    box-shadow:rgba(0,0,0,.5) 0 0 7px !important;
}

nav.header .buscar {
    background: rgba(0,0,0,.2) !important;
    border-color: transparent !important;
    border-radius: 0px !important;
    display:none !important;
}

nav.header .ico-shout {
    display: none !important;
}

nav.header .tool-profile .user-name {
    font-family: 'Roboto Condensed', sans-serif !important;
    font-size: 14px !important;
    font-weight: 400 !important;
    margin:0 !important;
}

nav.header .tool-profile img {
    margin:0 !important;
    padding:0 !important;
    position:relative !important;
    left:-10px !important;
    border-radius:0 !important;
    -moz-filter: grayscale(100%) !important;
}

nav.header .tool-profile:hover img {
    -moz-filter: grayscale(0%) !important;
}

nav.header h1 a {
    opacity:.8 !important;
}

nav.header h1 a:hover {
    opacity:1 !important;
}

nav.header #counter-newsfeed {
    position:absolute !important;
    top:7px !important;
    left:33px !important;
    background:transparent !important;
    color:rgba(255,255,255,.6) !important;
    font-size:9px !important;
}

nav.header li:hover #counter-newsfeed,
nav.header li.active #counter-newsfeed {
    color:white !important;
}

nav.header .user-actions .user-action > a {
    color:rgba(255,255,255,.7) !important;
    padding: 0 11px !important;
    text-shadow:none !important;
}

nav.header .user-actions .user-action:hover > a,
nav.header .user-actions .user-action:hover > a span {
    color:white !important;
}

.ico-notificaciones,
.ico-mensajes  {
    position:relative !important;
    right:1px !important;
}

#tool-notificaciones {
    left:-131px !important;
}

.ico-notificaciones span,
.ico-mensajes span {
    position:absolute !important;
    top:4px !important;
    left:24px !important;
    background:none !important;
    color:rgba(255,255,255,.7) !important;
}

.ico-notificaciones span:before,
.ico-notificaciones span:after,
.ico-mensajes span:before,
.ico-mensajes span:after {
    display:none !important;
}

#tool-mensajes {
    left:-130px !important;
}

#tool-favoritos {
    left:-130px !important;
}

.tools {
    border-radius:0 !important;
    top:50px !important;
    box-shadow:#000 0 0 50px !important;
}

.tools:before {
    top: -10px !important;
    right:149px !important;
}

nav.header .tools h4 {
    font-size:20px !important;
    font-family: 'Roboto Condensed', sans-serif !important;
    text-shadow:none !important;
    line-height:50px !important;
    font-weight:normal !important;
    text-indent:0px !important;
    text-align:center !important;
    position:relative !important;
    top:-1px !important;
}



nav.header .tools .actions a {
    position:absolute !important;
    width:30px !important;
    height:30px !important;
    margin:0 !important;
    padding:0 !important;
    right:0px !important;
    top:-1px !important;
    color:transparent !important;
    z-index:99 !important;
    overflow:hidden !important;
}

nav.header .tools .actions a:before {
    content:'+' !important;
    color:grey !important;
    font-size:19px !important;
    font-weight:normal !important;
    padding-left:9px !important;
}

nav.header .tools .actions a:hover {
    color:transparent !important;
}

nav.header .tools .actions a:hover:before {
    color:black !important;
}

nav.header #tool-profile {
    background:white !important;
    border: 1px solid #ebebeb !important;
    padding-top:50px !important;
}

nav.header #tool-profile:before {
    font-size: 20px !important;
    font-family: 'Roboto Condensed', sans-serif !important;
    text-shadow: none !important;
    line-height: 50px !important;
    font-weight: normal !important;
    text-indent: 0px !important;
    text-align: center !important;
    content:'Opciones' !important;
    color:black !important;
    border:0 !important;
    width:160px !important;
    background:none !important;
    top:-1px !important;
    left:0 !important;
}

nav.header #tool-profile:after {
    border-bottom-color:white !important;
    top:-10px !important;
}

nav.header #tool-profile ul {
    border-top: 1px solid #ebebeb !important;
    padding-top:6px !important;
}

nav.header #tool-profile li a {
    background:none !important;
    color:rgb(20,20,20) !important;
}

nav.header #tool-profile li a:hover {
    background:rgb(230,230,230) !important;
}

nav.header .user-actions .user-action .mas:hover {
    background:transparent !important;
}

nav.header .user-actions .user-action .mas {
    background:transparent !important;
    letter-spacing:-6px !important;
    color:transparent !important;
}

nav.header .user-actions .user-action .mas:before {
    content:'' !important;
    letter-spacing:0px !important;
    color:#777 !important;
}

nav.header .user-actions .user-action .mas:hover:before {
    color:#111 !important;
}

nav.header .user-actions .user-action #tool-notificaciones .mas:before {
    content:'Ver todas las notificaciones' !important;
}

nav.header .user-actions .user-action #tool-mensajes .mas:before {
    content:'Abrir mensajes' !important;
}

nav.header .user-actions .user-action #tool-favoritos .mas:before {
    content:'Ver todos los favoritos' !important;
}


/* Home */

.box-crear-post,
.box-juegos,
#eplAdDiv300,
.container-home aside.sidebar div:nth-child(3),
.container-home aside.sidebar .taringa-musica,
.container-home aside.sidebar .anuncios
{
    display:none !important;
}

.v6 nav.tabs ul li.crear a, .v6 div.meta-data ul li.crear a {
    color: #7e7e7e !important;
    font-weight: normal !important;
}

.content-left .list-l ul li h3 a {
    color:rgb(80,80,80) !important;
}

.content-left .list-l ul li:hover h3 a {
    color:black !important;
}

.v6 .estadisticas .slide-panel p span.num,
.content-right .ver,
aside.sidebar .vs,
aside.sidebar .like,
aside.sidebar .usuario span,
.content-left .list-l ul li h3 a:hover,
.content-right ul li a:hover h3,
.v6 .action-select ol li.active a,
.action-select ol li:hover a,
.v6 nav.tabs ul li a:hover {
    color:#0097A7 !important;
}

.content-left .list-l ul li:hover .meta a,
.content-left .list-l ul li:hover .meta span,
.content-left .list-l ul li:hover .meta a:before{
    color:rgb(120,120,120) !important;
    text-decoration:none !important;
}

.v6 .top-usuarios ul li a.bt {
    background:#4CAF50 !important;
}

.v6 .top-usuarios ul li a.bt:hover {
    background:#43A047 !important;
}

.v6 .list-m ul li a:hover,.content-left ul li:hover,
.content-right .ver:hover,
#lastCommentsBox li a:hover {
    background:rgb(245,245,245) !important;
}

.v6 .list-m ul li:hover a p {
    color: #adadad !important;
}

#lastCommentsBox li a {
    background:white !important;
}

/* Footer */


footer .anuncios-taringa ul li:last-child:after {
    content:' | Theme creado por @electrocrookers' !important;
}

/* Posts */

.v6 .sidebar .widget-usuario .follow-buttons .btn {
    border-radius: 0 !important;
}

.v6 .sidebar .widget-usuario .follow-buttons .btn.g.not-following {
    background:#4CAF50 !important;
}

.v6 .sidebar .widget-usuario .follow-buttons .btn.g.not-following :hover {
    background:#43A047 !important;
}

.v6 .sidebar .widget-usuario .follow-buttons .btn.r.unfollowing {
    background:#D32F2F !important;
}

.v6 .sidebar .widget-usuario .follow-buttons .btn.v.following {
    background:#4CAF50 !important;
}

.v6 body.section-post .v6 .v6-container .v6-content {
    border-left:solid 1px #ebebeb !important;
    margin-left:70px !important;
    padding-left:10px !important;
}

.v6 body.section-post #footer {
    background:white !important;
}

.v6 body.section-post .social-side-bar {
    /*top:100px;
    position:fixed;
    left:-1197px;
    right:0;
    margin:auto;*/
    display:none !important;
}   /* Necesita tiempo */

body.section-post .v6-container.container-home {
    background:transparent !important;
}

/* Mi Taringa! */

.shout-footer .s-action-list .icon:hover {
    opacity: 1  !important;
}

#Feed-reload, #Feed-error {
    border: 1px solid #CCC !important;
    border-radius: 0 !important;
    background-color: #F2F2F2 !important;
    box-shadow: 0 0 5px #BDBDBD !important;
}

.my-shout-attach-options .btn {
    background: rgb(235,235,235) !important;
    border-radius: 0 !important;
    border: solid 1px rgb(230,230,230) !important;
    padding-top:2px !important;
}

.my-shout-attach-options li:hover .btn {
    background: #E4E4E4 !important;
    border: solid 1px rgb(230,230,230) !important;
}

.my-shout-add.btn, .my-shout-add.btn:hover, .my-shout-add.btn:active {
    border: solid 1px rgb(244,244,244) !important;
    background: #EEEEEE !important;
    border-radius: 0 !important;
    color: rgb(160,160,160) !important;
    text-shadow: none !important;
    box-shadow: none !important;
}

.my-shout-add.btn:hover {
    background: #E0E0E0 !important;
    color: rgb(100,100,100) !important;
}

#Feed-list {
    width: 715px !important;
}

.activity-content .dialog {
    display: none !important;
}

.activity-content, .shout-footer {
    border-left: solid rgba(100,105,150,.7) 5px !important;
    -moz-transition: ease-in all .15s !important;
}

.activity-content:hover + .shout-footer, .activity-content:hover, .shout-footer:hover ~ .activity-content {
    border-left: solid rgb(100,105,150) 5px !important;
}

#Feed-list .activity-content, .shout-individual .activity-content {
    border-bottom: none !important;
}

.shout-footer {
    border-top: none !important;
}

#my-shout-body-mi,
#my-shout-body-wall {
    border-radius: 0 !important;
    outline: none !important;
    border: solid 5px #F2F2F2 !important;
    width:531px !important;
    height:48px !important;
    box-shadow: rgb(222,222,222) 0px 0px 0px 1px !important;
}

.activity-element .avatar {
    position:absolute !important;
}

.shout-box .avatar img,
.activity-element .avatar img,
.activity-element .alter img {
    border: solid 5px #F2F2F2 !important;
    box-shadow: rgb(222,222,222) 0px 0px 0px 1px !important ;
    padding:0 !important;
}

.activity-element .alter {
    margin-top:5px !important;
    margin-left:5px !important;
}

.activity-content {
    position:relative !important;
    left:78px !important;
}

#Feed-controls-mi {
    background:#F2F2F2 !important;
    border-radius:0 !important;
}

#Feed-controls-mi li a {
    border-radius:0 !important;
    box-shadow:none !important;
}

#Feed-controls-mi li a.active {
    background:rgb(150,150,150) !important;
    text-shadow:0 1px 1px rgba(0,0,0,0.7) !important;
}

.shout-footer {
    background:rgb(247,247,247) !important;
    border-top:none !important;
}

.button-action-s {
    background:none !important;
}

body.section-perfil .shout-box {
    background:white !important;
}

body.section-shout .activity-content {
    left:0 !important;
}

/* Perfiles */

    /* MOSTRAR ESTADO EN AVATAR */

#user-metadata-profile .dot-online-offline[title='Online']:after {
    content: '' !important;
    width: 120px !important;
    height: 120px !important;
    border: solid 5px #A5D6A7 !important;
    position: absolute !important;
    left: -126px !important;
    top: -152px !important;
    box-shadow: rgb(222,222,222) 0px 0px 0px 1px !important;
}

.perfil-avatar {
    margin-left:5px;
    box-shadow: #E0E0E0 0px 0px 0px 5px !important;
    padding-bottom:0 !important;
    margin-bottom:0 !important;
    height:120px !important;
    margin-bottom:5px !important;
}

}
