/* Facebook.com user style by @drewgaze. 
   credit to @eramdam because I lifted a few lines from his style which is available at https://github.com/eramdam/BetterFacebookIsBetter */

@-moz-document domain("facebook.com") {
    
/* KILL STUFF */    
    #rightCol,
    #appsNav,
    #groupsNav,
    #pagesNav,
    #interestsNav {
       display: none !important;
    }

/* Clean up the UI */    
    
    body,
    ._5vb_,
    ._5vb_ #contentCol {
       background-color:white !important;
    }

    ._5bfg #pageNav._5bfj .tinyman .navLink:after,
    ._hdd,
    ._5bfg #pageNav._5bfj .tinyman .navLink:hover::after {
       background-image:none !important;
    }

    ._2jq5 {
       border-left: 0px !important;
    }

/* Volets topbar */
    ._50c9 {
       -webkit-border-radius: 0 !important;
    }
    .notifNegativeBase .fbJewelFlyout, #tinymanNavWrapper .fbJewelFlyout {
       -webkit-border-radius: 0 !important;
    }

    ._4jy1, ._4jy1._42fr:active,
    ._4jy1._42fr._42fs {
       background:#4e69a2 !important;
       border-radius:0 !important;
    }

    ._4f7n {
       background-image: none !important;
       border-bottom: 0px;
       box-shadow: 0 2px 5px rgba(0, 0, 0, 0.26);
    }

/* Content Resize */
    ._5vb_._5vb_.hasLeftCol div#contentArea {
       width: 100% !important;
    }

    ._5vb_.hasLeftCol #leftCol {
       box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.26) !important;
       margin-top: 12px;
       position: fixed;
       border-radius: 0px;
    }

    ._6l-:after {
       border-width: 0px !important;
    }

    ._dcs {
       box-shadow: none !important;
    }


/* this contains all the content in your feed */
    ._4-u2 {
       box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.26) !important;
       border-radius: 0px;
       border-width: 0px !important;
    }

/*Blocky search bar*/
    ._585-[role="search"],
    ._42ft._42fu._4w98[role="submit"],
    ._585-, ._4w98 {
       border-radius: 0 !important;
    }

/* Moving the bar */
    ._4w96._585- {
       left: 144px;
    }

/* Kill more stuff */
    ._5lus a {
       display: none;
    }

    ._2dpe {
       display: none;
    }

    ._55bh:after{
       display: none;
    }

/* profile stuff */
    #pagelet_timeline_recent .fbTimelineCompactSection {
       background-color: #FFF !important;
    }

    #fbTimelineHeadline .profilePic,
    ._6_7,
    ._6-6,
    ._1zw4 {
       border-width: 0px !important;
    }

    ._513x {
       bottom: 0px !important;
       width: 16px !important;
    }

    .topBorder,
    .bottomBorder {
       display: none !important;
    }

/* Duarte made me do it */
    ._1zw4,
    fbTimelineComposerUnit.fbTimelineComposerUnit,
    ._4lh .timelineReportContainer,
    html ._6-d .fbTimelineSection {
       box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.26) !important;
       border-radius: 0px !important;
       border-width: 0px !important;
    }

    ._5-r ._93k {
       box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.26) !important;
    }


    ._70l {
       border-bottom: none !important;
       border-radius: 0px !important;
    }

    .uiButtonGroup {
       border-radius: 0px !important;
    }

    .fbTimelineCapsule .timelineUnitContainer {
       border: none !important;
       box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.26) !important;
    }

    .timelineUnitContainer.recentActivityUnit {
       height: 213px;
    }
    
    .uiBoxWhite {
       border: none !important;
    }
    
    ._596n {
        background-color: #fff !important;
        border: none !important;
        border-radius: 0px !important;
    }
}
