css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {
  #sitetitle {
    float: none;
  }
  #hd .menu ul a,
  #hd .menu #loginGreating a {
    background: rgb(85, 87, 83);
    border-radius: 0.2em;
    color: rgb(238, 238, 236) !important;
    text-decoration: none;
    padding: 6px 12px;
    display: inline-block;
  }
  #hd .menu ul a:hover,
  #hd .menu #loginGreating a:hover {
    box-shadow: 0px 0px 1px 1px #729FCF;
  }
  #hd .menu {
    margin-bottom: 15px;
  }
  #hd .menu ul {
    margin-left: 17px;
  }
  #hd .menu li {
    margin-right: .2em;
  }
  #hd .menu #loginGreating a[href="https://www.linux.org.ru/register.jsp"] {
    position: relative;
    right: -12px;
    z-index: 2;
  }
}
