@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {


/* This script is not usable as is. It is a compilation. You can disable definitions with the characters that surround titles. The titles indicate the effects. Some settings are doubled or are unnecessary, it is still a bit of testing. */


/*Delicious 2.0 No globalnav*/
#globalnav {
display: none !important;
}

#banner {
border-top: 0 !important;
margin-top:0 !important;
}


/*Delicious 2.0  No globalnav (with space)*/
#globalnav {
visibility: hidden !important;
}


/*Delicious 2.0 No banner (with space)*/
#banner {
visibility: hidden !important;
}


/*Delicious 2.0 No banner*/
#banner {
display: none !important;
}


/*Delicious 2.0 No Icon*/
#banner h1 a {
background: 0 !important;
padding: 0 !important;
}

#navigation {
margin-left: 7.5em !important;
}


/*Delicious 2.0 No site_title*/
#navigation {
margin-left: 2em !important;
}


/*Delicious 2.0 No icon and No site_title*/
#banner h1 a {
background: 0 !important;
padding: 0 !important;
}

#navigation {
margin-left: 0em !important;
}


/*Delicious 2.0 No list-item-home*/
#navigation #list-item-home {
display: none !important;
}


/*Delicious 2.0 No list-item-bookmarks*/
#navigation #list-item-bookmarks {
display: none !important;
}


/*Delicious 2.0 No list-item-people*/
#navigation #list-item-people {
display: none !important;
}


/*Delicious 2.0 No list-item-tags*/
#navigation #list-item-tags {
display: none !important;
}


/*Delicious 2.0 No search-box*/
#banner .search-box {
display: none !important;
}


/*Delicious 2.0 No space betwen banner and titlebar*/
#ttlbar {
margin: 0px !important;
}

#banner {
}


/*Delicious 2.0 No ttl*/
#ttlbar #ttl {
display: none !important;
}

#ttlbar #actions {
display:none !important;
}


/*Delicious 2.0 No Big Blue Logo*/
#ttlbar #ttl #tagDescFormWrapper, #ttlbar #ttl #pagetitle {
margin-left: 0 !important;
}

#pagetitle #pagetitleContent {
background: 0 !important;
}


/*Delicious 2.0 No pagetitle*/
#pagetitle h2 {
color: white !important;
}


/*Delicious 2.0 No pagetitle (No space)*/
#ttl {
min-height: 0 !important;
}

#pagetitleContent {
min-height: 0 !important;
}

#pagetitle h2 {
display:none !important;
}

#pagetitle #pagetitleContent {
min-height: 0px !important;
}


/*Delicious 2.0 No pagetitleContent alt_message*/
#pagetitle p#alt_message {
display: none !important;
}


/*Delicious 2.0 No pagetitleContent alt_message (No space)*/
#ttl {
min-height: 0 !important;
}

#pagetitleContent {
min-height: 0 !important;
}

#pagetitle p#alt_message {
display: none !important;
}


/*Delicious 2.0 No see_also*/
/*#ttl {*/
/*min-height: 0 !important;*/
/*}*/

/*#pagetitleContent {*/
/*min-height: 0 !important;*/
/*}*/

#pagetitle #see_also {
display:none !important;
}


/*Delicious 2.0 No space between ttl and tabscope*/
#bd {
padding: 0 !important;
}


/*Delicious 2.0 No actions*/
#ttlbar {
clear: none !important;
position: static !important;
}

#ttlbar #actions {
display: none !important;
position: static !important;
}

#ttlbar #ttl #tagDescFormWrapper, #ttlbar #ttl #pagetitle {
margin-right:0 !important;
}


/*Delicious 2.0 No tagscope*/
#tagscope {
display: none !important;
}


/*Delicious 2.0 No bookmark-display-options*/
#bookmark-display-options {
display: none !important;
}

/*ul.bookmarks li.first {*/
/*border-top: 0px !important;*/
/*}*/


/*Delicious 2.0 No border-top for first post*/
ul.bookmarks li.first {
border-top:0px solid #D5D5D5 !important;
}

ul.bookmarks li.first .dateGroup {
border-top:0px solid #D5D5D5 !important;
}


/*Delicious 2.0 sidebar*/
/* to delete */
#sidebar {
display: none !important;
margin-left: 0px !important;
}

/* to set larger */
#content {
margin-right: 2em !important;
}


/*Delicious 2.0 sidenav-title*/
#sidenav-title {
display: none !important;
}


/*Delicious 2.0 related-tags*/
#rrelated-tags {
display: none !important;
}


/*Delicious 2.0 top-tags*/
#rtop-tags {
display: none !important;
}


/*Delicious 2.0 bundle-tags*/
#rbundle-tags {
display: none !important;
}


/*Delicious 2.0 user-tags*/
#ruser-tags {
display: none !important;
}


/*Delicious 2.0 No border-top for pagination*/
#pagination {
border-top:0px solid #DDDDDD !important;
}


/*Delicious 2.0 No space before pagination*/
#pagination {
padding-top: 1em !important;
}


/*Delicious 2.0 No pagination*/
#pagination {
display: none !important;
}


/*Delicious 2.0 No space after pagination*/
#pagination {
padding-bottom: 0 !important;
margin-bottom: 0 !important;
}


/*Delicious 2.0 display-and-rss-options*/
#display-and-rss-options {
display: none !important;
}


/*Delicious 2.0 Space between display-and-rss-options and feet*/
#ft {
margin: 0 0 1em !important;
}

#bd {
margin: 0 !important;
}

#display-and-rss-options {
height: 0 !important;
}


/*Delicious 2.0 feet*/
#ft {
display: none !important;
}


/*Delicious 2.0 No space after feet*/
#ft {
margin-bottom: 0 !important;
}

#bd {
margin-bottom: 0 !important;
padding-bottom: 0 !important;
}

#display-and-rss-options {
height: 0 !important;
}

.beacon {
display: none !important;
}

#doc3 {
padding-bottom: 0 !important;
margin-bottom: 0 !important;
}

#form-edit-bookmarks {
padding-bottom: 0 !important;
margin-bottom: 0 !important;
}

body {
padding-bottom: 0 !important;
margin-bottom: 0 !important;
}

}
