css:
@-moz-document domain("zat.io") { 
html{background-color:#000000 !important;font-family:"Segeo UI Light","Segoe UI" !important;color:#ff2a00;cursor: url("http://u.cubeupload.com/LeJon/TwZH9R.png"), default !important;}

embed{box-shadow: 0px 0px 8px 5px #000000;border: 1px solid;}

header{width:100%;height:45px;background-color:#000000 !important;margin-bottom:15px;box-shadow: 0px 0px 8px 5px #000000;border: 1px solid;}

header div.logo{font-family:Century Gothic;float:left;display:block;margin-left:19px;line-height:45px;font-size:250%;}
nav{width:67%;position:relative;float:right;height:45px;z-index:10;}
nav ul{list-style-type:none;margin:0;padding:0;display:flex;text-align:center;}
nav ul li{text-align:center;display:inline-block;float:left;width:14%;padding-left:5px;height:45px;}

nav ul li:hover{background-color:#1b1b1b !important;}

nav ul li a{display:block;height:100%;text-decoration:none;line-height:45px;text-transform:uppercase;color:inherit;}
nav ul li ul,nav ul li ul li,nav ul li ul li a{display:none;}
nav ul li:hover ul,nav ul li:hover ul li,nav ul li:hover ul li a{display:block;text-align:center;width:100%;color:#ff2a00;}

nav ul li ul li{padding-left:5px;margin-left:-5px;float:left;width:100%;height:45px;font-family:"Segoe UI Light","Segoe UI";background-color:#1b1b1b;}
nav ul li ul li a.active{background-color:#eee;}

nav ul li ul li:hover{background-color:#000000 !important;}

nav ul li ul li:hover a{display:block;text-decoration:none;color:#ff2a00;height:100%;width:100%;line-height:45px;text-transform:uppercase;}.apps{position:fixed;top:0;bottom:0;left:10px;margin:auto;height:315px;width:45px;background-color:#000000;border-radius:3px;padding:3px 0px;z-index:1;}.apps ul li{text-align:center;}.apps ul li a,.apps ul li a i{display:block;color:inherit;line-height:45px;text-decoration:none;}.apps ul li:hover{color:#ff2a00;background-color:#1b1b1b !important;}.block{display:inline-block;width:98%;margin:0.5%;padding:0.5%;margin:0 auto;text-align:center;}#pages{color:#ff2a00;background-color:#000000 !important;width:100%;text-align:center;}.pvbox{display:none;}.tc{text-align:center;}.tl{text-align:left;}.tr{text-align:right;}.fr{float:right;}.f0px{font-size:0px;}.pnt{cursor:pointer;}.hus:hover{text-decoration:underline}.block.c1{width:98%;}.block.c2{width:48%;}.block.c3{width:31.3%;}.block.c4{width:23%;}.block.c5{width:18%;}.block.c2-3{width:62.6%;}.block.c4-5{width:72%;}

.everything{min-height: 100%;width:100%;overflow:hidden;background-image: url('http://u.cubeupload.com/LeJon/R4v68E.png');}

.import{width:100%;background-color:#000000 !important;padding:1.5%;text-align:center;margin-bottom:15px;/* background-image: url('http://u.cubeupload.com/LeJon/vSCTMl.png'); */ box-shadow: 0px 0px 8px 5px #000000;border: 1px solid;}

.notif{width:696px;background-color:#000000;padding:1%;text-align:center;margin:0 auto;margin-bottom:15px;box-shadow: 0px 0px 8px 5px #000000;border: 1px solid;}
.chat{text-align:center;display:block;position:relative;width:100%;}.left{width:20%;float:left;}.right{width:79%;float:right;}.halfopage{width:45%;margin:0 auto;}.window{position:relative;min-width:200px;background-color:#494949;}.window .heading{position:relative;top:0;width:100%;height:45px;background:#ff2a00;line-height:45px;text-align:center;border:none;}.window>.heading{position:relative;top:0;width:100%;height:45px;background-color:#000000;font-size:100%;line-height:45px;text-align:center;color:#ff2a00;border:none;}.window>.content{font-size:100%;text-align:center;color:#ff2a00;padding:1%;}.window>.content table{border-collapse:collapse;width:100%;margin:0px;padding:0px;}.window>.content table td{padding:5px;}.tabs{width:100%;text-align:center;background-color:#000000;}.tabs>ul{width:100%;height:45px;}.tabs>ul li{display:inline-block;width:25%;height:45px;background-color:#000000;}.tabs>ul li a{display:list-item;width:100%;height:100%;line-height:45px;text-decoration:none;color:#ff2a00;}.tabs>ul li span{display:list-item;width:100%;height:100%;line-height:45px;text-decoration:none;color:#ff2a00;}.tabs>ul>li:hover,.tabs>ul>li.active{background-color:#1b1b1b;}.tabs>ul>li:hover span{background-color:none;}.tabs_content{display:block;width:100%;margin:0 auto;text-align:center;background-color:#494949;}.tabs_content .tab_content{color:#ff2a00;display:none;}.tabs_content .tab_content:first-child{display:block;}input[type="text"],input[type="text"]:focus,input[type="text"]:hover,input[type="password"],input[type="password"]:focus,input[type="password"]:hover,input[type="submit"],input[type="submit"]:focus,input[type="submit"]:hover,button{background:#ff2a00;border:none;outline:none;border-radius:3px;padding:1%;margin:1% auto;}input[type="submit"]:hover,select:hover{cursor:pointer;}select,select option{background-color:#ff2a00;border:none;outline:none;border-radius:3px;padding:2%!important;}footer{position:relative;width:100%;height:50px;background-color:#000000;text-align:center;}footer strong{font-size:30px;}#pages span.active{text-decoration:underline;}@media screen and (max-width: 880px) {nav ul li a i{display:none;}}@media screen and (max-width: 600px) {nav ul li a{font-size:10px;}}@-moz-document url-prefix(){.everything{height:100%;}}}
