css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://pl.python.org/forum") 
{

 /* pozbycie sie dolnego przewijania w Chrome */
  
 body {
   overflow-x: hidden;
 } 
  
 /* zlote naglowki po najechaniu mysza */
  
 tr:hover td, /* rozne naglowki */
 #infocenter:hover /* naglowek Centrum informacji */
 {
   background-color: gold !important;
 }

 /* szerokie okno */
  
 #wrapper 
 {
   width: 100% !important;
   max-width: 1300px !important;
   margin: auto !important;
 }

 /* logo */

 img[src$="logo.png"]
 {
   width: 50% !important;
 }

 /* pole z komunikatem */

 #wrapper > .titlebg
 {
   border: 1px solid #377BB3 !important;
   background-color: #cfc !important;
 }

 /* ikona nowych wiadomosci */

 img[alt="Nowe wiadomości"] {
   outline: 7px solid lime !important;
   content: none;
 }

 /* linki do profili itp. na czerwono */

 a[href*="profile"], /* linki do profili - nazwa uzytkownika */
 a[href*="action=logout"] /* wylogowanie */
 {
   color: #900000 !important;
 }

 /* linki na niebiesko */

 .post a, /* linki w postach */
 a[href*="topic"], /* linki do tematow */
 .nav a, /* nawigacja gorna - drzewo podkatalogow */
 .maintab_back a,
 .maintab_last a,
 .maintab_first a,
 a[name^="b"] /* linki do dzialow */
 {
   color: #3776AB !important;
 }

 .maintab_active_back a, 
 .maintab_active_last a,
 .maintab_active_first a
 {
   color: #fff !important;
 }

 /* linki na niebiesko - podkreslenie */

 .post a:hover, /* linki w postach */
 a[href*="topic"]:hover, /* linki do tematow */
 .nav a:hover, /* nawigacja gorna - drzewo podkatalogow */
 .maintab_back a:hover,
 .maintab_last a:hover,
 .maintab_first a:hover,
 .maintab_active_back a:hover, 
 .maintab_active_last a:hover,
 .maintab_active_first a:hover, 
 a[name^="b"]:hover
 {
   text-decoration: underline !important;
 }

 .maintab_back,
 .maintab_last,
 .maintab_first
 {
   background-image: none !important;
   background-color: #eee;
 }

 .maintab_active_back,
 .maintab_active_last,
 .maintab_active_first {
   background-image: none !important;
   background-color: #3776AB;
   color: #fff;
 }
 
 .titlebg,
 .titlebg2
 {
   background-image: none !important;
 }

 #profile,
 .tborder,
 #footerarea
 {   
   border: 1px solid #3776AB !important;
 }

 .catbg,
 .catbg_l,
 .catbg_r,
 .catbg3,
 #infocenter
 {
   background-image: none !important;
   background-color: #888 !important;
 }

 /* bitmapy w naglowku "Centrum informacji" */
  
 img[src$="nav_l.gif"],
 img[src$="nav_r.gif"]
 {
   display: none !important;
   background-color: #888 !important;
 }
  
 td[width="5px"]
 {
   background-color: #888 !important;
 }
  
 /* wklejony kod - szersze pole */

 .geshi
 {
   width: auto !important;
 }

 /* wielkosc liter */

 body,
 td, 
 th,
 tr
 {
   font-size: 10pt !important;
 }

 .smalltext
 {
   font-size: 9pt !important;
 }

 /* zolte tlo w postach i nie tylko */

 .bordercolor
 {
   background-color: #FFDF76 !important;
 }
  
 .geshi
 {
   font-family: mono;
 }
}
