css:
@-moz-document domain("play.pokemonshowdown.com"), domain("frost.psim.us") {
/*********************************************************
 * Global
 *********************************************************/

html, body {
	margin: 0;
	padding: 0;
}
body {
	color: white;
	font-size: 16px;
	font-family: Helvetica, Arial, sans-serif;

	/* license: CC-BY source: http://commons.wikimedia.org/wiki/File:%22La_piedra%22_(2809466426).jpg */
	background: #344b6c url(http://static.zerochan.net/Gardevoir.full.357014.jpg) /*no-repeat left center fixed*/ ;
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
}

.pad {
	margin: 0;
	padding: 5px 15px;
}
.label {
	font-size: 12px;
	font-weight: normal;
	display: block;
}
.optlabel {
	font-size: 12px;
	display: block;
}
.label strong {
	font-size: 12px;
	display: block;
}
.label .textbox {
	display: block;
}
.textbox {
	border: 1px solid lightgrey;
	border-radius: 3px;
	padding: 2px 3px;
	font-family: Helvetica, Arial, sans-serif;
	font-size: 12px;

	box-shadow: none;
	background: white;
	color: black;
}
.textbox:hover {
	border-color: lightgrey;
	box-shadow: none;
	background: white;
}
.textbox:focus {
	outline: 0 none;
	border: 1px solid lightgrey;
	box-shadow: none;
	background: white;
}
a.subtle {
	text-decoration: none;
	color: white;
}
a.subtle:hover {
	text-decoration: underline;
	color: #6688CC;
}
.buttonbar {
	margin-top: 1em;
	text-align: center;
}

/*********************************************************
 * Header
 *********************************************************/

.header {
	position: relative;
	height: 50px;
	background: /* url(http://i.imgur.com/W5irw2t.png) repeat-x left top scroll */ rgba(0, 0, 0, 0.5);
}
.header h1 {
	margin: 0;
	padding: 0;
}
.header .logo {
    display: none;
	float: left;
	margin: 2px 0 0 2px;
}

.userbar {
	position: absolute;
	top: 14px;
	right: 14px;
	font-weight: bold;
}
.userbar .username {
	color: white;
  	font-size: 20px;
	text-shadow: .5px .5px .5px black;
}
.username {
	cursor: pointer;
}
.userbar button {
	cursor: pointer;
}
.userbar button.icon {
    border-radius: 1px;
	height: 21px;
	width: 23px;
	font-size: 14px;
	text-align: center;
	padding: 0;
}
.userbar button.icon i {
	margin: 0 -5px;
}

.tabbar {
	position: absolute;
	bottom: 1px;
	left: 0;
	right: 0;

	display: block;
	list-style: none;
	margin: 0;
	padding: 0 0 0 0;
	height: 37px;
	text-align: left;

	color: white;
	text-shadow: none;
	font-size: 16px;

	line-height: 100%;
}
.maintabbarbottom {
	content: "";
	display: block;
	position: absolute;
	left: 0;
	right: 0;
	bottom: -6px;
	height: 6px;
	background: white;
	border: 1px solid lightgrey;
	border-left: 0;
  	border-right: 0;
  	border-top: 0;
	margin: -1px 0 0 0;
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none;
}
.tabbar.maintabbar {
	margin-left: 0px;
	margin-right: 165px;
	overflow: hidden;
}
.maintabbar .inner {
	width: 400%;
}
.maintabbar .overflow {
	position: absolute;
	bottom: 0;
	right: 0;
	z-index: 12;
}
.maintabbar .overflow button {
	height: 37px;
	margin: 0;
}

.tabbar li,
.tabbar ul {
	display: block;
	float: left;

	list-style: none;
	margin: 0;
	padding: 0;
}
.tabbar a.button {
	outline: none;
	display: inline-block;
	height: 30px;
	min-width: 150px;
	cursor: pointer;
	text-align: center;
	text-shadow: none;
	border-radius: 5px;

	white-space: nowrap;

	/* default colors */
	color: white;
	text-shadow: none;
	border: solid 2px white !important;
	background: rgba(0, 0, 0, 0);

	position: relative;
	padding: 3px 2px 3px 0px;
	margin: 0 -2px 0 0;
	top: 2px;
	border-radius: 0;
	box-shadow: none;
	font-size: 11px;
}
.tabbar a.button i {
	display: block;
	text-align: center;
	margin: 1px auto 0 auto;
	font-style: normal;
	font-size: 14px;
	height: 14px;
}
.tabbar a.button span {
	display: block;
	overflow: hidden;
}
.tabbar a.button i.text {
	font-size: 11px;
	margin-right: 8px;
	padding-left: 0;
	overflow: hidden;
	font-weight: bold;
}
.tabbar a.button i.text + span {
	margin-right: -6px;
}
.tabbar a.button:hover {
	background: rgba(0, 0, 0, 0);
	border-color: white;

	z-index: 10;
}
.tabbar a.button:active {
	background: rgba(0, 0, 0, 0);
	border-color: white;
  
}
.tabbar a.button.notifying {
	background: rgba(0, 0, 0, 0);
	border-color: white;
}
.tabbar a.button.notifying:hover {
	background: rgba(0, 0, 0, 0);
	border-color: white;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
	color: white;
	background: rgba(0, 0, 0, 0);
	box-shadow: none;
	border-color: white;
	border-bottom: 0;
	cursor: default;
	padding-top: 3px;
	padding-bottom: 5px;
	top: 0px;
}

.tabbar li:first-child a.button {
	border-top-left-radius: 5px;
	margin-left: 6px;
}
.tabbar li:last-child a.button {
	border-top-right-radius: 5px;
}
.tabbar a.closable {
	padding-right: 8px;
}
.tabbar .closebutton {
	display: inline-block;
	margin: 0 0 0 -19px;
	width: 18px;
	position: relative;
	z-index: 11;
	top: 5px;
	vertical-align: top;
}
.tabbar a.cur + .closebutton {
	top: 4px;
}
.closebutton,
.minimizebutton {
	text-decoration: none;
	font-size: 14px;
	color: white;
	text-shadow: none;
	border: 0;
	padding: 0;
	margin: 0;
	background: transparent;
	cursor: pointer;
}
.closebutton:hover,
.minimizebutton:hover {
	color: #C12942;
}
.closebutton:active,
.minimizebutton:active {
	color: #C12942;
}

/*********************************************************
 * Rooms
 *********************************************************/

.ps-room {
	position: absolute;
	display: block;
	top: 56px;
	left: 0;
	right: 0;
	bottom: 0;

	border-left: 1px solid lightgrey;
	margin-left: 0px;
}
.scrollable {
	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.ps-room.ps-room-light {
	background: url(http://wallfoy.com/wp-content/uploads/2014/03/Grey-Background-39.jpg);
	color: black;
}
.ps-room.ps-room-opaque {
	background: rgba(0, 0, 0, 0);
	color: black;
}

.ps-overlay {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: rgba(0, 0, 0, 0);
	background: rgba(0, 0, 0, 0.5);

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
	z-index: 20;
}
.ps-popup {
	position: absolute;
	top: auto; right: auto; left: auto; bottom: auto;
	text-align: left;
	background: url(http://i.imgur.com/L2UjnkI.png);
	color: black;
	border: 1px solid lightgrey;
	padding: 2px 10px;

	border-radius: 5px;
	box-shadow: none;
	z-index: 20;
}
.ps-overlay .ps-popup {
	position: static;
	margin: 80px auto 20px auto;
	max-width: 320px;
}
    
.ps-popup p,
.ps-popup h3 {
	margin: 7px 0;
}
.ps-popup h3 {
	font-size: 12px;
}
p.or {
	font-size: 12pt;
	font-style: italic;
	font-weight: bold;
	color: grey;
	margin: 20px 0;
	text-align: center;
}
p.or:before,
p.or:after {
	content: "";
	font-size: 10px;
	padding: 0 0 0 85px;
	margin: 0 9px 0 0;
	border-bottom: 1px solid lightgrey;
	position: relative;
	top: -7px;
}
p.or:after {
	margin: 0 0 0 9px;
}
.ps-popup p.error {
	font-weight: bold;
	color: #C12942;
}

.popupmenu, popupmenu li {
	margin: -2px 0 -1px 0;
	padding: 0;
	list-style: none;
	font-size: 10px;
}
.popupmenu h3 {
	margin: 5px 0 0 -2px;
	padding: 1px 0;
	font-size: 10px;
	color: #552439;
}
.popupmenu li:first-child h3 {
	margin-top: 0;
}
.popupmenu button {
	display: block;
	cursor: pointer;
	font-size: 8pt;
	font-family: Helvetica, Arial, sans-serif;
	margin: 0 0 0 6px;
	padding: 0 4px;
	border: 1px;
	border-radius: 2px;
	background: transparent;
	color: black;
	width: 204px !important;
	height: 19px;
	text-align: left;

	box-sizing: border-box;
}
@media (max-height:590px) {
	.popupmenu button {
		height: 17px;
	}
	.popupmenu h3 {
		margin-top: 2px;
	}
	.popupmenu, popupmenu li {
		margin: -3px 0 -2px 0;
	}
}
@media (max-height:518px) {
	.popupmenu button {
		height: 16px;
	}
	.popupmenu h3 {
		margin-top: 1px;
	}
}

.popupmenu button.sel {
	padding: 0 3px;
	border: 1px solid lightgrey;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
	padding: 0 3px;
	border: 1px solid lightgrey;
	background: url(http://i.imgur.com/L2UjnkI.png);
}
.select {
	cursor: pointer;
	box-sizing: border-box;
	width: 230px;
	height: 32px;
	padding: 0 5px;
	font-size: 9pt;
	text-align: left;
	font-family: Helvetica, Arial, sans-serif;

	border-radius: 4px;
	border: 1px solid lightgrey;
	background: url(http://i.imgur.com/L2UjnkI.png);
	box-shadow: none;
	color: black;
}

.select.teamselect {
	height: 42px;
  	border-color: lightgrey !important;
  	background: url(http://i.imgur.com/L2UjnkI.png) !important;
  
}
.select:hover {
	border-color: lightgrey;
	background: url(http://i.imgur.com/L2UjnkI.png);
}
.select:active {
	border-color: lightgrey;
	background: url(http://i.imgur.com/L2UjnkI.png);
	box-shadow: none;
}
.select.formatselect:disabled {
	border-color: lightgrey;
	background: url(http://i.imgur.com/L2UjnkI.png);
	box-shadow: none;
}
.select:after {
	float: right;
	margin-top: 1px;
	font-size: 14px;
	content: "\f0d7";
  font-family: FontAwesome;
  -webkit-font-smoothing: antialiased;
  color: grey;
}

.select:disabled {
	border-color: lightgrey;
	background: rgba(0, 0, 0, 0);
	color: lightgrey;
	box-shadow: none;
}
.select:disabled:after {
	content: "";
}
.select.preselected,
.select:disabled.preselected {
	color: black;
}

/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {
	padding: 40px 0 0 0;
	width: 324px;
}
.rightmenu {
	position: absolute;
	top: 40px;
	right: 25px;
}
.activitymenu {
	position: absolute;
	left: 325px;
	top: 40px;
}
.mainmenu {
	padding: 0 0 30px 0;
}

.battleform {
	width: 230px;
	text-align: left;
	margin: 0 auto;
}
.battleform .buttonbar {
	text-align: center;
}

.mainmenuwrapper {
	position: relative;
	min-height: 100%;
}
.mainmenufooter {
  opacity: 0.0;
  }
.mainmenufooter blockquote {
	margin: 0 0 1em 0;
	padding: 0;
}
.mainmenufooter small {
	font-size: 9pt;
}
.mainmenufooter a {
	text-decoration: underline;
	color: #C12942;
}
.mainmenufooter a:hover {
	text-decoration: underline;
	color: #C12942;
}

.pmbox {
	width: 270px;
	margin: 0 auto;
	min-height: 2px;
}
.pm-window {
	margin-bottom: 12px;
}
.pm-window h3 {
	background: rgba(0, 0, 0, 0.5);
	color: white;
	margin: 0;
	padding: 2px 5px;
	font-size: 11pt;
	border: 2px solid white;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	cursor: pointer;
}
.pm-window h3:hover {
	color: white;
}
.pm-window h3.pm-minimized {
	border-radius: 5px;
}
.pm-window h3.pm-notifying {
	border-color: lightgrey;
	background: rgba(0, 0, 0, 0.5);
}
.pm-window h3.pm-notifying:hover {
	border-color: lightgrey;
	background: rgba(0, 0, 0, 0.5);
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 1px;
}
.pm-window h3 small {
	color: white;
	font-weight: normal;
	vertical-align: 1px;
}
.pm-log {
	min-height: 100px;
	max-height: 300px;
	background: url(http://i.imgur.com/L2UjnkI.png);
	color: black;
	word-wrap: break-word;
	border: 1px solid lightgrey;
	border-bottom: 0;

	overflow: auto;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.pm-log-add {
	border: 1px solid lightgrey;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
	background: url(http://i.imgur.com/L2UjnkI.png);
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {
	display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {
	border-color: lightgrey;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
	background: rgba(0, 0, 0, 0.5);
	color: white;
  	border-color: white;
	cursor: default;
}

.challenge {
	margin-top: 0px;
	background: #C12942;
	border: 2px solid white;
  	border-top: 0;
	color: white;
	padding: 1px 5px;
}
.challenge p {
	margin: 4px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {
	position: static;
	top: 0;
	right: 0;
	left: 0;
	width: auto;
	margin: 0;
	padding: 0;
}
.tiny-layout .leftmenu {
	padding-top: 30px;
}
.tiny-layout .activitymenu {
	padding-bottom: 8px;
}

.mainmessage {
	display: block;
	padding: 10px 15px;
	margin: 30px 40px;
	max-width: 400px;
	border-radius: 20px;

	background: rgba(0, 0, 0, 0);
	color: black;
}

.menugroup {
	display: block;
	padding: 10px 0;
	margin: 0 27px 20px 27px;
	max-width: 400px !important;
	border-radius: 10px;
  
	background: rgba(0, 0, 0, .5);
	color: white;
	text-shadow:none;
}
.menugroup {
	background: rgba(0, 0, 0, .5);
	text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
.menugroup .button {
	box-sizing: border-box;
	font-size: 14px;
	font-family: Helvetica, Arial, sans-serif;

	display: inline-block;
	padding: 5px 7px 3px 7px;

	width: 230px !important;
  	height: 32px !important;

  	font-size: 18px;
  
	outline: none;
	cursor: pointer;
	text-align: center;
	text-decoration: none;
	text-shadow: none;
	border-radius: 5px;

	/* default colors */
	color: white;
	border: solid 2px;
	background: #C12942;
	border-color: white;
  	box-shadow: none;
}
.menugroup button.big {
	width: 230px;
  	height: 32px;
	padding: 6px 7px 6px 7px;
	font-size: 14pt;
  
}
.menugroup .button:hover {
	color: white;
	border: solid 2px;
	background: url(http://i.imgur.com/MhIr3Z9.png);
  	background-size: 100%;
	border-color: black;
  	box-shadow: none;
}
.menugroup .button:active {
	color: white;
	border: solid 2px;
	background: url(http://i.imgur.com/MhIr3Z9.png);
  	background-size: 100%;
	border-color: black;
  	box-shadow: none;
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {
	cursor: default;
	color: white;
	border: solid 2px;
	background: url(http://i.imgur.com/MhIr3Z9.png);
  background-size: 100%;
	border-color: black;
  	box-shadow: none;
}

.rightmenu {
    width: 330px !important;
  }

@media (max-width:895px) {
	.mainmenu {
		padding-bottom: 1px;
	}
	.rightmenu {
		position: static;
		top: 0;
		right: 0;
		padding-bottom: 30px;
	}
	.rightmenu {
		width: 324px;
	}
	.rightmenu .menugroup .button {
		width: 200px;
	}
}

/*********************************************************
 * Ladder
 *********************************************************/

.ladder table, .ladder td, .ladder th {
	border-collapse: collapse;
	border: 1px solid lightgrey;
}
.ladder td, .ladder th {
	padding: 3px 5px;
}
.ladder th {
	text-align: left;
	font-size: 9pt;
	background: rgba(0, 0, 0, 0);
}
.ladder span {
	color: lightgrey;
}

.roomlist {
	text-align: center;
}
.roomlist a.ilink {
	display: block;
	margin: 2px 7px 4px 7px;
	padding: 1px 4px 2px 4px;
	border: solid white 2px;
	background: #C12942;
  	height: 30px;
	border-radius: 5px;
	text-decoration: none;
	color: white;
	text-shadow: none;
	box-shadow: none;
	cursor: pointer;
	font-size: 10pt;

	overflow: hidden;
	white-space: nowrap;
}
.roomlist a.ilink small {
	font-size: 12px;
}
.roomlist a.ilink:hover {
	border: solid white 2px;
	background: rgba(0,0,0,0.5);
	color: white;
	text-decoration: none;
}

.ps-popup.credits {
	max-width: 380px;
	margin-top: 20px;
	padding: 5px 60px;
}
.ps-popup.credits strong{
  color: black;
}

    .ps-popup.credits a.subtle{
  color: black;
}
.credits h2 {
	font-size: 11pt;
	font-style: italic;
	margin-bottom: 0;
	color: black;
}
.credits ul {
	padding-left: 1.5em;
	margin-top: 0;
	margin-bottom: 0;
	color: black;
}
.credits ul li p {
	color: black;
}
.credits li small {
	font-size: 10pt;
	display: block;
	color: black;
}

/*********************************************************
 * Chat
 *********************************************************/

.chat-log {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 40px;
	font-size: 10pt;
	background: rgba(0,0,0,0);
	color: black;
	word-wrap: break-word;

	overflow: auto;
	overflow-x: hidden;
	-webkit-overflow-scrolling: touch;
	overflow-scrolling: touch;
}
.chat-log-add {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	min-height: 36px;
	padding: 3px 4px 0 4px;
	border-top: 1px solid lightgrey;
}
.chat-log .notice,
.chat-log .chat,
.pm-log .chat {
	padding: 4px 0 2px 0;
	font-size: 10pt;
}
.chat em {
	padding: 0 4px 0 3px;
	font-style: normal;
}
.chat small {
	font-weight: normal;
	font-size: 8pt;
	color: lightgrey;
}
.chat.timer {
	color: #C12942;
}
.debug {
	display: none;
}
.highlighted {
	background-color: #EBD077;
}
.message-pm {
	color: lightgrey;
}

.message-pm .pmnote {
	cursor: pointer;
}
.chat-log .inner,
.pm-log .inner {
	padding: 4px 8px 6px 8px;
}
a.ilink {
	text-decoration: none;
	color: black;
}
a.ilink:hover {
	text-decoration: underline;
	color: black;
}

.chatbox {
	margin: 0 10px 0 80px;
	padding: 5px 0 0 0;
}
.chatbox.nolabel {
	margin: 0 14px 0 6px;
	padding: 5px 0 5px 0;
}
.chatbox label {
	float: left;
	margin-left: -78px;
	padding-top: 2px;
	width: 73px;
	text-align: right;
	overflow: hidden;
	white-space: pre;

	font-weight: bold;
	font-size: 8pt;
}
.chatbox textarea {
	display: block;
	height: 1.1em;
	width: 100%;
	resize: none;
	font-size: 9pt;
	font-family: Helvetica, Arial, sans-serif;
}

/* Tournaments */

.tournaments-info > ul {
	list-style: none;
	margin: 0;
	padding: 0;
}

.tournament-wrapper {
	display: none;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	height: 2em;
	line-height: 2em;
	text-align: center;
	border-bottom: 1px lightgrey solid;
}
.tournament-wrapper.active {
	display: block;
}
.tournament-wrapper.active + .chat-log {
	top: 2.4em;
}

.tournament-title {
	font-weight: bold;
	cursor: pointer;
}

.tournament-status, .tournament-toggle {
	position: absolute;
	top: 0;
	padding: 0 0.5em;
	font-weight: normal;
	font-size: 80%;
