css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("funnyjunk.com") {

#columnLeftSeparator > div.inner:nth-child(1) {
display: none !important;
}

h1, h2, .contentTitle, .title {
font-family: sans-serif !important;
}

* {
border-radius: 0px !important;
}

/*
Hide channels bar
*/
#channels_bar {
display: none !important;
}

div.aCenter > span {
display: none !important;
}

.userTagsList {
float: right !important;
list-style-type: none;
clear: right;
margin: 5px 10px 0px 0px !important;
width: 80%;
text-align: right !important;
padding: 0px;
z-index: 0 !important;
}


.uploadBttn {
background-image: none !important;
background-color: #445 !important;
border: 0px;
margin-top: 8px;
}
.uploadBttn:hover {
background-color: #556 !important;
border: 0px;
}







#logoFJ a {
    width: 30px !important;
    height: 30px !important;
    background: url('http://i.imgur.com/8PgI2uc.png') no-repeat !important;
}
#logoFJLogged a {
    width: 30px !important;
    height: 30px !important;
    background: url('http://i.imgur.com/8PgI2uc.png') no-repeat !important;
}

#logoFJ, #logoFJLogged {
    margin-right: 10px !important;
    margin-left: 5px !important;
}


#topUserbar {
background-image: none !important;
background-color: #272727 !important;
padding-bottom: 3px !important;
}


.thumbnailThumbPanel {
border: 1px solid black !important;
}
.thumbnailThumbPanel .thUp {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -223px -440px transparent;
}
.thumbnailThumbPanel .thUp_i {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -223px -539px transparent;
}
.thumbnailThumbPanel .thDn {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -293px -440px transparent;
}
.thumbnailThumbPanel .thDn_i {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -293px -539px transparent;
}
.thumbnailThumbPanel .thDn:hover, .thumbnailThumbPanel .thUp:hover, .thumbnailThumbPanel .thDn_i:hover, .thumbnailThumbPanel .thUp_i:hover {
border: 1px solid black !important;
}



.tUpBig {
    background: url('http://i.imgur.com/WQ2GygV.png') -210px -442px no-repeat !important;
}
.tDnBig {
    background: url('http://i.imgur.com/WQ2GygV.png') -280px -442px no-repeat !important;
}

.tDnBig_i {
    background: url('http://i.imgur.com/WQ2GygV.png') -280px -541px no-repeat !important;
}
.tUpBig_i {
    background: url('http://i.imgur.com/WQ2GygV.png') -210px -541px no-repeat !important;
}

#commentsList .r .thUp, .added-user-tags .thUp {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -210px -640px transparent;
}
#commentsList .r .thDn, .added-user-tags .thDn {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -225px -640px transparent;
}
#commentsList .r .thUp_i, .added-user-tags .thUp_i {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -210px -708px transparent !important;
}
#commentsList .r .thDn_i, .added-user-tags .thDn_i {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -225px -708px transparent !important;
}


#cNext_floating_js, #cNext_floating {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll 6px -1640px transparent !important;
}
#cPrev_floating, #cPrev_floating_js {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll 6px -1731px transparent !important;
}
#cPrev_floating_js {
z-index: 200 !important;
}
#columnLeftSeparator {
    z-index: 2 !important;
}

#cRandom_floating {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll 6px -1822px transparent !important;
}
.tUpBig > i, .tDnBig > i, .tUpBig_i > i, .tDnBig_i > i {
    background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -8px -1636px transparent !important;
}








#topSearchBttn {
background: url('http://i.imgur.com/WQ2GygV.png') no-repeat scroll -210px -73px transparent !important;
}
#topSearchBttn:hover {
background-color: #333 !important;
}


li.forLogged {
line-height: 20px !important;
}


#addCommentTopLink, .yellowButton, .greenButton, .addCntToFav {
background-color: #82e62a !important;
}
.thGreen {
color: #82e62a !important;
}

.pinkButton, .skyblueButton {
margin-top: 0px !important;
}

.ui-widget-overlay, .ui-widget-overlay2 {
background-color: rgba(0,0,0,1) !important;
}











}
