css:
@-moz-document url-prefix("http://www.frequence-news.net/") {
/*
 * Global site style reset
 */

div#contenu0,
div#centre,
div#centre1,
div#centre2 {
    width: 1200px !important;
    border: 0;
    background: none;
}
h1 {
    margin-top: 20px;
    text-transform: uppercase;
}
div#contenu0 {
    background: white;
    box-shadow: 0 0 5px #aaa;
    margin-bottom: 15px;
}
body,
html {
    background: #eee;
}
div#bas,
div#bas0,
div#bas1,
div#bas2,
div#bas3,
div#bas4,
div#bas5 {
    border: 0;
    box-shadow: 0;
    background: none;
}
/* Hide obsolete div*/

div#menud,
div.feed-continu-entete-fond,
div.feed-mini-entete-fond {
    display: none;
}
/*
 * Header
 */

div#haut,
div#haut2,
ul#menubar a,
ul#menubar a:hover,
ul#menubar li {
    background: none;
}
ul#menubar {
    background: #5389AA;
    padding: 5px 10px;
    font-size: 14px;
    font-weight: bold;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    box-shadow: 0 0 2px #aaa;
}
ul#menubar a,
ul#menubar a:hover {
    font-size: 14px !important;
    color: white;
}
ul#menubar a:hover {
    color: #F1C864;
}
ul#menubar li:last-child {
    display: none;
}
div#searchbar {
    top: 20px;
}
div#searchbar input {
    padding: 5px 7px;
    border: 0;
    background: white;
    box-shadow: 0 0 2px #aaa;
}
div#searchbar a {
    display: inline-block;
    position: relative;
    margin-left: -3px;
    padding: 5px 6px;
    border: 0;
    box-shadow: 0 0 2px #aaa;
    background-color: white !important;
    background-image: url('https://cdn3.iconfinder.com/data/icons/token/Token,%20128x128,%20PNG/Search.png');
    background-size: 90% 90%;
    background-position: 1px 1px;
    color: rgba(0, 0, 0, 0) !important;
}
div#searchbar a:hover {
    background-color: #ccc !important;
    color: rgba(0, 0, 0, 0) !important;
}
/* Reset Feed Width */

div[class*="feed-"] {
    width: 100% !important;
    margin: 0;
}
div.feed-mini {
    width: 50% !important;
    margin: 0;
    padding: 0;
}
/* Remove backgrounds */

div[class*="feed-"] div,
ul[class*="feed-"],
div.feed-mini-bas p,
.feed-mini-fond,
.feed-continu-fond,
.barre,
.barre li,
.barre ul {
    background: none !important;
}
.feed-mini-fond {
    border-top: 1px solid #FFA500;
    border-bottom: 1px solid #ccc;
}
div.feed-mini-entete,
div.feed-mini-entete-fond,
div.feed-continu-entete,
div.feed-continu-entete-fond {
    box-sizing: border-box;
    height: auto;
}
/*
 * Feed Style
 */

div.feed-mini-entete h2,
div.feed-continu-entete h2 {
    display: block;
    position: relative;
    top: 0;
    left: 0;
    margin: 10px 15px 0px 15px;
    padding: 5px 10px;
    color: #FFA500;
    text-shadow: none;
    font-size: 18px;
}
div.feed-continu ul,
div.feed-mini ul {
    box-sizing: border-box;
    margin: 0 10px;
    padding: 0;
    border-top: 1px solid #FFA500;
    border-bottom: 1px solid #ccc;
}
div.feed-continu li,
div.feed-mini li {
    font-family: verdana;
    font-size: 12px;
    padding: 3px 10px;
}
div.feed-continu li:nth-child(odd),
div.feed-mini li:nth-child(odd) {
    background: #f7f7f7;
}
div.feed-continu li span.date {
    font-weight: normal;
    padding: 3px 5px;
}
div.feed-mini-bas {
    width: 100% !important;
}
div.feed-continu-bas p,
div.feed-mini-bas p a,
div.feed-mini-bas p {
    color: #999 !important;
}
div#contenu > p {
    padding: 0px 15px;
}
/*
 * Bar Style
 */

.barre {
    margin: 20px;
    padding: 0;
}
.barre ul {
    position: relative;
    background: #eee !important;
    margin: 0;
    padding: 0;
    border-radius: 5px;
    height: 32px;
}
.barre a {
    padding: 2px 15px;
    color: #333;
}
.barre a:hover {
    background: #f7f7f7;
    color: black;
}
}
